INSERT INTO "quran_text" VALUES(3058,26,126,'فَٱتَّقُوا۟ ٱللَّهَ وَأَطِيعُونِ');
INSERT INTO "quran_text" VALUES(3059,26,127,'وَمَآ أَسْـَٔلُكُمْ عَلَيْهِ مِنْ أَجْرٍ ۖ إِنْ أَجْرِىَ إِلَّا عَلَىٰ رَبِّ ٱلْعَـٰلَمِينَ');
INSERT INTO "quran_text" VALUES(3060,26,128,'أَتَبْنُونَ بِكُلِّ رِيعٍ ءَايَةً تَعْبَثُونَ');
INSERT INTO "quran_text" VALUES(3061,26,129,'وَتَتَّخِذُونَ مَصَانِعَ لَعَلَّكُمْ تَخْلُدُونَ');
INSERT INTO "quran_text" VALUES(3062,26,130,'وَإِذَا بَطَشْتُم بَطَشْتُمْ جَبَّارِينَ');
INSERT INTO "quran_text" VALUES(3063,26,131,'فَٱتَّقُوا۟ ٱللَّهَ وَأَطِيعُونِ');
INSERT INTO "quran_text" VALUES(3064,26,132,'وَٱتَّقُوا۟ ٱلَّذِىٓ أَمَدَّكُم بِمَا تَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(3065,26,133,'أَمَدَّكُم بِأَنْعَـٰمٍ وَبَنِينَ');
INSERT INTO "quran_text" VALUES(3066,26,134,'وَجَنَّـٰتٍ وَعُيُونٍ');
INSERT INTO "quran_text" VALUES(3067,26,135,'إِنِّىٓ أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيمٍ');
INSERT INTO "quran_text" VALUES(3068,26,136,'قَالُوا۟ سَوَآءٌ عَلَيْنَآ أَوَعَظْتَ أَمْ لَمْ تَكُن مِّنَ ٱلْوَٰعِظِينَ');
INSERT INTO "quran_text" VALUES(3069,26,137,'إِنْ هَـٰذَآ إِلَّا خُلُقُ ٱلْأَوَّلِينَ');
INSERT INTO "quran_text" VALUES(3070,26,138,'وَمَا نَحْنُ بِمُعَذَّبِينَ');
INSERT INTO "quran_text" VALUES(3071,26,139,'فَكَذَّبُوهُ فَأَهْلَكْنَـٰهُمْ ۗ إِنَّ فِى ذَٰلِكَ لَـَٔايَةً ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ');
INSERT INTO "quran_text" VALUES(3072,26,140,'وَإِنَّ رَبَّكَ لَهُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ');
INSERT INTO "quran_text" VALUES(3073,26,141,'كَذَّبَتْ ثَمُودُ ٱلْمُرْسَلِينَ');
INSERT INTO "quran_text" VALUES(3074,26,142,'إِذْ قَالَ لَهُمْ أَخُوهُمْ صَـٰلِحٌ أَلَا تَتَّقُونَ');
