INSERT INTO "quran_text" VALUES(3078,26,146,'أَتُتْرَكُونَ فِى مَا هَـٰهُنَآ ءَامِنِينَ');
INSERT INTO "quran_text" VALUES(3079,26,147,'فِى جَنَّـٰتٍ وَعُيُونٍ');
INSERT INTO "quran_text" VALUES(3080,26,148,'وَزُرُوعٍ وَنَخْلٍ طَلْعُهَا هَضِيمٌ');
INSERT INTO "quran_text" VALUES(3081,26,149,'وَتَنْحِتُونَ مِنَ ٱلْجِبَالِ بُيُوتًا فَـٰرِهِينَ');
INSERT INTO "quran_text" VALUES(3082,26,150,'فَٱتَّقُوا۟ ٱللَّهَ وَأَطِيعُونِ');
INSERT INTO "quran_text" VALUES(3083,26,151,'وَلَا تُطِيعُوٓا۟ أَمْرَ ٱلْمُسْرِفِينَ');
