INSERT INTO "quran_text" VALUES(3099,26,167,'قَالُوا۟ لَئِن لَّمْ تَنتَهِ يَـٰلُوطُ لَتَكُونَنَّ مِنَ ٱلْمُخْرَجِينَ');
INSERT INTO "quran_text" VALUES(3100,26,168,'قَالَ إِنِّى لِعَمَلِكُم مِّنَ ٱلْقَالِينَ');
INSERT INTO "quran_text" VALUES(3101,26,169,'رَبِّ نَجِّنِى وَأَهْلِى مِمَّا يَعْمَلُونَ');
INSERT INTO "quran_text" VALUES(3102,26,170,'فَنَجَّيْنَـٰهُ وَأَهْلَهُۥٓ أَجْمَعِينَ');
INSERT INTO "quran_text" VALUES(3103,26,171,'إِلَّا عَجُوزًا فِى ٱلْغَـٰبِرِينَ');
INSERT INTO "quran_text" VALUES(3104,26,172,'ثُمَّ دَمَّرْنَا ٱلْـَٔاخَرِينَ');
INSERT INTO "quran_text" VALUES(3105,26,173,'وَأَمْطَرْنَا عَلَيْهِم مَّطَرًا ۖ فَسَآءَ مَطَرُ ٱلْمُنذَرِينَ');
INSERT INTO "quran_text" VALUES(3106,26,174,'إِنَّ فِى ذَٰلِكَ لَـَٔايَةً ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ');
INSERT INTO "quran_text" VALUES(3107,26,175,'وَإِنَّ رَبَّكَ لَهُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ');
INSERT INTO "quran_text" VALUES(3108,26,176,'كَذَّبَ أَصْحَـٰبُ لْـَٔيْكَةِ ٱلْمُرْسَلِينَ');
