INSERT INTO "quran_text" VALUES(3113,26,181,'۞ أَوْفُوا۟ ٱلْكَيْلَ وَلَا تَكُونُوا۟ مِنَ ٱلْمُخْسِرِينَ');
INSERT INTO "quran_text" VALUES(3114,26,182,'وَزِنُوا۟ بِٱلْقِسْطَاسِ ٱلْمُسْتَقِيمِ');
INSERT INTO "quran_text" VALUES(3115,26,183,'وَلَا تَبْخَسُوا۟ ٱلنَّاسَ أَشْيَآءَهُمْ وَلَا تَعْثَوْا۟ فِى ٱلْأَرْضِ مُفْسِدِينَ');
INSERT INTO "quran_text" VALUES(3116,26,184,'وَٱتَّقُوا۟ ٱلَّذِى خَلَقَكُمْ وَٱلْجِبِلَّةَ ٱلْأَوَّلِينَ');
INSERT INTO "quran_text" VALUES(3117,26,185,'قَالُوٓا۟ إِنَّمَآ أَنتَ مِنَ ٱلْمُسَحَّرِينَ');
INSERT INTO "quran_text" VALUES(3118,26,186,'وَمَآ أَنتَ إِلَّا بَشَرٌ مِّثْلُنَا وَإِن نَّظُنُّكَ لَمِنَ ٱلْكَـٰذِبِينَ');
INSERT INTO "quran_text" VALUES(3119,26,187,'فَأَسْقِطْ عَلَيْنَا كِسَفًا مِّنَ ٱلسَّمَآءِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ');
INSERT INTO "quran_text" VALUES(3120,26,188,'قَالَ رَبِّىٓ أَعْلَمُ بِمَا تَعْمَلُونَ');
INSERT INTO "quran_text" VALUES(3121,26,189,'فَكَذَّبُوهُ فَأَخَذَهُمْ عَذَابُ يَوْمِ ٱلظُّلَّةِ ۚ إِنَّهُۥ كَانَ عَذَابَ يَوْمٍ عَظِيمٍ');
INSERT INTO "quran_text" VALUES(3122,26,190,'إِنَّ فِى ذَٰلِكَ لَـَٔايَةً ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ');
INSERT INTO "quran_text" VALUES(3123,26,191,'وَإِنَّ رَبَّكَ لَهُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ');
INSERT INTO "quran_text" VALUES(3124,26,192,'وَإِنَّهُۥ لَتَنزِيلُ رَبِّ ٱلْعَـٰلَمِينَ');
