INSERT INTO "quran_text" VALUES(5037,56,58,'أَفَرَءَيْتُم مَّا تُمْنُونَ');
INSERT INTO "quran_text" VALUES(5038,56,59,'ءَأَنتُمْ تَخْلُقُونَهُۥٓ أَمْ نَحْنُ ٱلْخَـٰلِقُونَ');
INSERT INTO "quran_text" VALUES(5039,56,60,'نَحْنُ قَدَّرْنَا بَيْنَكُمُ ٱلْمَوْتَ وَمَا نَحْنُ بِمَسْبُوقِينَ');
INSERT INTO "quran_text" VALUES(5040,56,61,'عَلَىٰٓ أَن نُّبَدِّلَ أَمْثَـٰلَكُمْ وَنُنشِئَكُمْ فِى مَا لَا تَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(5041,56,62,'وَلَقَدْ عَلِمْتُمُ ٱلنَّشْأَةَ ٱلْأُولَىٰ فَلَوْلَا تَذَكَّرُونَ');
INSERT INTO "quran_text" VALUES(5042,56,63,'أَفَرَءَيْتُم مَّا تَحْرُثُونَ');
INSERT INTO "quran_text" VALUES(5043,56,64,'ءَأَنتُمْ تَزْرَعُونَهُۥٓ أَمْ نَحْنُ ٱلزَّٰرِعُونَ');
INSERT INTO "quran_text" VALUES(5044,56,65,'لَوْ نَشَآءُ لَجَعَلْنَـٰهُ حُطَـٰمًا فَظَلْتُمْ تَفَكَّهُونَ');
INSERT INTO "quran_text" VALUES(5045,56,66,'إِنَّا لَمُغْرَمُونَ');
INSERT INTO "quran_text" VALUES(5046,56,67,'بَلْ نَحْنُ مَحْرُومُونَ');
INSERT INTO "quran_text" VALUES(5047,56,68,'أَفَرَءَيْتُمُ ٱلْمَآءَ ٱلَّذِى تَشْرَبُونَ');
INSERT INTO "quran_text" VALUES(5048,56,69,'ءَأَنتُمْ أَنزَلْتُمُوهُ مِنَ ٱلْمُزْنِ أَمْ نَحْنُ ٱلْمُنزِلُونَ');
INSERT INTO "quran_text" VALUES(5049,56,70,'لَوْ نَشَآءُ جَعَلْنَـٰهُ أُجَاجًا فَلَوْلَا تَشْكُرُونَ');
INSERT INTO "quran_text" VALUES(5050,56,71,'أَفَرَءَيْتُمُ ٱلنَّارَ ٱلَّتِى تُورُونَ');
INSERT INTO "quran_text" VALUES(5051,56,72,'ءَأَنتُمْ أَنشَأْتُمْ شَجَرَتَهَآ أَمْ نَحْنُ ٱلْمُنشِـُٔونَ');
INSERT INTO "quran_text" VALUES(5052,56,73,'نَحْنُ جَعَلْنَـٰهَا تَذْكِرَةً وَمَتَـٰعًا لِّلْمُقْوِينَ');
INSERT INTO "quran_text" VALUES(5053,56,74,'فَسَبِّحْ بِٱسْمِ رَبِّكَ ٱلْعَظِيمِ');
INSERT INTO "quran_text" VALUES(5054,56,75,'۞ فَلَآ أُقْسِمُ بِمَوَٰقِعِ ٱلنُّجُومِ');
INSERT INTO "quran_text" VALUES(5055,56,76,'وَإِنَّهُۥ لَقَسَمٌ لَّوْ تَعْلَمُونَ عَظِيمٌ');
INSERT INTO "quran_text" VALUES(5056,56,77,'إِنَّهُۥ لَقُرْءَانٌ كَرِيمٌ');
INSERT INTO "quran_text" VALUES(5057,56,78,'فِى كِتَـٰبٍ مَّكْنُونٍ');
INSERT INTO "quran_text" VALUES(5058,56,79,'لَّا يَمَسُّهُۥٓ إِلَّا ٱلْمُطَهَّرُونَ');
INSERT INTO "quran_text" VALUES(5059,56,80,'تَنزِيلٌ مِّن رَّبِّ ٱلْعَـٰلَمِينَ');
INSERT INTO "quran_text" VALUES(5060,56,81,'أَفَبِهَـٰذَا ٱلْحَدِيثِ أَنتُم مُّدْهِنُونَ');
INSERT INTO "quran_text" VALUES(5061,56,82,'وَتَجْعَلُونَ رِزْقَكُمْ أَنَّكُمْ تُكَذِّبُونَ');
INSERT INTO "quran_text" VALUES(5062,56,83,'فَلَوْلَآ إِذَا بَلَغَتِ ٱلْحُلْقُومَ');
INSERT INTO "quran_text" VALUES(5063,56,84,'وَأَنتُمْ حِينَئِذٍ تَنظُرُونَ');
INSERT INTO "quran_text" VALUES(5064,56,85,'وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنكُمْ وَلَـٰكِن لَّا تُبْصِرُونَ');
INSERT INTO "quran_text" VALUES(5065,56,86,'فَلَوْلَآ إِن كُنتُمْ غَيْرَ مَدِينِينَ');
INSERT INTO "quran_text" VALUES(5066,56,87,'تَرْجِعُونَهَآ إِن كُنتُمْ صَـٰدِقِينَ');
INSERT INTO "quran_text" VALUES(5067,56,88,'فَأَمَّآ إِن كَانَ مِنَ ٱلْمُقَرَّبِينَ');
INSERT INTO "quran_text" VALUES(5068,56,89,'فَرَوْحٌ وَرَيْحَانٌ وَجَنَّتُ نَعِيمٍ');
INSERT INTO "quran_text" VALUES(5069,56,90,'وَأَمَّآ إِن كَانَ مِنْ أَصْحَـٰبِ ٱلْيَمِينِ');
INSERT INTO "quran_text" VALUES(5070,56,91,'فَسَلَـٰمٌ لَّكَ مِنْ أَصْحَـٰبِ ٱلْيَمِينِ');
INSERT INTO "quran_text" VALUES(5071,56,92,'وَأَمَّآ إِن كَانَ مِنَ ٱلْمُكَذِّبِينَ ٱلضَّآلِّينَ');
INSERT INTO "quran_text" VALUES(5072,56,93,'فَنُزُلٌ مِّنْ حَمِيمٍ');
INSERT INTO "quran_text" VALUES(5073,56,94,'وَتَصْلِيَةُ جَحِيمٍ');
INSERT INTO "quran_text" VALUES(5074,56,95,'إِنَّ هَـٰذَا لَهُوَ حَقُّ ٱلْيَقِينِ');
INSERT INTO "quran_text" VALUES(5075,56,96,'فَسَبِّحْ بِٱسْمِ رَبِّكَ ٱلْعَظِيمِ');
INSERT INTO "quran_text" VALUES(5076,57,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ سَبَّحَ لِلَّهِ مَا فِى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۖ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ');
INSERT INTO "quran_text" VALUES(5077,57,2,'لَهُۥ مُلْكُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۖ يُحْىِۦ وَيُمِيتُ ۖ وَهُوَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ');
INSERT INTO "quran_text" VALUES(5078,57,3,'هُوَ ٱلْأَوَّلُ وَٱلْـَٔاخِرُ وَٱلظَّـٰهِرُ وَٱلْبَاطِنُ ۖ وَهُوَ بِكُلِّ شَىْءٍ عَلِيمٌ');
INSERT INTO "quran_text" VALUES(5079,57,4,'هُوَ ٱلَّذِى خَلَقَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ فِى سِتَّةِ أَيَّامٍ ثُمَّ ٱسْتَوَىٰ عَلَى ٱلْعَرْشِ ۚ يَعْلَمُ مَا يَلِجُ فِى ٱلْأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنزِلُ مِنَ ٱلسَّمَآءِ وَمَا يَعْرُجُ فِيهَا ۖ وَهُوَ مَعَكُمْ أَيْنَ مَا كُنتُمْ ۚ وَٱللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ');
INSERT INTO "quran_text" VALUES(5080,57,5,'لَّهُۥ مُلْكُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ وَإِلَى ٱللَّهِ تُرْجَعُ ٱلْأُمُورُ');
INSERT INTO "quran_text" VALUES(5081,57,6,'يُولِجُ ٱلَّيْلَ فِى ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِى ٱلَّيْلِ ۚ وَهُوَ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ');
INSERT INTO "quran_text" VALUES(5082,57,7,'ءَامِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَأَنفِقُوا۟ مِمَّا جَعَلَكُم مُّسْتَخْلَفِينَ فِيهِ ۖ فَٱلَّذِينَ ءَامَنُوا۟ مِنكُمْ وَأَنفَقُوا۟ لَهُمْ أَجْرٌ كَبِيرٌ');
INSERT INTO "quran_text" VALUES(5083,57,8,'وَمَا لَكُمْ لَا تُؤْمِنُونَ بِٱللَّهِ ۙ وَٱلرَّسُولُ يَدْعُوكُمْ لِتُؤْمِنُوا۟ بِرَبِّكُمْ وَقَدْ أَخَذَ مِيثَـٰقَكُمْ إِن كُنتُم مُّؤْمِنِينَ');
INSERT INTO "quran_text" VALUES(5084,57,9,'هُوَ ٱلَّذِى يُنَزِّلُ عَلَىٰ عَبْدِهِۦٓ ءَايَـٰتٍۭ بَيِّنَـٰتٍ لِّيُخْرِجَكُم مِّنَ ٱلظُّلُمَـٰتِ إِلَى ٱلنُّورِ ۚ وَإِنَّ ٱللَّهَ بِكُمْ لَرَءُوفٌ رَّحِيمٌ');
INSERT INTO "quran_text" VALUES(5085,57,10,'وَمَا لَكُمْ أَلَّا تُنفِقُوا۟ فِى سَبِيلِ ٱللَّهِ وَلِلَّهِ مِيرَٰثُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ لَا يَسْتَوِى مِنكُم مَّنْ أَنفَقَ مِن قَبْلِ ٱلْفَتْحِ وَقَـٰتَلَ ۚ أُو۟لَـٰٓئِكَ أَعْظَمُ دَرَجَةً مِّنَ ٱلَّذِينَ أَنفَقُوا۟ مِنۢ بَعْدُ وَقَـٰتَلُوا۟ ۚ وَكُلًّا وَعَدَ ٱللَّهُ ٱلْحُسْنَىٰ ۚ وَٱللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ');
INSERT INTO "quran_text" VALUES(5086,57,11,'مَّن ذَا ٱلَّذِى يُقْرِضُ ٱللَّهَ قَرْضًا حَسَنًا فَيُضَـٰعِفَهُۥ لَهُۥ وَلَهُۥٓ أَجْرٌ كَرِيمٌ');
INSERT INTO "quran_text" VALUES(5087,57,12,'يَوْمَ تَرَى ٱلْمُؤْمِنِينَ وَٱلْمُؤْمِنَـٰتِ يَسْعَىٰ نُورُهُم بَيْنَ أَيْدِيهِمْ وَبِأَيْمَـٰنِهِم بُشْرَىٰكُمُ ٱلْيَوْمَ جَنَّـٰتٌ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ خَـٰلِدِينَ فِيهَا ۚ ذَٰلِكَ هُوَ ٱلْفَوْزُ ٱلْعَظِيمُ');
INSERT INTO "quran_text" VALUES(5088,57,13,'يَوْمَ يَقُولُ ٱلْمُنَـٰفِقُونَ وَٱلْمُنَـٰفِقَـٰتُ لِلَّذِينَ ءَامَنُوا۟ ٱنظُرُونَا نَقْتَبِسْ مِن نُّورِكُمْ قِيلَ ٱرْجِعُوا۟ وَرَآءَكُمْ فَٱلْتَمِسُوا۟ نُورًا فَضُرِبَ بَيْنَهُم بِسُورٍ لَّهُۥ بَابٌۢ بَاطِنُهُۥ فِيهِ ٱلرَّحْمَةُ وَظَـٰهِرُهُۥ مِن قِبَلِهِ ٱلْعَذَابُ');
INSERT INTO "quran_text" VALUES(5089,57,14,'يُنَادُونَهُمْ أَلَمْ نَكُن مَّعَكُمْ ۖ قَالُوا۟ بَلَىٰ وَلَـٰكِنَّكُمْ فَتَنتُمْ أَنفُسَكُمْ وَتَرَبَّصْتُمْ وَٱرْتَبْتُمْ وَغَرَّتْكُمُ ٱلْأَمَانِىُّ حَتَّىٰ جَآءَ أَمْرُ ٱللَّهِ وَغَرَّكُم بِٱللَّهِ ٱلْغَرُورُ');
INSERT INTO "quran_text" VALUES(5090,57,15,'فَٱلْيَوْمَ لَا يُؤْخَذُ مِنكُمْ فِدْيَةٌ وَلَا مِنَ ٱلَّذِينَ كَفَرُوا۟ ۚ مَأْوَىٰكُمُ ٱلنَّارُ ۖ هِىَ مَوْلَىٰكُمْ ۖ وَبِئْسَ ٱلْمَصِيرُ');
INSERT INTO "quran_text" VALUES(5091,57,16,'۞ أَلَمْ يَأْنِ لِلَّذِينَ ءَامَنُوٓا۟ أَن تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ ٱللَّهِ وَمَا نَزَلَ مِنَ ٱلْحَقِّ وَلَا يَكُونُوا۟ كَٱلَّذِينَ أُوتُوا۟ ٱلْكِتَـٰبَ مِن قَبْلُ فَطَالَ عَلَيْهِمُ ٱلْأَمَدُ فَقَسَتْ قُلُوبُهُمْ ۖ وَكَثِيرٌ مِّنْهُمْ فَـٰسِقُونَ');
INSERT INTO "quran_text" VALUES(5092,57,17,'ٱعْلَمُوٓا۟ أَنَّ ٱللَّهَ يُحْىِ ٱلْأَرْضَ بَعْدَ مَوْتِهَا ۚ قَدْ بَيَّنَّا لَكُمُ ٱلْـَٔايَـٰتِ لَعَلَّكُمْ تَعْقِلُونَ');
INSERT INTO "quran_text" VALUES(5093,57,18,'إِنَّ ٱلْمُصَّدِّقِينَ وَٱلْمُصَّدِّقَـٰتِ وَأَقْرَضُوا۟ ٱللَّهَ قَرْضًا حَسَنًا يُضَـٰعَفُ لَهُمْ وَلَهُمْ أَجْرٌ كَرِيمٌ');
INSERT INTO "quran_text" VALUES(5094,57,19,'وَٱلَّذِينَ ءَامَنُوا۟ بِٱللَّهِ وَرُسُلِهِۦٓ أُو۟لَـٰٓئِكَ هُمُ ٱلصِّدِّيقُونَ ۖ وَٱلشُّهَدَآءُ عِندَ رَبِّهِمْ لَهُمْ أَجْرُهُمْ وَنُورُهُمْ ۖ وَٱلَّذِينَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔايَـٰتِنَآ أُو۟لَـٰٓئِكَ أَصْحَـٰبُ ٱلْجَحِيمِ');
INSERT INTO "quran_text" VALUES(5095,57,20,'ٱعْلَمُوٓا۟ أَنَّمَا ٱلْحَيَوٰةُ ٱلدُّنْيَا لَعِبٌ وَلَهْوٌ وَزِينَةٌ وَتَفَاخُرٌۢ بَيْنَكُمْ وَتَكَاثُرٌ فِى ٱلْأَمْوَٰلِ وَٱلْأَوْلَـٰدِ ۖ كَمَثَلِ غَيْثٍ أَعْجَبَ ٱلْكُفَّارَ نَبَاتُهُۥ ثُمَّ يَهِيجُ فَتَرَىٰهُ مُصْفَرًّا ثُمَّ يَكُونُ حُطَـٰمًا ۖ وَفِى ٱلْـَٔاخِرَةِ عَذَابٌ شَدِيدٌ وَمَغْفِرَةٌ مِّنَ ٱللَّهِ وَرِضْوَٰنٌ ۚ وَمَا ٱلْحَيَوٰةُ ٱلدُّنْيَآ إِلَّا مَتَـٰعُ ٱلْغُرُورِ');
INSERT INTO "quran_text" VALUES(5096,57,21,'سَابِقُوٓا۟ إِلَىٰ مَغْفِرَةٍ مِّن رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا كَعَرْضِ ٱلسَّمَآءِ وَٱلْأَرْضِ أُعِدَّتْ لِلَّذِينَ ءَامَنُوا۟ بِٱللَّهِ وَرُسُلِهِۦ ۚ ذَٰلِكَ فَضْلُ ٱللَّهِ يُؤْتِيهِ مَن يَشَآءُ ۚ وَٱللَّهُ ذُو ٱلْفَضْلِ ٱلْعَظِيمِ');
INSERT INTO "quran_text" VALUES(5097,57,22,'مَآ أَصَابَ مِن مُّصِيبَةٍ فِى ٱلْأَرْضِ وَلَا فِىٓ أَنفُسِكُمْ إِلَّا فِى كِتَـٰبٍ مِّن قَبْلِ أَن نَّبْرَأَهَآ ۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٌ');
INSERT INTO "quran_text" VALUES(5098,57,23,'لِّكَيْلَا تَأْسَوْا۟ عَلَىٰ مَا فَاتَكُمْ وَلَا تَفْرَحُوا۟ بِمَآ ءَاتَىٰكُمْ ۗ وَٱللَّهُ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍ');
INSERT INTO "quran_text" VALUES(5099,57,24,'ٱلَّذِينَ يَبْخَلُونَ وَيَأْمُرُونَ ٱلنَّاسَ بِٱلْبُخْلِ ۗ وَمَن يَتَوَلَّ فَإِنَّ ٱللَّهَ هُوَ ٱلْغَنِىُّ ٱلْحَمِيدُ');
INSERT INTO "quran_text" VALUES(5100,57,25,'لَقَدْ أَرْسَلْنَا رُسُلَنَا بِٱلْبَيِّنَـٰتِ وَأَنزَلْنَا مَعَهُمُ ٱلْكِتَـٰبَ وَٱلْمِيزَانَ لِيَقُومَ ٱلنَّاسُ بِٱلْقِسْطِ ۖ وَأَنزَلْنَا ٱلْحَدِيدَ فِيهِ بَأْسٌ شَدِيدٌ وَمَنَـٰفِعُ لِلنَّاسِ وَلِيَعْلَمَ ٱللَّهُ مَن يَنصُرُهُۥ وَرُسُلَهُۥ بِٱلْغَيْبِ ۚ إِنَّ ٱللَّهَ قَوِىٌّ عَزِيزٌ');
INSERT INTO "quran_text" VALUES(5101,57,26,'وَلَقَدْ أَرْسَلْنَا نُوحًا وَإِبْرَٰهِيمَ وَجَعَلْنَا فِى ذُرِّيَّتِهِمَا ٱلنُّبُوَّةَ وَٱلْكِتَـٰبَ ۖ فَمِنْهُم مُّهْتَدٍ ۖ وَكَثِيرٌ مِّنْهُمْ فَـٰسِقُونَ');
INSERT INTO "quran_text" VALUES(5102,57,27,'ثُمَّ قَفَّيْنَا عَلَىٰٓ ءَاثَـٰرِهِم بِرُسُلِنَا وَقَفَّيْنَا بِعِيسَى ٱبْنِ مَرْيَمَ وَءَاتَيْنَـٰهُ ٱلْإِنجِيلَ وَجَعَلْنَا فِى قُلُوبِ ٱلَّذِينَ ٱتَّبَعُوهُ رَأْفَةً وَرَحْمَةً وَرَهْبَانِيَّةً ٱبْتَدَعُوهَا مَا كَتَبْنَـٰهَا عَلَيْهِمْ إِلَّا ٱبْتِغَآءَ رِضْوَٰنِ ٱللَّهِ فَمَا رَعَوْهَا حَقَّ رِعَايَتِهَا ۖ فَـَٔاتَيْنَا ٱلَّذِينَ ءَامَنُوا۟ مِنْهُمْ أَجْرَهُمْ ۖ وَكَثِيرٌ مِّنْهُمْ فَـٰسِقُونَ');
INSERT INTO "quran_text" VALUES(5103,57,28,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ وَءَامِنُوا۟ بِرَسُولِهِۦ يُؤْتِكُمْ كِفْلَيْنِ مِن رَّحْمَتِهِۦ وَيَجْعَل لَّكُمْ نُورًا تَمْشُونَ بِهِۦ وَيَغْفِرْ لَكُمْ ۚ وَٱللَّهُ غَفُورٌ رَّحِيمٌ');
INSERT INTO "quran_text" VALUES(5104,57,29,'لِّئَلَّا يَعْلَمَ أَهْلُ ٱلْكِتَـٰبِ أَلَّا يَقْدِرُونَ عَلَىٰ شَىْءٍ مِّن فَضْلِ ٱللَّهِ ۙ وَأَنَّ ٱلْفَضْلَ بِيَدِ ٱللَّهِ يُؤْتِيهِ مَن يَشَآءُ ۚ وَٱللَّهُ ذُو ٱلْفَضْلِ ٱلْعَظِيمِ');
INSERT INTO "quran_text" VALUES(5105,58,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ قَدْ سَمِعَ ٱللَّهُ قَوْلَ ٱلَّتِى تُجَـٰدِلُكَ فِى زَوْجِهَا وَتَشْتَكِىٓ إِلَى ٱللَّهِ وَٱللَّهُ يَسْمَعُ تَحَاوُرَكُمَآ ۚ إِنَّ ٱللَّهَ سَمِيعٌۢ بَصِيرٌ');
INSERT INTO "quran_text" VALUES(5106,58,2,'ٱلَّذِينَ يُظَـٰهِرُونَ مِنكُم مِّن نِّسَآئِهِم مَّا هُنَّ أُمَّهَـٰتِهِمْ ۖ إِنْ أُمَّهَـٰتُهُمْ إِلَّا ٱلَّـٰٓـِٔى وَلَدْنَهُمْ ۚ وَإِنَّهُمْ لَيَقُولُونَ مُنكَرًا مِّنَ ٱلْقَوْلِ وَزُورًا ۚ وَإِنَّ ٱللَّهَ لَعَفُوٌّ غَفُورٌ');
INSERT INTO "quran_text" VALUES(5107,58,3,'وَٱلَّذِينَ يُظَـٰهِرُونَ مِن نِّسَآئِهِمْ ثُمَّ يَعُودُونَ لِمَا قَالُوا۟ فَتَحْرِيرُ رَقَبَةٍ مِّن قَبْلِ أَن يَتَمَآسَّا ۚ ذَٰلِكُمْ تُوعَظُونَ بِهِۦ ۚ وَٱللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ');
INSERT INTO "quran_text" VALUES(5108,58,4,'فَمَن لَّمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ مِن قَبْلِ أَن يَتَمَآسَّا ۖ فَمَن لَّمْ يَسْتَطِعْ فَإِطْعَامُ سِتِّينَ مِسْكِينًا ۚ ذَٰلِكَ لِتُؤْمِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ ۚ وَتِلْكَ حُدُودُ ٱللَّهِ ۗ وَلِلْكَـٰفِرِينَ عَذَابٌ أَلِيمٌ');
INSERT INTO "quran_text" VALUES(5109,58,5,'إِنَّ ٱلَّذِينَ يُحَآدُّونَ ٱللَّهَ وَرَسُولَهُۥ كُبِتُوا۟ كَمَا كُبِتَ ٱلَّذِينَ مِن قَبْلِهِمْ ۚ وَقَدْ أَنزَلْنَآ ءَايَـٰتٍۭ بَيِّنَـٰتٍ ۚ وَلِلْكَـٰفِرِينَ عَذَابٌ مُّهِينٌ');
INSERT INTO "quran_text" VALUES(5110,58,6,'يَوْمَ يَبْعَثُهُمُ ٱللَّهُ جَمِيعًا فَيُنَبِّئُهُم بِمَا عَمِلُوٓا۟ ۚ أَحْصَىٰهُ ٱللَّهُ وَنَسُوهُ ۚ وَٱللَّهُ عَلَىٰ كُلِّ شَىْءٍ شَهِيدٌ');
INSERT INTO "quran_text" VALUES(5111,58,7,'أَلَمْ تَرَ أَنَّ ٱللَّهَ يَعْلَمُ مَا فِى ٱلسَّمَـٰوَٰتِ وَمَا فِى ٱلْأَرْضِ ۖ مَا يَكُونُ مِن نَّجْوَىٰ ثَلَـٰثَةٍ إِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ إِلَّا هُوَ سَادِسُهُمْ وَلَآ أَدْنَىٰ مِن ذَٰلِكَ وَلَآ أَكْثَرَ إِلَّا هُوَ مَعَهُمْ أَيْنَ مَا كَانُوا۟ ۖ ثُمَّ يُنَبِّئُهُم بِمَا عَمِلُوا۟ يَوْمَ ٱلْقِيَـٰمَةِ ۚ إِنَّ ٱللَّهَ بِكُلِّ شَىْءٍ عَلِيمٌ');
INSERT INTO "quran_text" VALUES(5112,58,8,'أَلَمْ تَرَ إِلَى ٱلَّذِينَ نُهُوا۟ عَنِ ٱلنَّجْوَىٰ ثُمَّ يَعُودُونَ لِمَا نُهُوا۟ عَنْهُ وَيَتَنَـٰجَوْنَ بِٱلْإِثْمِ وَٱلْعُدْوَٰنِ وَمَعْصِيَتِ ٱلرَّسُولِ وَإِذَا جَآءُوكَ حَيَّوْكَ بِمَا لَمْ يُحَيِّكَ بِهِ ٱللَّهُ وَيَقُولُونَ فِىٓ أَنفُسِهِمْ لَوْلَا يُعَذِّبُنَا ٱللَّهُ بِمَا نَقُولُ ۚ حَسْبُهُمْ جَهَنَّمُ يَصْلَوْنَهَا ۖ فَبِئْسَ ٱلْمَصِيرُ');
INSERT INTO "quran_text" VALUES(5113,58,9,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِذَا تَنَـٰجَيْتُمْ فَلَا تَتَنَـٰجَوْا۟ بِٱلْإِثْمِ وَٱلْعُدْوَٰنِ وَمَعْصِيَتِ ٱلرَّسُولِ وَتَنَـٰجَوْا۟ بِٱلْبِرِّ وَٱلتَّقْوَىٰ ۖ وَٱتَّقُوا۟ ٱللَّهَ ٱلَّذِىٓ إِلَيْهِ تُحْشَرُونَ');
INSERT INTO "quran_text" VALUES(5114,58,10,'إِنَّمَا ٱلنَّجْوَىٰ مِنَ ٱلشَّيْطَـٰنِ لِيَحْزُنَ ٱلَّذِينَ ءَامَنُوا۟ وَلَيْسَ بِضَآرِّهِمْ شَيْـًٔا إِلَّا بِإِذْنِ ٱللَّهِ ۚ وَعَلَى ٱللَّهِ فَلْيَتَوَكَّلِ ٱلْمُؤْمِنُونَ');
INSERT INTO "quran_text" VALUES(5115,58,11,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِذَا قِيلَ لَكُمْ تَفَسَّحُوا۟ فِى ٱلْمَجَـٰلِسِ فَٱفْسَحُوا۟ يَفْسَحِ ٱللَّهُ لَكُمْ ۖ وَإِذَا قِيلَ ٱنشُزُوا۟ فَٱنشُزُوا۟ يَرْفَعِ ٱللَّهُ ٱلَّذِينَ ءَامَنُوا۟ مِنكُمْ وَٱلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ دَرَجَـٰتٍ ۚ وَٱللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ');
INSERT INTO "quran_text" VALUES(5116,58,12,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِذَا نَـٰجَيْتُمُ ٱلرَّسُولَ فَقَدِّمُوا۟ بَيْنَ يَدَىْ نَجْوَىٰكُمْ صَدَقَةً ۚ ذَٰلِكَ خَيْرٌ لَّكُمْ وَأَطْهَرُ ۚ فَإِن لَّمْ تَجِدُوا۟ فَإِنَّ ٱللَّهَ غَفُورٌ رَّحِيمٌ');
INSERT INTO "quran_text" VALUES(5117,58,13,'ءَأَشْفَقْتُمْ أَن تُقَدِّمُوا۟ بَيْنَ يَدَىْ نَجْوَىٰكُمْ صَدَقَـٰتٍ ۚ فَإِذْ لَمْ تَفْعَلُوا۟ وَتَابَ ٱللَّهُ عَلَيْكُمْ فَأَقِيمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ وَأَطِيعُوا۟ ٱللَّهَ وَرَسُولَهُۥ ۚ وَٱللَّهُ خَبِيرٌۢ بِمَا تَعْمَلُونَ');
INSERT INTO "quran_text" VALUES(5118,58,14,'۞ أَلَمْ تَرَ إِلَى ٱلَّذِينَ تَوَلَّوْا۟ قَوْمًا غَضِبَ ٱللَّهُ عَلَيْهِم مَّا هُم مِّنكُمْ وَلَا مِنْهُمْ وَيَحْلِفُونَ عَلَى ٱلْكَذِبِ وَهُمْ يَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(5119,58,15,'أَعَدَّ ٱللَّهُ لَهُمْ عَذَابًا شَدِيدًا ۖ إِنَّهُمْ سَآءَ مَا كَانُوا۟ يَعْمَلُونَ');
INSERT INTO "quran_text" VALUES(5120,58,16,'ٱتَّخَذُوٓا۟ أَيْمَـٰنَهُمْ جُنَّةً فَصَدُّوا۟ عَن سَبِيلِ ٱللَّهِ فَلَهُمْ عَذَابٌ مُّهِينٌ');
INSERT INTO "quran_text" VALUES(5121,58,17,'لَّن تُغْنِىَ عَنْهُمْ أَمْوَٰلُهُمْ وَلَآ أَوْلَـٰدُهُم مِّنَ ٱللَّهِ شَيْـًٔا ۚ أُو۟لَـٰٓئِكَ أَصْحَـٰبُ ٱلنَّارِ ۖ هُمْ فِيهَا خَـٰلِدُونَ');
INSERT INTO "quran_text" VALUES(5122,58,18,'يَوْمَ يَبْعَثُهُمُ ٱللَّهُ جَمِيعًا فَيَحْلِفُونَ لَهُۥ كَمَا يَحْلِفُونَ لَكُمْ ۖ وَيَحْسَبُونَ أَنَّهُمْ عَلَىٰ شَىْءٍ ۚ أَلَآ إِنَّهُمْ هُمُ ٱلْكَـٰذِبُونَ');
INSERT INTO "quran_text" VALUES(5123,58,19,'ٱسْتَحْوَذَ عَلَيْهِمُ ٱلشَّيْطَـٰنُ فَأَنسَىٰهُمْ ذِكْرَ ٱللَّهِ ۚ أُو۟لَـٰٓئِكَ حِزْبُ ٱلشَّيْطَـٰنِ ۚ أَلَآ إِنَّ حِزْبَ ٱلشَّيْطَـٰنِ هُمُ ٱلْخَـٰسِرُونَ');
INSERT INTO "quran_text" VALUES(5124,58,20,'إِنَّ ٱلَّذِينَ يُحَآدُّونَ ٱللَّهَ وَرَسُولَهُۥٓ أُو۟لَـٰٓئِكَ فِى ٱلْأَذَلِّينَ');
INSERT INTO "quran_text" VALUES(5125,58,21,'كَتَبَ ٱللَّهُ لَأَغْلِبَنَّ أَنَا۠ وَرُسُلِىٓ ۚ إِنَّ ٱللَّهَ قَوِىٌّ عَزِيزٌ');
INSERT INTO "quran_text" VALUES(5126,58,22,'لَّا تَجِدُ قَوْمًا يُؤْمِنُونَ بِٱللَّهِ وَٱلْيَوْمِ ٱلْـَٔاخِرِ يُوَآدُّونَ مَنْ حَآدَّ ٱللَّهَ وَرَسُولَهُۥ وَلَوْ كَانُوٓا۟ ءَابَآءَهُمْ أَوْ أَبْنَآءَهُمْ أَوْ إِخْوَٰنَهُمْ أَوْ عَشِيرَتَهُمْ ۚ أُو۟لَـٰٓئِكَ كَتَبَ فِى قُلُوبِهِمُ ٱلْإِيمَـٰنَ وَأَيَّدَهُم بِرُوحٍ مِّنْهُ ۖ وَيُدْخِلُهُمْ جَنَّـٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ خَـٰلِدِينَ فِيهَا ۚ رَضِىَ ٱللَّهُ عَنْهُمْ وَرَضُوا۟ عَنْهُ ۚ أُو۟لَـٰٓئِكَ حِزْبُ ٱللَّهِ ۚ أَلَآ إِنَّ حِزْبَ ٱللَّهِ هُمُ ٱلْمُفْلِحُونَ');
INSERT INTO "quran_text" VALUES(5127,59,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ سَبَّحَ لِلَّهِ مَا فِى ٱلسَّمَـٰوَٰتِ وَمَا فِى ٱلْأَرْضِ ۖ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ');
INSERT INTO "quran_text" VALUES(5128,59,2,'هُوَ ٱلَّذِىٓ أَخْرَجَ ٱلَّذِينَ كَفَرُوا۟ مِنْ أَهْلِ ٱلْكِتَـٰبِ مِن دِيَـٰرِهِمْ لِأَوَّلِ ٱلْحَشْرِ ۚ مَا ظَنَنتُمْ أَن يَخْرُجُوا۟ ۖ وَظَنُّوٓا۟ أَنَّهُم مَّانِعَتُهُمْ حُصُونُهُم مِّنَ ٱللَّهِ فَأَتَىٰهُمُ ٱللَّهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوا۟ ۖ وَقَذَفَ فِى قُلُوبِهِمُ ٱلرُّعْبَ ۚ يُخْرِبُونَ بُيُوتَهُم بِأَيْدِيهِمْ وَأَيْدِى ٱلْمُؤْمِنِينَ فَٱعْتَبِرُوا۟ يَـٰٓأُو۟لِى ٱلْأَبْصَـٰرِ');
INSERT INTO "quran_text" VALUES(5129,59,3,'وَلَوْلَآ أَن كَتَبَ ٱللَّهُ عَلَيْهِمُ ٱلْجَلَآءَ لَعَذَّبَهُمْ فِى ٱلدُّنْيَا ۖ وَلَهُمْ فِى ٱلْـَٔاخِرَةِ عَذَابُ ٱلنَّارِ');
INSERT INTO "quran_text" VALUES(5130,59,4,'ذَٰلِكَ بِأَنَّهُمْ شَآقُّوا۟ ٱللَّهَ وَرَسُولَهُۥ ۖ وَمَن يُشَآقِّ ٱللَّهَ فَإِنَّ ٱللَّهَ شَدِيدُ ٱلْعِقَابِ');
INSERT INTO "quran_text" VALUES(5131,59,5,'مَا قَطَعْتُم مِّن لِّينَةٍ أَوْ تَرَكْتُمُوهَا قَآئِمَةً عَلَىٰٓ أُصُولِهَا فَبِإِذْنِ ٱللَّهِ وَلِيُخْزِىَ ٱلْفَـٰسِقِينَ');
INSERT INTO "quran_text" VALUES(5132,59,6,'وَمَآ أَفَآءَ ٱللَّهُ عَلَىٰ رَسُولِهِۦ مِنْهُمْ فَمَآ أَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٍ وَلَا رِكَابٍ وَلَـٰكِنَّ ٱللَّهَ يُسَلِّطُ رُسُلَهُۥ عَلَىٰ مَن يَشَآءُ ۚ وَٱللَّهُ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ');
INSERT INTO "quran_text" VALUES(5133,59,7,'مَّآ أَفَآءَ ٱللَّهُ عَلَىٰ رَسُولِهِۦ مِنْ أَهْلِ ٱلْقُرَىٰ فَلِلَّهِ وَلِلرَّسُولِ وَلِذِى ٱلْقُرْبَىٰ وَٱلْيَتَـٰمَىٰ وَٱلْمَسَـٰكِينِ وَٱبْنِ ٱلسَّبِيلِ كَىْ لَا يَكُونَ دُولَةًۢ بَيْنَ ٱلْأَغْنِيَآءِ مِنكُمْ ۚ وَمَآ ءَاتَىٰكُمُ ٱلرَّسُولُ فَخُذُوهُ وَمَا نَهَىٰكُمْ عَنْهُ فَٱنتَهُوا۟ ۚ وَٱتَّقُوا۟ ٱللَّهَ ۖ إِنَّ ٱللَّهَ شَدِيدُ ٱلْعِقَابِ');
INSERT INTO "quran_text" VALUES(5134,59,8,'لِلْفُقَرَآءِ ٱلْمُهَـٰجِرِينَ ٱلَّذِينَ أُخْرِجُوا۟ مِن دِيَـٰرِهِمْ وَأَمْوَٰلِهِمْ يَبْتَغُونَ فَضْلًا مِّنَ ٱللَّهِ وَرِضْوَٰنًا وَيَنصُرُونَ ٱللَّهَ وَرَسُولَهُۥٓ ۚ أُو۟لَـٰٓئِكَ هُمُ ٱلصَّـٰدِقُونَ');
INSERT INTO "quran_text" VALUES(5135,59,9,'وَٱلَّذِينَ تَبَوَّءُو ٱلدَّارَ وَٱلْإِيمَـٰنَ مِن قَبْلِهِمْ يُحِبُّونَ مَنْ هَاجَرَ إِلَيْهِمْ وَلَا يَجِدُونَ فِى صُدُورِهِمْ حَاجَةً مِّمَّآ أُوتُوا۟ وَيُؤْثِرُونَ عَلَىٰٓ أَنفُسِهِمْ وَلَوْ كَانَ بِهِمْ خَصَاصَةٌ ۚ وَمَن يُوقَ شُحَّ نَفْسِهِۦ فَأُو۟لَـٰٓئِكَ هُمُ ٱلْمُفْلِحُونَ');
INSERT INTO "quran_text" VALUES(5136,59,10,'وَٱلَّذِينَ جَآءُو مِنۢ بَعْدِهِمْ يَقُولُونَ رَبَّنَا ٱغْفِرْ لَنَا وَلِإِخْوَٰنِنَا ٱلَّذِينَ سَبَقُونَا بِٱلْإِيمَـٰنِ وَلَا تَجْعَلْ فِى قُلُوبِنَا غِلًّا لِّلَّذِينَ ءَامَنُوا۟ رَبَّنَآ إِنَّكَ رَءُوفٌ رَّحِيمٌ');
INSERT INTO "quran_text" VALUES(5137,59,11,'۞ أَلَمْ تَرَ إِلَى ٱلَّذِينَ نَافَقُوا۟ يَقُولُونَ لِإِخْوَٰنِهِمُ ٱلَّذِينَ كَفَرُوا۟ مِنْ أَهْلِ ٱلْكِتَـٰبِ لَئِنْ أُخْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَلَا نُطِيعُ فِيكُمْ أَحَدًا أَبَدًا وَإِن قُوتِلْتُمْ لَنَنصُرَنَّكُمْ وَٱللَّهُ يَشْهَدُ إِنَّهُمْ لَكَـٰذِبُونَ');
INSERT INTO "quran_text" VALUES(5138,59,12,'لَئِنْ أُخْرِجُوا۟ لَا يَخْرُجُونَ مَعَهُمْ وَلَئِن قُوتِلُوا۟ لَا يَنصُرُونَهُمْ وَلَئِن نَّصَرُوهُمْ لَيُوَلُّنَّ ٱلْأَدْبَـٰرَ ثُمَّ لَا يُنصَرُونَ');
INSERT INTO "quran_text" VALUES(5139,59,13,'لَأَنتُمْ أَشَدُّ رَهْبَةً فِى صُدُورِهِم مِّنَ ٱللَّهِ ۚ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَّا يَفْقَهُونَ');
INSERT INTO "quran_text" VALUES(5140,59,14,'لَا يُقَـٰتِلُونَكُمْ جَمِيعًا إِلَّا فِى قُرًى مُّحَصَّنَةٍ أَوْ مِن وَرَآءِ جُدُرٍۭ ۚ بَأْسُهُم بَيْنَهُمْ شَدِيدٌ ۚ تَحْسَبُهُمْ جَمِيعًا وَقُلُوبُهُمْ شَتَّىٰ ۚ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَّا يَعْقِلُونَ');
INSERT INTO "quran_text" VALUES(5141,59,15,'كَمَثَلِ ٱلَّذِينَ مِن قَبْلِهِمْ قَرِيبًا ۖ ذَاقُوا۟ وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ');
INSERT INTO "quran_text" VALUES(5142,59,16,'كَمَثَلِ ٱلشَّيْطَـٰنِ إِذْ قَالَ لِلْإِنسَـٰنِ ٱكْفُرْ فَلَمَّا كَفَرَ قَالَ إِنِّى بَرِىٓءٌ مِّنكَ إِنِّىٓ أَخَافُ ٱللَّهَ رَبَّ ٱلْعَـٰلَمِينَ');
INSERT INTO "quran_text" VALUES(5143,59,17,'فَكَانَ عَـٰقِبَتَهُمَآ أَنَّهُمَا فِى ٱلنَّارِ خَـٰلِدَيْنِ فِيهَا ۚ وَذَٰلِكَ جَزَٰٓؤُا۟ ٱلظَّـٰلِمِينَ');
INSERT INTO "quran_text" VALUES(5144,59,18,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ وَلْتَنظُرْ نَفْسٌ مَّا قَدَّمَتْ لِغَدٍ ۖ وَٱتَّقُوا۟ ٱللَّهَ ۚ إِنَّ ٱللَّهَ خَبِيرٌۢ بِمَا تَعْمَلُونَ');
INSERT INTO "quran_text" VALUES(5145,59,19,'وَلَا تَكُونُوا۟ كَٱلَّذِينَ نَسُوا۟ ٱللَّهَ فَأَنسَىٰهُمْ أَنفُسَهُمْ ۚ أُو۟لَـٰٓئِكَ هُمُ ٱلْفَـٰسِقُونَ');
INSERT INTO "quran_text" VALUES(5146,59,20,'لَا يَسْتَوِىٓ أَصْحَـٰبُ ٱلنَّارِ وَأَصْحَـٰبُ ٱلْجَنَّةِ ۚ أَصْحَـٰبُ ٱلْجَنَّةِ هُمُ ٱلْفَآئِزُونَ');
INSERT INTO "quran_text" VALUES(5147,59,21,'لَوْ أَنزَلْنَا هَـٰذَا ٱلْقُرْءَانَ عَلَىٰ جَبَلٍ لَّرَأَيْتَهُۥ خَـٰشِعًا مُّتَصَدِّعًا مِّنْ خَشْيَةِ ٱللَّهِ ۚ وَتِلْكَ ٱلْأَمْثَـٰلُ نَضْرِبُهَا لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُونَ');
INSERT INTO "quran_text" VALUES(5148,59,22,'هُوَ ٱللَّهُ ٱلَّذِى لَآ إِلَـٰهَ إِلَّا هُوَ ۖ عَـٰلِمُ ٱلْغَيْبِ وَٱلشَّهَـٰدَةِ ۖ هُوَ ٱلرَّحْمَـٰنُ ٱلرَّحِيمُ');
INSERT INTO "quran_text" VALUES(5149,59,23,'هُوَ ٱللَّهُ ٱلَّذِى لَآ إِلَـٰهَ إِلَّا هُوَ ٱلْمَلِكُ ٱلْقُدُّوسُ ٱلسَّلَـٰمُ ٱلْمُؤْمِنُ ٱلْمُهَيْمِنُ ٱلْعَزِيزُ ٱلْجَبَّارُ ٱلْمُتَكَبِّرُ ۚ سُبْحَـٰنَ ٱللَّهِ عَمَّا يُشْرِكُونَ');
INSERT INTO "quran_text" VALUES(5150,59,24,'هُوَ ٱللَّهُ ٱلْخَـٰلِقُ ٱلْبَارِئُ ٱلْمُصَوِّرُ ۖ لَهُ ٱلْأَسْمَآءُ ٱلْحُسْنَىٰ ۚ يُسَبِّحُ لَهُۥ مَا فِى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۖ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ');
INSERT INTO "quran_text" VALUES(5151,60,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تَتَّخِذُوا۟ عَدُوِّى وَعَدُوَّكُمْ أَوْلِيَآءَ تُلْقُونَ إِلَيْهِم بِٱلْمَوَدَّةِ وَقَدْ كَفَرُوا۟ بِمَا جَآءَكُم مِّنَ ٱلْحَقِّ يُخْرِجُونَ ٱلرَّسُولَ وَإِيَّاكُمْ ۙ أَن تُؤْمِنُوا۟ بِٱللَّهِ رَبِّكُمْ إِن كُنتُمْ خَرَجْتُمْ جِهَـٰدًا فِى سَبِيلِى وَٱبْتِغَآءَ مَرْضَاتِى ۚ تُسِرُّونَ إِلَيْهِم بِٱلْمَوَدَّةِ وَأَنَا۠ أَعْلَمُ بِمَآ أَخْفَيْتُمْ وَمَآ أَعْلَنتُمْ ۚ وَمَن يَفْعَلْهُ مِنكُمْ فَقَدْ ضَلَّ سَوَآءَ ٱلسَّبِيلِ');
INSERT INTO "quran_text" VALUES(5152,60,2,'إِن يَثْقَفُوكُمْ يَكُونُوا۟ لَكُمْ أَعْدَآءً وَيَبْسُطُوٓا۟ إِلَيْكُمْ أَيْدِيَهُمْ وَأَلْسِنَتَهُم بِٱلسُّوٓءِ وَوَدُّوا۟ لَوْ تَكْفُرُونَ');
INSERT INTO "quran_text" VALUES(5153,60,3,'لَن تَنفَعَكُمْ أَرْحَامُكُمْ وَلَآ أَوْلَـٰدُكُمْ ۚ يَوْمَ ٱلْقِيَـٰمَةِ يَفْصِلُ بَيْنَكُمْ ۚ وَٱللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ');
INSERT INTO "quran_text" VALUES(5154,60,4,'قَدْ كَانَتْ لَكُمْ أُسْوَةٌ حَسَنَةٌ فِىٓ إِبْرَٰهِيمَ وَٱلَّذِينَ مَعَهُۥٓ إِذْ قَالُوا۟ لِقَوْمِهِمْ إِنَّا بُرَءَٰٓؤُا۟ مِنكُمْ وَمِمَّا تَعْبُدُونَ مِن دُونِ ٱللَّهِ كَفَرْنَا بِكُمْ وَبَدَا بَيْنَنَا وَبَيْنَكُمُ ٱلْعَدَٰوَةُ وَٱلْبَغْضَآءُ أَبَدًا حَتَّىٰ تُؤْمِنُوا۟ بِٱللَّهِ وَحْدَهُۥٓ إِلَّا قَوْلَ إِبْرَٰهِيمَ لِأَبِيهِ لَأَسْتَغْفِرَنَّ لَكَ وَمَآ أَمْلِكُ لَكَ مِنَ ٱللَّهِ مِن شَىْءٍ ۖ رَّبَّنَا عَلَيْكَ تَوَكَّلْنَا وَإِلَيْكَ أَنَبْنَا وَإِلَيْكَ ٱلْمَصِيرُ');
INSERT INTO "quran_text" VALUES(5155,60,5,'رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِّلَّذِينَ كَفَرُوا۟ وَٱغْفِرْ لَنَا رَبَّنَآ ۖ إِنَّكَ أَنتَ ٱلْعَزِيزُ ٱلْحَكِيمُ');
INSERT INTO "quran_text" VALUES(5156,60,6,'لَقَدْ كَانَ لَكُمْ فِيهِمْ أُسْوَةٌ حَسَنَةٌ لِّمَن كَانَ يَرْجُوا۟ ٱللَّهَ وَٱلْيَوْمَ ٱلْـَٔاخِرَ ۚ وَمَن يَتَوَلَّ فَإِنَّ ٱللَّهَ هُوَ ٱلْغَنِىُّ ٱلْحَمِيدُ');
INSERT INTO "quran_text" VALUES(5157,60,7,'۞ عَسَى ٱللَّهُ أَن يَجْعَلَ بَيْنَكُمْ وَبَيْنَ ٱلَّذِينَ عَادَيْتُم مِّنْهُم مَّوَدَّةً ۚ وَٱللَّهُ قَدِيرٌ ۚ وَٱللَّهُ غَفُورٌ رَّحِيمٌ');
INSERT INTO "quran_text" VALUES(5158,60,8,'لَّا يَنْهَىٰكُمُ ٱللَّهُ عَنِ ٱلَّذِينَ لَمْ يُقَـٰتِلُوكُمْ فِى ٱلدِّينِ وَلَمْ يُخْرِجُوكُم مِّن دِيَـٰرِكُمْ أَن تَبَرُّوهُمْ وَتُقْسِطُوٓا۟ إِلَيْهِمْ ۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلْمُقْسِطِينَ');
INSERT INTO "quran_text" VALUES(5159,60,9,'إِنَّمَا يَنْهَىٰكُمُ ٱللَّهُ عَنِ ٱلَّذِينَ قَـٰتَلُوكُمْ فِى ٱلدِّينِ وَأَخْرَجُوكُم مِّن دِيَـٰرِكُمْ وَظَـٰهَرُوا۟ عَلَىٰٓ إِخْرَاجِكُمْ أَن تَوَلَّوْهُمْ ۚ وَمَن يَتَوَلَّهُمْ فَأُو۟لَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ');
INSERT INTO "quran_text" VALUES(5160,60,10,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِذَا جَآءَكُمُ ٱلْمُؤْمِنَـٰتُ مُهَـٰجِرَٰتٍ فَٱمْتَحِنُوهُنَّ ۖ ٱللَّهُ أَعْلَمُ بِإِيمَـٰنِهِنَّ ۖ فَإِنْ عَلِمْتُمُوهُنَّ مُؤْمِنَـٰتٍ فَلَا تَرْجِعُوهُنَّ إِلَى ٱلْكُفَّارِ ۖ لَا هُنَّ حِلٌّ لَّهُمْ وَلَا هُمْ يَحِلُّونَ لَهُنَّ ۖ وَءَاتُوهُم مَّآ أَنفَقُوا۟ ۚ وَلَا جُنَاحَ عَلَيْكُمْ أَن تَنكِحُوهُنَّ إِذَآ ءَاتَيْتُمُوهُنَّ أُجُورَهُنَّ ۚ وَلَا تُمْسِكُوا۟ بِعِصَمِ ٱلْكَوَافِرِ وَسْـَٔلُوا۟ مَآ أَنفَقْتُمْ وَلْيَسْـَٔلُوا۟ مَآ أَنفَقُوا۟ ۚ ذَٰلِكُمْ حُكْمُ ٱللَّهِ ۖ يَحْكُمُ بَيْنَكُمْ ۚ وَٱللَّهُ عَلِيمٌ حَكِيمٌ');
INSERT INTO "quran_text" VALUES(5161,60,11,'وَإِن فَاتَكُمْ شَىْءٌ مِّنْ أَزْوَٰجِكُمْ إِلَى ٱلْكُفَّارِ فَعَاقَبْتُمْ فَـَٔاتُوا۟ ٱلَّذِينَ ذَهَبَتْ أَزْوَٰجُهُم مِّثْلَ مَآ أَنفَقُوا۟ ۚ وَٱتَّقُوا۟ ٱللَّهَ ٱلَّذِىٓ أَنتُم بِهِۦ مُؤْمِنُونَ');
INSERT INTO "quran_text" VALUES(5162,60,12,'يَـٰٓأَيُّهَا ٱلنَّبِىُّ إِذَا جَآءَكَ ٱلْمُؤْمِنَـٰتُ يُبَايِعْنَكَ عَلَىٰٓ أَن لَّا يُشْرِكْنَ بِٱللَّهِ شَيْـًٔا وَلَا يَسْرِقْنَ وَلَا يَزْنِينَ وَلَا يَقْتُلْنَ أَوْلَـٰدَهُنَّ وَلَا يَأْتِينَ بِبُهْتَـٰنٍ يَفْتَرِينَهُۥ بَيْنَ أَيْدِيهِنَّ وَأَرْجُلِهِنَّ وَلَا يَعْصِينَكَ فِى مَعْرُوفٍ ۙ فَبَايِعْهُنَّ وَٱسْتَغْفِرْ لَهُنَّ ٱللَّهَ ۖ إِنَّ ٱللَّهَ غَفُورٌ رَّحِيمٌ');
INSERT INTO "quran_text" VALUES(5163,60,13,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تَتَوَلَّوْا۟ قَوْمًا غَضِبَ ٱللَّهُ عَلَيْهِمْ قَدْ يَئِسُوا۟ مِنَ ٱلْـَٔاخِرَةِ كَمَا يَئِسَ ٱلْكُفَّارُ مِنْ أَصْحَـٰبِ ٱلْقُبُورِ');
INSERT INTO "quran_text" VALUES(5164,61,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ سَبَّحَ لِلَّهِ مَا فِى ٱلسَّمَـٰوَٰتِ وَمَا فِى ٱلْأَرْضِ ۖ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ');
INSERT INTO "quran_text" VALUES(5165,61,2,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لِمَ تَقُولُونَ مَا لَا تَفْعَلُونَ');
INSERT INTO "quran_text" VALUES(5166,61,3,'كَبُرَ مَقْتًا عِندَ ٱللَّهِ أَن تَقُولُوا۟ مَا لَا تَفْعَلُونَ');
INSERT INTO "quran_text" VALUES(5167,61,4,'إِنَّ ٱللَّهَ يُحِبُّ ٱلَّذِينَ يُقَـٰتِلُونَ فِى سَبِيلِهِۦ صَفًّا كَأَنَّهُم بُنْيَـٰنٌ مَّرْصُوصٌ');
INSERT INTO "quran_text" VALUES(5168,61,5,'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِۦ يَـٰقَوْمِ لِمَ تُؤْذُونَنِى وَقَد تَّعْلَمُونَ أَنِّى رَسُولُ ٱللَّهِ إِلَيْكُمْ ۖ فَلَمَّا زَاغُوٓا۟ أَزَاغَ ٱللَّهُ قُلُوبَهُمْ ۚ وَٱللَّهُ لَا يَهْدِى ٱلْقَوْمَ ٱلْفَـٰسِقِينَ');
INSERT INTO "quran_text" VALUES(5169,61,6,'وَإِذْ قَالَ عِيسَى ٱبْنُ مَرْيَمَ يَـٰبَنِىٓ إِسْرَٰٓءِيلَ إِنِّى رَسُولُ ٱللَّهِ إِلَيْكُم مُّصَدِّقًا لِّمَا بَيْنَ يَدَىَّ مِنَ ٱلتَّوْرَىٰةِ وَمُبَشِّرًۢا بِرَسُولٍ يَأْتِى مِنۢ بَعْدِى ٱسْمُهُۥٓ أَحْمَدُ ۖ فَلَمَّا جَآءَهُم بِٱلْبَيِّنَـٰتِ قَالُوا۟ هَـٰذَا سِحْرٌ مُّبِينٌ');
INSERT INTO "quran_text" VALUES(5170,61,7,'وَمَنْ أَظْلَمُ مِمَّنِ ٱفْتَرَىٰ عَلَى ٱللَّهِ ٱلْكَذِبَ وَهُوَ يُدْعَىٰٓ إِلَى ٱلْإِسْلَـٰمِ ۚ وَٱللَّهُ لَا يَهْدِى ٱلْقَوْمَ ٱلظَّـٰلِمِينَ');
INSERT INTO "quran_text" VALUES(5171,61,8,'يُرِيدُونَ لِيُطْفِـُٔوا۟ نُورَ ٱللَّهِ بِأَفْوَٰهِهِمْ وَٱللَّهُ مُتِمُّ نُورِهِۦ وَلَوْ كَرِهَ ٱلْكَـٰفِرُونَ');
INSERT INTO "quran_text" VALUES(5172,61,9,'هُوَ ٱلَّذِىٓ أَرْسَلَ رَسُولَهُۥ بِٱلْهُدَىٰ وَدِينِ ٱلْحَقِّ لِيُظْهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦ وَلَوْ كَرِهَ ٱلْمُشْرِكُونَ');
INSERT INTO "quran_text" VALUES(5173,61,10,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ هَلْ أَدُلُّكُمْ عَلَىٰ تِجَـٰرَةٍ تُنجِيكُم مِّنْ عَذَابٍ أَلِيمٍ');
INSERT INTO "quran_text" VALUES(5174,61,11,'تُؤْمِنُونَ بِٱللَّهِ وَرَسُولِهِۦ وَتُجَـٰهِدُونَ فِى سَبِيلِ ٱللَّهِ بِأَمْوَٰلِكُمْ وَأَنفُسِكُمْ ۚ ذَٰلِكُمْ خَيْرٌ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(5175,61,12,'يَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَيُدْخِلْكُمْ جَنَّـٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ وَمَسَـٰكِنَ طَيِّبَةً فِى جَنَّـٰتِ عَدْنٍ ۚ ذَٰلِكَ ٱلْفَوْزُ ٱلْعَظِيمُ');
INSERT INTO "quran_text" VALUES(5176,61,13,'وَأُخْرَىٰ تُحِبُّونَهَا ۖ نَصْرٌ مِّنَ ٱللَّهِ وَفَتْحٌ قَرِيبٌ ۗ وَبَشِّرِ ٱلْمُؤْمِنِينَ');
INSERT INTO "quran_text" VALUES(5177,61,14,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ كُونُوٓا۟ أَنصَارَ ٱللَّهِ كَمَا قَالَ عِيسَى ٱبْنُ مَرْيَمَ لِلْحَوَارِيِّـۧنَ مَنْ أَنصَارِىٓ إِلَى ٱللَّهِ ۖ قَالَ ٱلْحَوَارِيُّونَ نَحْنُ أَنصَارُ ٱللَّهِ ۖ فَـَٔامَنَت طَّآئِفَةٌ مِّنۢ بَنِىٓ إِسْرَٰٓءِيلَ وَكَفَرَت طَّآئِفَةٌ ۖ فَأَيَّدْنَا ٱلَّذِينَ ءَامَنُوا۟ عَلَىٰ عَدُوِّهِمْ فَأَصْبَحُوا۟ ظَـٰهِرِينَ');
INSERT INTO "quran_text" VALUES(5178,62,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ يُسَبِّحُ لِلَّهِ مَا فِى ٱلسَّمَـٰوَٰتِ وَمَا فِى ٱلْأَرْضِ ٱلْمَلِكِ ٱلْقُدُّوسِ ٱلْعَزِيزِ ٱلْحَكِيمِ');
INSERT INTO "quran_text" VALUES(5179,62,2,'هُوَ ٱلَّذِى بَعَثَ فِى ٱلْأُمِّيِّـۧنَ رَسُولًا مِّنْهُمْ يَتْلُوا۟ عَلَيْهِمْ ءَايَـٰتِهِۦ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ ٱلْكِتَـٰبَ وَٱلْحِكْمَةَ وَإِن كَانُوا۟ مِن قَبْلُ لَفِى ضَلَـٰلٍ مُّبِينٍ');
INSERT INTO "quran_text" VALUES(5180,62,3,'وَءَاخَرِينَ مِنْهُمْ لَمَّا يَلْحَقُوا۟ بِهِمْ ۚ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ');
INSERT INTO "quran_text" VALUES(5181,62,4,'ذَٰلِكَ فَضْلُ ٱللَّهِ يُؤْتِيهِ مَن يَشَآءُ ۚ وَٱللَّهُ ذُو ٱلْفَضْلِ ٱلْعَظِيمِ');
INSERT INTO "quran_text" VALUES(5182,62,5,'مَثَلُ ٱلَّذِينَ حُمِّلُوا۟ ٱلتَّوْرَىٰةَ ثُمَّ لَمْ يَحْمِلُوهَا كَمَثَلِ ٱلْحِمَارِ يَحْمِلُ أَسْفَارًۢا ۚ بِئْسَ مَثَلُ ٱلْقَوْمِ ٱلَّذِينَ كَذَّبُوا۟ بِـَٔايَـٰتِ ٱللَّهِ ۚ وَٱللَّهُ لَا يَهْدِى ٱلْقَوْمَ ٱلظَّـٰلِمِينَ');
INSERT INTO "quran_text" VALUES(5183,62,6,'قُلْ يَـٰٓأَيُّهَا ٱلَّذِينَ هَادُوٓا۟ إِن زَعَمْتُمْ أَنَّكُمْ أَوْلِيَآءُ لِلَّهِ مِن دُونِ ٱلنَّاسِ فَتَمَنَّوُا۟ ٱلْمَوْتَ إِن كُنتُمْ صَـٰدِقِينَ');
INSERT INTO "quran_text" VALUES(5184,62,7,'وَلَا يَتَمَنَّوْنَهُۥٓ أَبَدًۢا بِمَا قَدَّمَتْ أَيْدِيهِمْ ۚ وَٱللَّهُ عَلِيمٌۢ بِٱلظَّـٰلِمِينَ');
INSERT INTO "quran_text" VALUES(5185,62,8,'قُلْ إِنَّ ٱلْمَوْتَ ٱلَّذِى تَفِرُّونَ مِنْهُ فَإِنَّهُۥ مُلَـٰقِيكُمْ ۖ ثُمَّ تُرَدُّونَ إِلَىٰ عَـٰلِمِ ٱلْغَيْبِ وَٱلشَّهَـٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ');
INSERT INTO "quran_text" VALUES(5186,62,9,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِذَا نُودِىَ لِلصَّلَوٰةِ مِن يَوْمِ ٱلْجُمُعَةِ فَٱسْعَوْا۟ إِلَىٰ ذِكْرِ ٱللَّهِ وَذَرُوا۟ ٱلْبَيْعَ ۚ ذَٰلِكُمْ خَيْرٌ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(5187,62,10,'فَإِذَا قُضِيَتِ ٱلصَّلَوٰةُ فَٱنتَشِرُوا۟ فِى ٱلْأَرْضِ وَٱبْتَغُوا۟ مِن فَضْلِ ٱللَّهِ وَٱذْكُرُوا۟ ٱللَّهَ كَثِيرًا لَّعَلَّكُمْ تُفْلِحُونَ');
INSERT INTO "quran_text" VALUES(5188,62,11,'وَإِذَا رَأَوْا۟ تِجَـٰرَةً أَوْ لَهْوًا ٱنفَضُّوٓا۟ إِلَيْهَا وَتَرَكُوكَ قَآئِمًا ۚ قُلْ مَا عِندَ ٱللَّهِ خَيْرٌ مِّنَ ٱللَّهْوِ وَمِنَ ٱلتِّجَـٰرَةِ ۚ وَٱللَّهُ خَيْرُ ٱلرَّٰزِقِينَ');
INSERT INTO "quran_text" VALUES(5189,63,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ إِذَا جَآءَكَ ٱلْمُنَـٰفِقُونَ قَالُوا۟ نَشْهَدُ إِنَّكَ لَرَسُولُ ٱللَّهِ ۗ وَٱللَّهُ يَعْلَمُ إِنَّكَ لَرَسُولُهُۥ وَٱللَّهُ يَشْهَدُ إِنَّ ٱلْمُنَـٰفِقِينَ لَكَـٰذِبُونَ');
INSERT INTO "quran_text" VALUES(5190,63,2,'ٱتَّخَذُوٓا۟ أَيْمَـٰنَهُمْ جُنَّةً فَصَدُّوا۟ عَن سَبِيلِ ٱللَّهِ ۚ إِنَّهُمْ سَآءَ مَا كَانُوا۟ يَعْمَلُونَ');
INSERT INTO "quran_text" VALUES(5191,63,3,'ذَٰلِكَ بِأَنَّهُمْ ءَامَنُوا۟ ثُمَّ كَفَرُوا۟ فَطُبِعَ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَ');
INSERT INTO "quran_text" VALUES(5192,63,4,'۞ وَإِذَا رَأَيْتَهُمْ تُعْجِبُكَ أَجْسَامُهُمْ ۖ وَإِن يَقُولُوا۟ تَسْمَعْ لِقَوْلِهِمْ ۖ كَأَنَّهُمْ خُشُبٌ مُّسَنَّدَةٌ ۖ يَحْسَبُونَ كُلَّ صَيْحَةٍ عَلَيْهِمْ ۚ هُمُ ٱلْعَدُوُّ فَٱحْذَرْهُمْ ۚ قَـٰتَلَهُمُ ٱللَّهُ ۖ أَنَّىٰ يُؤْفَكُونَ');
INSERT INTO "quran_text" VALUES(5193,63,5,'وَإِذَا قِيلَ لَهُمْ تَعَالَوْا۟ يَسْتَغْفِرْ لَكُمْ رَسُولُ ٱللَّهِ لَوَّوْا۟ رُءُوسَهُمْ وَرَأَيْتَهُمْ يَصُدُّونَ وَهُم مُّسْتَكْبِرُونَ');
INSERT INTO "quran_text" VALUES(5194,63,6,'سَوَآءٌ عَلَيْهِمْ أَسْتَغْفَرْتَ لَهُمْ أَمْ لَمْ تَسْتَغْفِرْ لَهُمْ لَن يَغْفِرَ ٱللَّهُ لَهُمْ ۚ إِنَّ ٱللَّهَ لَا يَهْدِى ٱلْقَوْمَ ٱلْفَـٰسِقِينَ');
INSERT INTO "quran_text" VALUES(5195,63,7,'هُمُ ٱلَّذِينَ يَقُولُونَ لَا تُنفِقُوا۟ عَلَىٰ مَنْ عِندَ رَسُولِ ٱللَّهِ حَتَّىٰ يَنفَضُّوا۟ ۗ وَلِلَّهِ خَزَآئِنُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَلَـٰكِنَّ ٱلْمُنَـٰفِقِينَ لَا يَفْقَهُونَ');
INSERT INTO "quran_text" VALUES(5196,63,8,'يَقُولُونَ لَئِن رَّجَعْنَآ إِلَى ٱلْمَدِينَةِ لَيُخْرِجَنَّ ٱلْأَعَزُّ مِنْهَا ٱلْأَذَلَّ ۚ وَلِلَّهِ ٱلْعِزَّةُ وَلِرَسُولِهِۦ وَلِلْمُؤْمِنِينَ وَلَـٰكِنَّ ٱلْمُنَـٰفِقِينَ لَا يَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(5197,63,9,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تُلْهِكُمْ أَمْوَٰلُكُمْ وَلَآ أَوْلَـٰدُكُمْ عَن ذِكْرِ ٱللَّهِ ۚ وَمَن يَفْعَلْ ذَٰلِكَ فَأُو۟لَـٰٓئِكَ هُمُ ٱلْخَـٰسِرُونَ');
INSERT INTO "quran_text" VALUES(5198,63,10,'وَأَنفِقُوا۟ مِن مَّا رَزَقْنَـٰكُم مِّن قَبْلِ أَن يَأْتِىَ أَحَدَكُمُ ٱلْمَوْتُ فَيَقُولَ رَبِّ لَوْلَآ أَخَّرْتَنِىٓ إِلَىٰٓ أَجَلٍ قَرِيبٍ فَأَصَّدَّقَ وَأَكُن مِّنَ ٱلصَّـٰلِحِينَ');
INSERT INTO "quran_text" VALUES(5199,63,11,'وَلَن يُؤَخِّرَ ٱللَّهُ نَفْسًا إِذَا جَآءَ أَجَلُهَا ۚ وَٱللَّهُ خَبِيرٌۢ بِمَا تَعْمَلُونَ');
INSERT INTO "quran_text" VALUES(5200,64,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ يُسَبِّحُ لِلَّهِ مَا فِى ٱلسَّمَـٰوَٰتِ وَمَا فِى ٱلْأَرْضِ ۖ لَهُ ٱلْمُلْكُ وَلَهُ ٱلْحَمْدُ ۖ وَهُوَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ');
INSERT INTO "quran_text" VALUES(5201,64,2,'هُوَ ٱلَّذِى خَلَقَكُمْ فَمِنكُمْ كَافِرٌ وَمِنكُم مُّؤْمِنٌ ۚ وَٱللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ');
INSERT INTO "quran_text" VALUES(5202,64,3,'خَلَقَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ بِٱلْحَقِّ وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ ۖ وَإِلَيْهِ ٱلْمَصِيرُ');
INSERT INTO "quran_text" VALUES(5203,64,4,'يَعْلَمُ مَا فِى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَيَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ ۚ وَٱللَّهُ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ');
INSERT INTO "quran_text" VALUES(5204,64,5,'أَلَمْ يَأْتِكُمْ نَبَؤُا۟ ٱلَّذِينَ كَفَرُوا۟ مِن قَبْلُ فَذَاقُوا۟ وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ');
INSERT INTO "quran_text" VALUES(5205,64,6,'ذَٰلِكَ بِأَنَّهُۥ كَانَت تَّأْتِيهِمْ رُسُلُهُم بِٱلْبَيِّنَـٰتِ فَقَالُوٓا۟ أَبَشَرٌ يَهْدُونَنَا فَكَفَرُوا۟ وَتَوَلَّوا۟ ۚ وَّٱسْتَغْنَى ٱللَّهُ ۚ وَٱللَّهُ غَنِىٌّ حَمِيدٌ');
INSERT INTO "quran_text" VALUES(5206,64,7,'زَعَمَ ٱلَّذِينَ كَفَرُوٓا۟ أَن لَّن يُبْعَثُوا۟ ۚ قُلْ بَلَىٰ وَرَبِّى لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلْتُمْ ۚ وَذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٌ');
INSERT INTO "quran_text" VALUES(5207,64,8,'فَـَٔامِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَٱلنُّورِ ٱلَّذِىٓ أَنزَلْنَا ۚ وَٱللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ');
INSERT INTO "quran_text" VALUES(5208,64,9,'يَوْمَ يَجْمَعُكُمْ لِيَوْمِ ٱلْجَمْعِ ۖ ذَٰلِكَ يَوْمُ ٱلتَّغَابُنِ ۗ وَمَن يُؤْمِنۢ بِٱللَّهِ وَيَعْمَلْ صَـٰلِحًا يُكَفِّرْ عَنْهُ سَيِّـَٔاتِهِۦ وَيُدْخِلْهُ جَنَّـٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ خَـٰلِدِينَ فِيهَآ أَبَدًا ۚ ذَٰلِكَ ٱلْفَوْزُ ٱلْعَظِيمُ');
INSERT INTO "quran_text" VALUES(5209,64,10,'وَٱلَّذِينَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔايَـٰتِنَآ أُو۟لَـٰٓئِكَ أَصْحَـٰبُ ٱلنَّارِ خَـٰلِدِينَ فِيهَا ۖ وَبِئْسَ ٱلْمَصِيرُ');
INSERT INTO "quran_text" VALUES(5210,64,11,'مَآ أَصَابَ مِن مُّصِيبَةٍ إِلَّا بِإِذْنِ ٱللَّهِ ۗ وَمَن يُؤْمِنۢ بِٱللَّهِ يَهْدِ قَلْبَهُۥ ۚ وَٱللَّهُ بِكُلِّ شَىْءٍ عَلِيمٌ');
INSERT INTO "quran_text" VALUES(5211,64,12,'وَأَطِيعُوا۟ ٱللَّهَ وَأَطِيعُوا۟ ٱلرَّسُولَ ۚ فَإِن تَوَلَّيْتُمْ فَإِنَّمَا عَلَىٰ رَسُولِنَا ٱلْبَلَـٰغُ ٱلْمُبِينُ');
INSERT INTO "quran_text" VALUES(5212,64,13,'ٱللَّهُ لَآ إِلَـٰهَ إِلَّا هُوَ ۚ وَعَلَى ٱللَّهِ فَلْيَتَوَكَّلِ ٱلْمُؤْمِنُونَ');
INSERT INTO "quran_text" VALUES(5213,64,14,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِنَّ مِنْ أَزْوَٰجِكُمْ وَأَوْلَـٰدِكُمْ عَدُوًّا لَّكُمْ فَٱحْذَرُوهُمْ ۚ وَإِن تَعْفُوا۟ وَتَصْفَحُوا۟ وَتَغْفِرُوا۟ فَإِنَّ ٱللَّهَ غَفُورٌ رَّحِيمٌ');
INSERT INTO "quran_text" VALUES(5214,64,15,'إِنَّمَآ أَمْوَٰلُكُمْ وَأَوْلَـٰدُكُمْ فِتْنَةٌ ۚ وَٱللَّهُ عِندَهُۥٓ أَجْرٌ عَظِيمٌ');
INSERT INTO "quran_text" VALUES(5215,64,16,'فَٱتَّقُوا۟ ٱللَّهَ مَا ٱسْتَطَعْتُمْ وَٱسْمَعُوا۟ وَأَطِيعُوا۟ وَأَنفِقُوا۟ خَيْرًا لِّأَنفُسِكُمْ ۗ وَمَن يُوقَ شُحَّ نَفْسِهِۦ فَأُو۟لَـٰٓئِكَ هُمُ ٱلْمُفْلِحُونَ');
INSERT INTO "quran_text" VALUES(5216,64,17,'إِن تُقْرِضُوا۟ ٱللَّهَ قَرْضًا حَسَنًا يُضَـٰعِفْهُ لَكُمْ وَيَغْفِرْ لَكُمْ ۚ وَٱللَّهُ شَكُورٌ حَلِيمٌ');
INSERT INTO "quran_text" VALUES(5217,64,18,'عَـٰلِمُ ٱلْغَيْبِ وَٱلشَّهَـٰدَةِ ٱلْعَزِيزُ ٱلْحَكِيمُ');
INSERT INTO "quran_text" VALUES(5218,65,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ يَـٰٓأَيُّهَا ٱلنَّبِىُّ إِذَا طَلَّقْتُمُ ٱلنِّسَآءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَأَحْصُوا۟ ٱلْعِدَّةَ ۖ وَٱتَّقُوا۟ ٱللَّهَ رَبَّكُمْ ۖ لَا تُخْرِجُوهُنَّ مِنۢ بُيُوتِهِنَّ وَلَا يَخْرُجْنَ إِلَّآ أَن يَأْتِينَ بِفَـٰحِشَةٍ مُّبَيِّنَةٍ ۚ وَتِلْكَ حُدُودُ ٱللَّهِ ۚ وَمَن يَتَعَدَّ حُدُودَ ٱللَّهِ فَقَدْ ظَلَمَ نَفْسَهُۥ ۚ لَا تَدْرِى لَعَلَّ ٱللَّهَ يُحْدِثُ بَعْدَ ذَٰلِكَ أَمْرًا');
INSERT INTO "quran_text" VALUES(5219,65,2,'فَإِذَا بَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ فَارِقُوهُنَّ بِمَعْرُوفٍ وَأَشْهِدُوا۟ ذَوَىْ عَدْلٍ مِّنكُمْ وَأَقِيمُوا۟ ٱلشَّهَـٰدَةَ لِلَّهِ ۚ ذَٰلِكُمْ يُوعَظُ بِهِۦ مَن كَانَ يُؤْمِنُ بِٱللَّهِ وَٱلْيَوْمِ ٱلْـَٔاخِرِ ۚ وَمَن يَتَّقِ ٱللَّهَ يَجْعَل لَّهُۥ مَخْرَجًا');
INSERT INTO "quran_text" VALUES(5220,65,3,'وَيَرْزُقْهُ مِنْ حَيْثُ لَا يَحْتَسِبُ ۚ وَمَن يَتَوَكَّلْ عَلَى ٱللَّهِ فَهُوَ حَسْبُهُۥٓ ۚ إِنَّ ٱللَّهَ بَـٰلِغُ أَمْرِهِۦ ۚ قَدْ جَعَلَ ٱللَّهُ لِكُلِّ شَىْءٍ قَدْرًا');
INSERT INTO "quran_text" VALUES(5221,65,4,'وَٱلَّـٰٓـِٔى يَئِسْنَ مِنَ ٱلْمَحِيضِ مِن نِّسَآئِكُمْ إِنِ ٱرْتَبْتُمْ فَعِدَّتُهُنَّ ثَلَـٰثَةُ أَشْهُرٍ وَٱلَّـٰٓـِٔى لَمْ يَحِضْنَ ۚ وَأُو۟لَـٰتُ ٱلْأَحْمَالِ أَجَلُهُنَّ أَن يَضَعْنَ حَمْلَهُنَّ ۚ وَمَن يَتَّقِ ٱللَّهَ يَجْعَل لَّهُۥ مِنْ أَمْرِهِۦ يُسْرًا');
INSERT INTO "quran_text" VALUES(5222,65,5,'ذَٰلِكَ أَمْرُ ٱللَّهِ أَنزَلَهُۥٓ إِلَيْكُمْ ۚ وَمَن يَتَّقِ ٱللَّهَ يُكَفِّرْ عَنْهُ سَيِّـَٔاتِهِۦ وَيُعْظِمْ لَهُۥٓ أَجْرًا');
INSERT INTO "quran_text" VALUES(5223,65,6,'أَسْكِنُوهُنَّ مِنْ حَيْثُ سَكَنتُم مِّن وُجْدِكُمْ وَلَا تُضَآرُّوهُنَّ لِتُضَيِّقُوا۟ عَلَيْهِنَّ ۚ وَإِن كُنَّ أُو۟لَـٰتِ حَمْلٍ فَأَنفِقُوا۟ عَلَيْهِنَّ حَتَّىٰ يَضَعْنَ حَمْلَهُنَّ ۚ فَإِنْ أَرْضَعْنَ لَكُمْ فَـَٔاتُوهُنَّ أُجُورَهُنَّ ۖ وَأْتَمِرُوا۟ بَيْنَكُم بِمَعْرُوفٍ ۖ وَإِن تَعَاسَرْتُمْ فَسَتُرْضِعُ لَهُۥٓ أُخْرَىٰ');
INSERT INTO "quran_text" VALUES(5224,65,7,'لِيُنفِقْ ذُو سَعَةٍ مِّن سَعَتِهِۦ ۖ وَمَن قُدِرَ عَلَيْهِ رِزْقُهُۥ فَلْيُنفِقْ مِمَّآ ءَاتَىٰهُ ٱللَّهُ ۚ لَا يُكَلِّفُ ٱللَّهُ نَفْسًا إِلَّا مَآ ءَاتَىٰهَا ۚ سَيَجْعَلُ ٱللَّهُ بَعْدَ عُسْرٍ يُسْرًا');
INSERT INTO "quran_text" VALUES(5225,65,8,'وَكَأَيِّن مِّن قَرْيَةٍ عَتَتْ عَنْ أَمْرِ رَبِّهَا وَرُسُلِهِۦ فَحَاسَبْنَـٰهَا حِسَابًا شَدِيدًا وَعَذَّبْنَـٰهَا عَذَابًا نُّكْرًا');
INSERT INTO "quran_text" VALUES(5226,65,9,'فَذَاقَتْ وَبَالَ أَمْرِهَا وَكَانَ عَـٰقِبَةُ أَمْرِهَا خُسْرًا');
INSERT INTO "quran_text" VALUES(5227,65,10,'أَعَدَّ ٱللَّهُ لَهُمْ عَذَابًا شَدِيدًا ۖ فَٱتَّقُوا۟ ٱللَّهَ يَـٰٓأُو۟لِى ٱلْأَلْبَـٰبِ ٱلَّذِينَ ءَامَنُوا۟ ۚ قَدْ أَنزَلَ ٱللَّهُ إِلَيْكُمْ ذِكْرًا');
INSERT INTO "quran_text" VALUES(5228,65,11,'رَّسُولًا يَتْلُوا۟ عَلَيْكُمْ ءَايَـٰتِ ٱللَّهِ مُبَيِّنَـٰتٍ لِّيُخْرِجَ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ مِنَ ٱلظُّلُمَـٰتِ إِلَى ٱلنُّورِ ۚ وَمَن يُؤْمِنۢ بِٱللَّهِ وَيَعْمَلْ صَـٰلِحًا يُدْخِلْهُ جَنَّـٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ خَـٰلِدِينَ فِيهَآ أَبَدًا ۖ قَدْ أَحْسَنَ ٱللَّهُ لَهُۥ رِزْقًا');
INSERT INTO "quran_text" VALUES(5229,65,12,'ٱللَّهُ ٱلَّذِى خَلَقَ سَبْعَ سَمَـٰوَٰتٍ وَمِنَ ٱلْأَرْضِ مِثْلَهُنَّ يَتَنَزَّلُ ٱلْأَمْرُ بَيْنَهُنَّ لِتَعْلَمُوٓا۟ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ وَأَنَّ ٱللَّهَ قَدْ أَحَاطَ بِكُلِّ شَىْءٍ عِلْمًۢا');
INSERT INTO "quran_text" VALUES(5230,66,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ يَـٰٓأَيُّهَا ٱلنَّبِىُّ لِمَ تُحَرِّمُ مَآ أَحَلَّ ٱللَّهُ لَكَ ۖ تَبْتَغِى مَرْضَاتَ أَزْوَٰجِكَ ۚ وَٱللَّهُ غَفُورٌ رَّحِيمٌ');
INSERT INTO "quran_text" VALUES(5231,66,2,'قَدْ فَرَضَ ٱللَّهُ لَكُمْ تَحِلَّةَ أَيْمَـٰنِكُمْ ۚ وَٱللَّهُ مَوْلَىٰكُمْ ۖ وَهُوَ ٱلْعَلِيمُ ٱلْحَكِيمُ');
INSERT INTO "quran_text" VALUES(5232,66,3,'وَإِذْ أَسَرَّ ٱلنَّبِىُّ إِلَىٰ بَعْضِ أَزْوَٰجِهِۦ حَدِيثًا فَلَمَّا نَبَّأَتْ بِهِۦ وَأَظْهَرَهُ ٱللَّهُ عَلَيْهِ عَرَّفَ بَعْضَهُۥ وَأَعْرَضَ عَنۢ بَعْضٍ ۖ فَلَمَّا نَبَّأَهَا بِهِۦ قَالَتْ مَنْ أَنۢبَأَكَ هَـٰذَا ۖ قَالَ نَبَّأَنِىَ ٱلْعَلِيمُ ٱلْخَبِيرُ');
INSERT INTO "quran_text" VALUES(5233,66,4,'إِن تَتُوبَآ إِلَى ٱللَّهِ فَقَدْ صَغَتْ قُلُوبُكُمَا ۖ وَإِن تَظَـٰهَرَا عَلَيْهِ فَإِنَّ ٱللَّهَ هُوَ مَوْلَىٰهُ وَجِبْرِيلُ وَصَـٰلِحُ ٱلْمُؤْمِنِينَ ۖ وَٱلْمَلَـٰٓئِكَةُ بَعْدَ ذَٰلِكَ ظَهِيرٌ');
INSERT INTO "quran_text" VALUES(5234,66,5,'عَسَىٰ رَبُّهُۥٓ إِن طَلَّقَكُنَّ أَن يُبْدِلَهُۥٓ أَزْوَٰجًا خَيْرًا مِّنكُنَّ مُسْلِمَـٰتٍ مُّؤْمِنَـٰتٍ قَـٰنِتَـٰتٍ تَـٰٓئِبَـٰتٍ عَـٰبِدَٰتٍ سَـٰٓئِحَـٰتٍ ثَيِّبَـٰتٍ وَأَبْكَارًا');
INSERT INTO "quran_text" VALUES(5235,66,6,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ قُوٓا۟ أَنفُسَكُمْ وَأَهْلِيكُمْ نَارًا وَقُودُهَا ٱلنَّاسُ وَٱلْحِجَارَةُ عَلَيْهَا مَلَـٰٓئِكَةٌ غِلَاظٌ شِدَادٌ لَّا يَعْصُونَ ٱللَّهَ مَآ أَمَرَهُمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ');
INSERT INTO "quran_text" VALUES(5236,66,7,'يَـٰٓأَيُّهَا ٱلَّذِينَ كَفَرُوا۟ لَا تَعْتَذِرُوا۟ ٱلْيَوْمَ ۖ إِنَّمَا تُجْزَوْنَ مَا كُنتُمْ تَعْمَلُونَ');
INSERT INTO "quran_text" VALUES(5237,66,8,'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ تُوبُوٓا۟ إِلَى ٱللَّهِ تَوْبَةً نَّصُوحًا عَسَىٰ رَبُّكُمْ أَن يُكَفِّرَ عَنكُمْ سَيِّـَٔاتِكُمْ وَيُدْخِلَكُمْ جَنَّـٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ يَوْمَ لَا يُخْزِى ٱللَّهُ ٱلنَّبِىَّ وَٱلَّذِينَ ءَامَنُوا۟ مَعَهُۥ ۖ نُورُهُمْ يَسْعَىٰ بَيْنَ أَيْدِيهِمْ وَبِأَيْمَـٰنِهِمْ يَقُولُونَ رَبَّنَآ أَتْمِمْ لَنَا نُورَنَا وَٱغْفِرْ لَنَآ ۖ إِنَّكَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ');
INSERT INTO "quran_text" VALUES(5238,66,9,'يَـٰٓأَيُّهَا ٱلنَّبِىُّ جَـٰهِدِ ٱلْكُفَّارَ وَٱلْمُنَـٰفِقِينَ وَٱغْلُظْ عَلَيْهِمْ ۚ وَمَأْوَىٰهُمْ جَهَنَّمُ ۖ وَبِئْسَ ٱلْمَصِيرُ');
INSERT INTO "quran_text" VALUES(5239,66,10,'ضَرَبَ ٱللَّهُ مَثَلًا لِّلَّذِينَ كَفَرُوا۟ ٱمْرَأَتَ نُوحٍ وَٱمْرَأَتَ لُوطٍ ۖ كَانَتَا تَحْتَ عَبْدَيْنِ مِنْ عِبَادِنَا صَـٰلِحَيْنِ فَخَانَتَاهُمَا فَلَمْ يُغْنِيَا عَنْهُمَا مِنَ ٱللَّهِ شَيْـًٔا وَقِيلَ ٱدْخُلَا ٱلنَّارَ مَعَ ٱلدَّٰخِلِينَ');
INSERT INTO "quran_text" VALUES(5240,66,11,'وَضَرَبَ ٱللَّهُ مَثَلًا لِّلَّذِينَ ءَامَنُوا۟ ٱمْرَأَتَ فِرْعَوْنَ إِذْ قَالَتْ رَبِّ ٱبْنِ لِى عِندَكَ بَيْتًا فِى ٱلْجَنَّةِ وَنَجِّنِى مِن فِرْعَوْنَ وَعَمَلِهِۦ وَنَجِّنِى مِنَ ٱلْقَوْمِ ٱلظَّـٰلِمِينَ');
INSERT INTO "quran_text" VALUES(5241,66,12,'وَمَرْيَمَ ٱبْنَتَ عِمْرَٰنَ ٱلَّتِىٓ أَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيهِ مِن رُّوحِنَا وَصَدَّقَتْ بِكَلِمَـٰتِ رَبِّهَا وَكُتُبِهِۦ وَكَانَتْ مِنَ ٱلْقَـٰنِتِينَ');
INSERT INTO "quran_text" VALUES(5242,67,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ تَبَـٰرَكَ ٱلَّذِى بِيَدِهِ ٱلْمُلْكُ وَهُوَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ');
INSERT INTO "quran_text" VALUES(5243,67,2,'ٱلَّذِى خَلَقَ ٱلْمَوْتَ وَٱلْحَيَوٰةَ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلًا ۚ وَهُوَ ٱلْعَزِيزُ ٱلْغَفُورُ');
INSERT INTO "quran_text" VALUES(5244,67,3,'ٱلَّذِى خَلَقَ سَبْعَ سَمَـٰوَٰتٍ طِبَاقًا ۖ مَّا تَرَىٰ فِى خَلْقِ ٱلرَّحْمَـٰنِ مِن تَفَـٰوُتٍ ۖ فَٱرْجِعِ ٱلْبَصَرَ هَلْ تَرَىٰ مِن فُطُورٍ');
INSERT INTO "quran_text" VALUES(5245,67,4,'ثُمَّ ٱرْجِعِ ٱلْبَصَرَ كَرَّتَيْنِ يَنقَلِبْ إِلَيْكَ ٱلْبَصَرُ خَاسِئًا وَهُوَ حَسِيرٌ');
INSERT INTO "quran_text" VALUES(5246,67,5,'وَلَقَدْ زَيَّنَّا ٱلسَّمَآءَ ٱلدُّنْيَا بِمَصَـٰبِيحَ وَجَعَلْنَـٰهَا رُجُومًا لِّلشَّيَـٰطِينِ ۖ وَأَعْتَدْنَا لَهُمْ عَذَابَ ٱلسَّعِيرِ');
INSERT INTO "quran_text" VALUES(5247,67,6,'وَلِلَّذِينَ كَفَرُوا۟ بِرَبِّهِمْ عَذَابُ جَهَنَّمَ ۖ وَبِئْسَ ٱلْمَصِيرُ');
INSERT INTO "quran_text" VALUES(5248,67,7,'إِذَآ أُلْقُوا۟ فِيهَا سَمِعُوا۟ لَهَا شَهِيقًا وَهِىَ تَفُورُ');
INSERT INTO "quran_text" VALUES(5249,67,8,'تَكَادُ تَمَيَّزُ مِنَ ٱلْغَيْظِ ۖ كُلَّمَآ أُلْقِىَ فِيهَا فَوْجٌ سَأَلَهُمْ خَزَنَتُهَآ أَلَمْ يَأْتِكُمْ نَذِيرٌ');
INSERT INTO "quran_text" VALUES(5250,67,9,'قَالُوا۟ بَلَىٰ قَدْ جَآءَنَا نَذِيرٌ فَكَذَّبْنَا وَقُلْنَا مَا نَزَّلَ ٱللَّهُ مِن شَىْءٍ إِنْ أَنتُمْ إِلَّا فِى ضَلَـٰلٍ كَبِيرٍ');
INSERT INTO "quran_text" VALUES(5251,67,10,'وَقَالُوا۟ لَوْ كُنَّا نَسْمَعُ أَوْ نَعْقِلُ مَا كُنَّا فِىٓ أَصْحَـٰبِ ٱلسَّعِيرِ');
INSERT INTO "quran_text" VALUES(5252,67,11,'فَٱعْتَرَفُوا۟ بِذَنۢبِهِمْ فَسُحْقًا لِّأَصْحَـٰبِ ٱلسَّعِيرِ');
INSERT INTO "quran_text" VALUES(5253,67,12,'إِنَّ ٱلَّذِينَ يَخْشَوْنَ رَبَّهُم بِٱلْغَيْبِ لَهُم مَّغْفِرَةٌ وَأَجْرٌ كَبِيرٌ');
INSERT INTO "quran_text" VALUES(5254,67,13,'وَأَسِرُّوا۟ قَوْلَكُمْ أَوِ ٱجْهَرُوا۟ بِهِۦٓ ۖ إِنَّهُۥ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ');
INSERT INTO "quran_text" VALUES(5255,67,14,'أَلَا يَعْلَمُ مَنْ خَلَقَ وَهُوَ ٱللَّطِيفُ ٱلْخَبِيرُ');
INSERT INTO "quran_text" VALUES(5256,67,15,'هُوَ ٱلَّذِى جَعَلَ لَكُمُ ٱلْأَرْضَ ذَلُولًا فَٱمْشُوا۟ فِى مَنَاكِبِهَا وَكُلُوا۟ مِن رِّزْقِهِۦ ۖ وَإِلَيْهِ ٱلنُّشُورُ');
INSERT INTO "quran_text" VALUES(5257,67,16,'ءَأَمِنتُم مَّن فِى ٱلسَّمَآءِ أَن يَخْسِفَ بِكُمُ ٱلْأَرْضَ فَإِذَا هِىَ تَمُورُ');
INSERT INTO "quran_text" VALUES(5258,67,17,'أَمْ أَمِنتُم مَّن فِى ٱلسَّمَآءِ أَن يُرْسِلَ عَلَيْكُمْ حَاصِبًا ۖ فَسَتَعْلَمُونَ كَيْفَ نَذِيرِ');
INSERT INTO "quran_text" VALUES(5259,67,18,'وَلَقَدْ كَذَّبَ ٱلَّذِينَ مِن قَبْلِهِمْ فَكَيْفَ كَانَ نَكِيرِ');
INSERT INTO "quran_text" VALUES(5260,67,19,'أَوَلَمْ يَرَوْا۟ إِلَى ٱلطَّيْرِ فَوْقَهُمْ صَـٰٓفَّـٰتٍ وَيَقْبِضْنَ ۚ مَا يُمْسِكُهُنَّ إِلَّا ٱلرَّحْمَـٰنُ ۚ إِنَّهُۥ بِكُلِّ شَىْءٍۭ بَصِيرٌ');
INSERT INTO "quran_text" VALUES(5261,67,20,'أَمَّنْ هَـٰذَا ٱلَّذِى هُوَ جُندٌ لَّكُمْ يَنصُرُكُم مِّن دُونِ ٱلرَّحْمَـٰنِ ۚ إِنِ ٱلْكَـٰفِرُونَ إِلَّا فِى غُرُورٍ');
INSERT INTO "quran_text" VALUES(5262,67,21,'أَمَّنْ هَـٰذَا ٱلَّذِى يَرْزُقُكُمْ إِنْ أَمْسَكَ رِزْقَهُۥ ۚ بَل لَّجُّوا۟ فِى عُتُوٍّ وَنُفُورٍ');
INSERT INTO "quran_text" VALUES(5263,67,22,'أَفَمَن يَمْشِى مُكِبًّا عَلَىٰ وَجْهِهِۦٓ أَهْدَىٰٓ أَمَّن يَمْشِى سَوِيًّا عَلَىٰ صِرَٰطٍ مُّسْتَقِيمٍ');
INSERT INTO "quran_text" VALUES(5264,67,23,'قُلْ هُوَ ٱلَّذِىٓ أَنشَأَكُمْ وَجَعَلَ لَكُمُ ٱلسَّمْعَ وَٱلْأَبْصَـٰرَ وَٱلْأَفْـِٔدَةَ ۖ قَلِيلًا مَّا تَشْكُرُونَ');
INSERT INTO "quran_text" VALUES(5265,67,24,'قُلْ هُوَ ٱلَّذِى ذَرَأَكُمْ فِى ٱلْأَرْضِ وَإِلَيْهِ تُحْشَرُونَ');
INSERT INTO "quran_text" VALUES(5266,67,25,'وَيَقُولُونَ مَتَىٰ هَـٰذَا ٱلْوَعْدُ إِن كُنتُمْ صَـٰدِقِينَ');
INSERT INTO "quran_text" VALUES(5267,67,26,'قُلْ إِنَّمَا ٱلْعِلْمُ عِندَ ٱللَّهِ وَإِنَّمَآ أَنَا۠ نَذِيرٌ مُّبِينٌ');
INSERT INTO "quran_text" VALUES(5268,67,27,'فَلَمَّا رَأَوْهُ زُلْفَةً سِيٓـَٔتْ وُجُوهُ ٱلَّذِينَ كَفَرُوا۟ وَقِيلَ هَـٰذَا ٱلَّذِى كُنتُم بِهِۦ تَدَّعُونَ');
INSERT INTO "quran_text" VALUES(5269,67,28,'قُلْ أَرَءَيْتُمْ إِنْ أَهْلَكَنِىَ ٱللَّهُ وَمَن مَّعِىَ أَوْ رَحِمَنَا فَمَن يُجِيرُ ٱلْكَـٰفِرِينَ مِنْ عَذَابٍ أَلِيمٍ');
INSERT INTO "quran_text" VALUES(5270,67,29,'قُلْ هُوَ ٱلرَّحْمَـٰنُ ءَامَنَّا بِهِۦ وَعَلَيْهِ تَوَكَّلْنَا ۖ فَسَتَعْلَمُونَ مَنْ هُوَ فِى ضَلَـٰلٍ مُّبِينٍ');
INSERT INTO "quran_text" VALUES(5271,67,30,'قُلْ أَرَءَيْتُمْ إِنْ أَصْبَحَ مَآؤُكُمْ غَوْرًا فَمَن يَأْتِيكُم بِمَآءٍ مَّعِينٍۭ');
INSERT INTO "quran_text" VALUES(5272,68,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ نٓ ۚ وَٱلْقَلَمِ وَمَا يَسْطُرُونَ');
INSERT INTO "quran_text" VALUES(5273,68,2,'مَآ أَنتَ بِنِعْمَةِ رَبِّكَ بِمَجْنُونٍ');
INSERT INTO "quran_text" VALUES(5274,68,3,'وَإِنَّ لَكَ لَأَجْرًا غَيْرَ مَمْنُونٍ');
INSERT INTO "quran_text" VALUES(5275,68,4,'وَإِنَّكَ لَعَلَىٰ خُلُقٍ عَظِيمٍ');
INSERT INTO "quran_text" VALUES(5276,68,5,'فَسَتُبْصِرُ وَيُبْصِرُونَ');
INSERT INTO "quran_text" VALUES(5277,68,6,'بِأَييِّكُمُ ٱلْمَفْتُونُ');
INSERT INTO "quran_text" VALUES(5278,68,7,'إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعْلَمُ بِٱلْمُهْتَدِينَ');
INSERT INTO "quran_text" VALUES(5279,68,8,'فَلَا تُطِعِ ٱلْمُكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5280,68,9,'وَدُّوا۟ لَوْ تُدْهِنُ فَيُدْهِنُونَ');
INSERT INTO "quran_text" VALUES(5281,68,10,'وَلَا تُطِعْ كُلَّ حَلَّافٍ مَّهِينٍ');
INSERT INTO "quran_text" VALUES(5282,68,11,'هَمَّازٍ مَّشَّآءٍۭ بِنَمِيمٍ');
INSERT INTO "quran_text" VALUES(5283,68,12,'مَّنَّاعٍ لِّلْخَيْرِ مُعْتَدٍ أَثِيمٍ');
INSERT INTO "quran_text" VALUES(5284,68,13,'عُتُلٍّۭ بَعْدَ ذَٰلِكَ زَنِيمٍ');
INSERT INTO "quran_text" VALUES(5285,68,14,'أَن كَانَ ذَا مَالٍ وَبَنِينَ');
INSERT INTO "quran_text" VALUES(5286,68,15,'إِذَا تُتْلَىٰ عَلَيْهِ ءَايَـٰتُنَا قَالَ أَسَـٰطِيرُ ٱلْأَوَّلِينَ');
INSERT INTO "quran_text" VALUES(5287,68,16,'سَنَسِمُهُۥ عَلَى ٱلْخُرْطُومِ');
INSERT INTO "quran_text" VALUES(5288,68,17,'إِنَّا بَلَوْنَـٰهُمْ كَمَا بَلَوْنَآ أَصْحَـٰبَ ٱلْجَنَّةِ إِذْ أَقْسَمُوا۟ لَيَصْرِمُنَّهَا مُصْبِحِينَ');
INSERT INTO "quran_text" VALUES(5289,68,18,'وَلَا يَسْتَثْنُونَ');
INSERT INTO "quran_text" VALUES(5290,68,19,'فَطَافَ عَلَيْهَا طَآئِفٌ مِّن رَّبِّكَ وَهُمْ نَآئِمُونَ');
INSERT INTO "quran_text" VALUES(5291,68,20,'فَأَصْبَحَتْ كَٱلصَّرِيمِ');
INSERT INTO "quran_text" VALUES(5292,68,21,'فَتَنَادَوْا۟ مُصْبِحِينَ');
INSERT INTO "quran_text" VALUES(5293,68,22,'أَنِ ٱغْدُوا۟ عَلَىٰ حَرْثِكُمْ إِن كُنتُمْ صَـٰرِمِينَ');
INSERT INTO "quran_text" VALUES(5294,68,23,'فَٱنطَلَقُوا۟ وَهُمْ يَتَخَـٰفَتُونَ');
INSERT INTO "quran_text" VALUES(5295,68,24,'أَن لَّا يَدْخُلَنَّهَا ٱلْيَوْمَ عَلَيْكُم مِّسْكِينٌ');
INSERT INTO "quran_text" VALUES(5296,68,25,'وَغَدَوْا۟ عَلَىٰ حَرْدٍ قَـٰدِرِينَ');
INSERT INTO "quran_text" VALUES(5297,68,26,'فَلَمَّا رَأَوْهَا قَالُوٓا۟ إِنَّا لَضَآلُّونَ');
INSERT INTO "quran_text" VALUES(5298,68,27,'بَلْ نَحْنُ مَحْرُومُونَ');
INSERT INTO "quran_text" VALUES(5299,68,28,'قَالَ أَوْسَطُهُمْ أَلَمْ أَقُل لَّكُمْ لَوْلَا تُسَبِّحُونَ');
INSERT INTO "quran_text" VALUES(5300,68,29,'قَالُوا۟ سُبْحَـٰنَ رَبِّنَآ إِنَّا كُنَّا ظَـٰلِمِينَ');
INSERT INTO "quran_text" VALUES(5301,68,30,'فَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَلَـٰوَمُونَ');
INSERT INTO "quran_text" VALUES(5302,68,31,'قَالُوا۟ يَـٰوَيْلَنَآ إِنَّا كُنَّا طَـٰغِينَ');
INSERT INTO "quran_text" VALUES(5303,68,32,'عَسَىٰ رَبُّنَآ أَن يُبْدِلَنَا خَيْرًا مِّنْهَآ إِنَّآ إِلَىٰ رَبِّنَا رَٰغِبُونَ');
INSERT INTO "quran_text" VALUES(5304,68,33,'كَذَٰلِكَ ٱلْعَذَابُ ۖ وَلَعَذَابُ ٱلْـَٔاخِرَةِ أَكْبَرُ ۚ لَوْ كَانُوا۟ يَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(5305,68,34,'إِنَّ لِلْمُتَّقِينَ عِندَ رَبِّهِمْ جَنَّـٰتِ ٱلنَّعِيمِ');
INSERT INTO "quran_text" VALUES(5306,68,35,'أَفَنَجْعَلُ ٱلْمُسْلِمِينَ كَٱلْمُجْرِمِينَ');
INSERT INTO "quran_text" VALUES(5307,68,36,'مَا لَكُمْ كَيْفَ تَحْكُمُونَ');
INSERT INTO "quran_text" VALUES(5308,68,37,'أَمْ لَكُمْ كِتَـٰبٌ فِيهِ تَدْرُسُونَ');
INSERT INTO "quran_text" VALUES(5309,68,38,'إِنَّ لَكُمْ فِيهِ لَمَا تَخَيَّرُونَ');
INSERT INTO "quran_text" VALUES(5310,68,39,'أَمْ لَكُمْ أَيْمَـٰنٌ عَلَيْنَا بَـٰلِغَةٌ إِلَىٰ يَوْمِ ٱلْقِيَـٰمَةِ ۙ إِنَّ لَكُمْ لَمَا تَحْكُمُونَ');
INSERT INTO "quran_text" VALUES(5311,68,40,'سَلْهُمْ أَيُّهُم بِذَٰلِكَ زَعِيمٌ');
INSERT INTO "quran_text" VALUES(5312,68,41,'أَمْ لَهُمْ شُرَكَآءُ فَلْيَأْتُوا۟ بِشُرَكَآئِهِمْ إِن كَانُوا۟ صَـٰدِقِينَ');
INSERT INTO "quran_text" VALUES(5313,68,42,'يَوْمَ يُكْشَفُ عَن سَاقٍ وَيُدْعَوْنَ إِلَى ٱلسُّجُودِ فَلَا يَسْتَطِيعُونَ');
INSERT INTO "quran_text" VALUES(5314,68,43,'خَـٰشِعَةً أَبْصَـٰرُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ۖ وَقَدْ كَانُوا۟ يُدْعَوْنَ إِلَى ٱلسُّجُودِ وَهُمْ سَـٰلِمُونَ');
INSERT INTO "quran_text" VALUES(5315,68,44,'فَذَرْنِى وَمَن يُكَذِّبُ بِهَـٰذَا ٱلْحَدِيثِ ۖ سَنَسْتَدْرِجُهُم مِّنْ حَيْثُ لَا يَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(5316,68,45,'وَأُمْلِى لَهُمْ ۚ إِنَّ كَيْدِى مَتِينٌ');
INSERT INTO "quran_text" VALUES(5317,68,46,'أَمْ تَسْـَٔلُهُمْ أَجْرًا فَهُم مِّن مَّغْرَمٍ مُّثْقَلُونَ');
INSERT INTO "quran_text" VALUES(5318,68,47,'أَمْ عِندَهُمُ ٱلْغَيْبُ فَهُمْ يَكْتُبُونَ');
INSERT INTO "quran_text" VALUES(5319,68,48,'فَٱصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تَكُن كَصَاحِبِ ٱلْحُوتِ إِذْ نَادَىٰ وَهُوَ مَكْظُومٌ');
INSERT INTO "quran_text" VALUES(5320,68,49,'لَّوْلَآ أَن تَدَٰرَكَهُۥ نِعْمَةٌ مِّن رَّبِّهِۦ لَنُبِذَ بِٱلْعَرَآءِ وَهُوَ مَذْمُومٌ');
INSERT INTO "quran_text" VALUES(5321,68,50,'فَٱجْتَبَـٰهُ رَبُّهُۥ فَجَعَلَهُۥ مِنَ ٱلصَّـٰلِحِينَ');
INSERT INTO "quran_text" VALUES(5322,68,51,'وَإِن يَكَادُ ٱلَّذِينَ كَفَرُوا۟ لَيُزْلِقُونَكَ بِأَبْصَـٰرِهِمْ لَمَّا سَمِعُوا۟ ٱلذِّكْرَ وَيَقُولُونَ إِنَّهُۥ لَمَجْنُونٌ');
INSERT INTO "quran_text" VALUES(5323,68,52,'وَمَا هُوَ إِلَّا ذِكْرٌ لِّلْعَـٰلَمِينَ');
INSERT INTO "quran_text" VALUES(5324,69,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ ٱلْحَآقَّةُ');
INSERT INTO "quran_text" VALUES(5325,69,2,'مَا ٱلْحَآقَّةُ');
INSERT INTO "quran_text" VALUES(5326,69,3,'وَمَآ أَدْرَىٰكَ مَا ٱلْحَآقَّةُ');
INSERT INTO "quran_text" VALUES(5327,69,4,'كَذَّبَتْ ثَمُودُ وَعَادٌۢ بِٱلْقَارِعَةِ');
INSERT INTO "quran_text" VALUES(5328,69,5,'فَأَمَّا ثَمُودُ فَأُهْلِكُوا۟ بِٱلطَّاغِيَةِ');
INSERT INTO "quran_text" VALUES(5329,69,6,'وَأَمَّا عَادٌ فَأُهْلِكُوا۟ بِرِيحٍ صَرْصَرٍ عَاتِيَةٍ');
INSERT INTO "quran_text" VALUES(5330,69,7,'سَخَّرَهَا عَلَيْهِمْ سَبْعَ لَيَالٍ وَثَمَـٰنِيَةَ أَيَّامٍ حُسُومًا فَتَرَى ٱلْقَوْمَ فِيهَا صَرْعَىٰ كَأَنَّهُمْ أَعْجَازُ نَخْلٍ خَاوِيَةٍ');
INSERT INTO "quran_text" VALUES(5331,69,8,'فَهَلْ تَرَىٰ لَهُم مِّنۢ بَاقِيَةٍ');
INSERT INTO "quran_text" VALUES(5332,69,9,'وَجَآءَ فِرْعَوْنُ وَمَن قَبْلَهُۥ وَٱلْمُؤْتَفِكَـٰتُ بِٱلْخَاطِئَةِ');
INSERT INTO "quran_text" VALUES(5333,69,10,'فَعَصَوْا۟ رَسُولَ رَبِّهِمْ فَأَخَذَهُمْ أَخْذَةً رَّابِيَةً');
INSERT INTO "quran_text" VALUES(5334,69,11,'إِنَّا لَمَّا طَغَا ٱلْمَآءُ حَمَلْنَـٰكُمْ فِى ٱلْجَارِيَةِ');
INSERT INTO "quran_text" VALUES(5335,69,12,'لِنَجْعَلَهَا لَكُمْ تَذْكِرَةً وَتَعِيَهَآ أُذُنٌ وَٰعِيَةٌ');
INSERT INTO "quran_text" VALUES(5336,69,13,'فَإِذَا نُفِخَ فِى ٱلصُّورِ نَفْخَةٌ وَٰحِدَةٌ');
INSERT INTO "quran_text" VALUES(5337,69,14,'وَحُمِلَتِ ٱلْأَرْضُ وَٱلْجِبَالُ فَدُكَّتَا دَكَّةً وَٰحِدَةً');
INSERT INTO "quran_text" VALUES(5338,69,15,'فَيَوْمَئِذٍ وَقَعَتِ ٱلْوَاقِعَةُ');
INSERT INTO "quran_text" VALUES(5339,69,16,'وَٱنشَقَّتِ ٱلسَّمَآءُ فَهِىَ يَوْمَئِذٍ وَاهِيَةٌ');
INSERT INTO "quran_text" VALUES(5340,69,17,'وَٱلْمَلَكُ عَلَىٰٓ أَرْجَآئِهَا ۚ وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَئِذٍ ثَمَـٰنِيَةٌ');
INSERT INTO "quran_text" VALUES(5341,69,18,'يَوْمَئِذٍ تُعْرَضُونَ لَا تَخْفَىٰ مِنكُمْ خَافِيَةٌ');
INSERT INTO "quran_text" VALUES(5342,69,19,'فَأَمَّا مَنْ أُوتِىَ كِتَـٰبَهُۥ بِيَمِينِهِۦ فَيَقُولُ هَآؤُمُ ٱقْرَءُوا۟ كِتَـٰبِيَهْ');
INSERT INTO "quran_text" VALUES(5343,69,20,'إِنِّى ظَنَنتُ أَنِّى مُلَـٰقٍ حِسَابِيَهْ');
INSERT INTO "quran_text" VALUES(5344,69,21,'فَهُوَ فِى عِيشَةٍ رَّاضِيَةٍ');
INSERT INTO "quran_text" VALUES(5345,69,22,'فِى جَنَّةٍ عَالِيَةٍ');
INSERT INTO "quran_text" VALUES(5346,69,23,'قُطُوفُهَا دَانِيَةٌ');
INSERT INTO "quran_text" VALUES(5347,69,24,'كُلُوا۟ وَٱشْرَبُوا۟ هَنِيٓـًٔۢا بِمَآ أَسْلَفْتُمْ فِى ٱلْأَيَّامِ ٱلْخَالِيَةِ');
INSERT INTO "quran_text" VALUES(5348,69,25,'وَأَمَّا مَنْ أُوتِىَ كِتَـٰبَهُۥ بِشِمَالِهِۦ فَيَقُولُ يَـٰلَيْتَنِى لَمْ أُوتَ كِتَـٰبِيَهْ');
INSERT INTO "quran_text" VALUES(5349,69,26,'وَلَمْ أَدْرِ مَا حِسَابِيَهْ');
INSERT INTO "quran_text" VALUES(5350,69,27,'يَـٰلَيْتَهَا كَانَتِ ٱلْقَاضِيَةَ');
INSERT INTO "quran_text" VALUES(5351,69,28,'مَآ أَغْنَىٰ عَنِّى مَالِيَهْ ۜ');
INSERT INTO "quran_text" VALUES(5352,69,29,'هَلَكَ عَنِّى سُلْطَـٰنِيَهْ');
INSERT INTO "quran_text" VALUES(5353,69,30,'خُذُوهُ فَغُلُّوهُ');
INSERT INTO "quran_text" VALUES(5354,69,31,'ثُمَّ ٱلْجَحِيمَ صَلُّوهُ');
INSERT INTO "quran_text" VALUES(5355,69,32,'ثُمَّ فِى سِلْسِلَةٍ ذَرْعُهَا سَبْعُونَ ذِرَاعًا فَٱسْلُكُوهُ');
INSERT INTO "quran_text" VALUES(5356,69,33,'إِنَّهُۥ كَانَ لَا يُؤْمِنُ بِٱللَّهِ ٱلْعَظِيمِ');
INSERT INTO "quran_text" VALUES(5357,69,34,'وَلَا يَحُضُّ عَلَىٰ طَعَامِ ٱلْمِسْكِينِ');
INSERT INTO "quran_text" VALUES(5358,69,35,'فَلَيْسَ لَهُ ٱلْيَوْمَ هَـٰهُنَا حَمِيمٌ');
INSERT INTO "quran_text" VALUES(5359,69,36,'وَلَا طَعَامٌ إِلَّا مِنْ غِسْلِينٍ');
INSERT INTO "quran_text" VALUES(5360,69,37,'لَّا يَأْكُلُهُۥٓ إِلَّا ٱلْخَـٰطِـُٔونَ');
INSERT INTO "quran_text" VALUES(5361,69,38,'فَلَآ أُقْسِمُ بِمَا تُبْصِرُونَ');
INSERT INTO "quran_text" VALUES(5362,69,39,'وَمَا لَا تُبْصِرُونَ');
INSERT INTO "quran_text" VALUES(5363,69,40,'إِنَّهُۥ لَقَوْلُ رَسُولٍ كَرِيمٍ');
INSERT INTO "quran_text" VALUES(5364,69,41,'وَمَا هُوَ بِقَوْلِ شَاعِرٍ ۚ قَلِيلًا مَّا تُؤْمِنُونَ');
INSERT INTO "quran_text" VALUES(5365,69,42,'وَلَا بِقَوْلِ كَاهِنٍ ۚ قَلِيلًا مَّا تَذَكَّرُونَ');
INSERT INTO "quran_text" VALUES(5366,69,43,'تَنزِيلٌ مِّن رَّبِّ ٱلْعَـٰلَمِينَ');
INSERT INTO "quran_text" VALUES(5367,69,44,'وَلَوْ تَقَوَّلَ عَلَيْنَا بَعْضَ ٱلْأَقَاوِيلِ');
INSERT INTO "quran_text" VALUES(5368,69,45,'لَأَخَذْنَا مِنْهُ بِٱلْيَمِينِ');
INSERT INTO "quran_text" VALUES(5369,69,46,'ثُمَّ لَقَطَعْنَا مِنْهُ ٱلْوَتِينَ');
INSERT INTO "quran_text" VALUES(5370,69,47,'فَمَا مِنكُم مِّنْ أَحَدٍ عَنْهُ حَـٰجِزِينَ');
INSERT INTO "quran_text" VALUES(5371,69,48,'وَإِنَّهُۥ لَتَذْكِرَةٌ لِّلْمُتَّقِينَ');
INSERT INTO "quran_text" VALUES(5372,69,49,'وَإِنَّا لَنَعْلَمُ أَنَّ مِنكُم مُّكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5373,69,50,'وَإِنَّهُۥ لَحَسْرَةٌ عَلَى ٱلْكَـٰفِرِينَ');
INSERT INTO "quran_text" VALUES(5374,69,51,'وَإِنَّهُۥ لَحَقُّ ٱلْيَقِينِ');
INSERT INTO "quran_text" VALUES(5375,69,52,'فَسَبِّحْ بِٱسْمِ رَبِّكَ ٱلْعَظِيمِ');
INSERT INTO "quran_text" VALUES(5376,70,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ سَأَلَ سَآئِلٌۢ بِعَذَابٍ وَاقِعٍ');
INSERT INTO "quran_text" VALUES(5377,70,2,'لِّلْكَـٰفِرِينَ لَيْسَ لَهُۥ دَافِعٌ');
INSERT INTO "quran_text" VALUES(5378,70,3,'مِّنَ ٱللَّهِ ذِى ٱلْمَعَارِجِ');
INSERT INTO "quran_text" VALUES(5379,70,4,'تَعْرُجُ ٱلْمَلَـٰٓئِكَةُ وَٱلرُّوحُ إِلَيْهِ فِى يَوْمٍ كَانَ مِقْدَارُهُۥ خَمْسِينَ أَلْفَ سَنَةٍ');
INSERT INTO "quran_text" VALUES(5380,70,5,'فَٱصْبِرْ صَبْرًا جَمِيلًا');
INSERT INTO "quran_text" VALUES(5381,70,6,'إِنَّهُمْ يَرَوْنَهُۥ بَعِيدًا');
INSERT INTO "quran_text" VALUES(5382,70,7,'وَنَرَىٰهُ قَرِيبًا');
INSERT INTO "quran_text" VALUES(5383,70,8,'يَوْمَ تَكُونُ ٱلسَّمَآءُ كَٱلْمُهْلِ');
INSERT INTO "quran_text" VALUES(5384,70,9,'وَتَكُونُ ٱلْجِبَالُ كَٱلْعِهْنِ');
INSERT INTO "quran_text" VALUES(5385,70,10,'وَلَا يَسْـَٔلُ حَمِيمٌ حَمِيمًا');
INSERT INTO "quran_text" VALUES(5386,70,11,'يُبَصَّرُونَهُمْ ۚ يَوَدُّ ٱلْمُجْرِمُ لَوْ يَفْتَدِى مِنْ عَذَابِ يَوْمِئِذٍۭ بِبَنِيهِ');
INSERT INTO "quran_text" VALUES(5387,70,12,'وَصَـٰحِبَتِهِۦ وَأَخِيهِ');
INSERT INTO "quran_text" VALUES(5388,70,13,'وَفَصِيلَتِهِ ٱلَّتِى تُـْٔوِيهِ');
INSERT INTO "quran_text" VALUES(5389,70,14,'وَمَن فِى ٱلْأَرْضِ جَمِيعًا ثُمَّ يُنجِيهِ');
INSERT INTO "quran_text" VALUES(5390,70,15,'كَلَّآ ۖ إِنَّهَا لَظَىٰ');
INSERT INTO "quran_text" VALUES(5391,70,16,'نَزَّاعَةً لِّلشَّوَىٰ');
INSERT INTO "quran_text" VALUES(5392,70,17,'تَدْعُوا۟ مَنْ أَدْبَرَ وَتَوَلَّىٰ');
INSERT INTO "quran_text" VALUES(5393,70,18,'وَجَمَعَ فَأَوْعَىٰٓ');
INSERT INTO "quran_text" VALUES(5394,70,19,'۞ إِنَّ ٱلْإِنسَـٰنَ خُلِقَ هَلُوعًا');
INSERT INTO "quran_text" VALUES(5395,70,20,'إِذَا مَسَّهُ ٱلشَّرُّ جَزُوعًا');
INSERT INTO "quran_text" VALUES(5396,70,21,'وَإِذَا مَسَّهُ ٱلْخَيْرُ مَنُوعًا');
INSERT INTO "quran_text" VALUES(5397,70,22,'إِلَّا ٱلْمُصَلِّينَ');
INSERT INTO "quran_text" VALUES(5398,70,23,'ٱلَّذِينَ هُمْ عَلَىٰ صَلَاتِهِمْ دَآئِمُونَ');
INSERT INTO "quran_text" VALUES(5399,70,24,'وَٱلَّذِينَ فِىٓ أَمْوَٰلِهِمْ حَقٌّ مَّعْلُومٌ');
INSERT INTO "quran_text" VALUES(5400,70,25,'لِّلسَّآئِلِ وَٱلْمَحْرُومِ');
INSERT INTO "quran_text" VALUES(5401,70,26,'وَٱلَّذِينَ يُصَدِّقُونَ بِيَوْمِ ٱلدِّينِ');
INSERT INTO "quran_text" VALUES(5402,70,27,'وَٱلَّذِينَ هُم مِّنْ عَذَابِ رَبِّهِم مُّشْفِقُونَ');
INSERT INTO "quran_text" VALUES(5403,70,28,'إِنَّ عَذَابَ رَبِّهِمْ غَيْرُ مَأْمُونٍ');
INSERT INTO "quran_text" VALUES(5404,70,29,'وَٱلَّذِينَ هُمْ لِفُرُوجِهِمْ حَـٰفِظُونَ');
INSERT INTO "quran_text" VALUES(5405,70,30,'إِلَّا عَلَىٰٓ أَزْوَٰجِهِمْ أَوْ مَا مَلَكَتْ أَيْمَـٰنُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ');
INSERT INTO "quran_text" VALUES(5406,70,31,'فَمَنِ ٱبْتَغَىٰ وَرَآءَ ذَٰلِكَ فَأُو۟لَـٰٓئِكَ هُمُ ٱلْعَادُونَ');
INSERT INTO "quran_text" VALUES(5407,70,32,'وَٱلَّذِينَ هُمْ لِأَمَـٰنَـٰتِهِمْ وَعَهْدِهِمْ رَٰعُونَ');
INSERT INTO "quran_text" VALUES(5408,70,33,'وَٱلَّذِينَ هُم بِشَهَـٰدَٰتِهِمْ قَآئِمُونَ');
INSERT INTO "quran_text" VALUES(5409,70,34,'وَٱلَّذِينَ هُمْ عَلَىٰ صَلَاتِهِمْ يُحَافِظُونَ');
INSERT INTO "quran_text" VALUES(5410,70,35,'أُو۟لَـٰٓئِكَ فِى جَنَّـٰتٍ مُّكْرَمُونَ');
INSERT INTO "quran_text" VALUES(5411,70,36,'فَمَالِ ٱلَّذِينَ كَفَرُوا۟ قِبَلَكَ مُهْطِعِينَ');
INSERT INTO "quran_text" VALUES(5412,70,37,'عَنِ ٱلْيَمِينِ وَعَنِ ٱلشِّمَالِ عِزِينَ');
INSERT INTO "quran_text" VALUES(5413,70,38,'أَيَطْمَعُ كُلُّ ٱمْرِئٍ مِّنْهُمْ أَن يُدْخَلَ جَنَّةَ نَعِيمٍ');
INSERT INTO "quran_text" VALUES(5414,70,39,'كَلَّآ ۖ إِنَّا خَلَقْنَـٰهُم مِّمَّا يَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(5415,70,40,'فَلَآ أُقْسِمُ بِرَبِّ ٱلْمَشَـٰرِقِ وَٱلْمَغَـٰرِبِ إِنَّا لَقَـٰدِرُونَ');
INSERT INTO "quran_text" VALUES(5416,70,41,'عَلَىٰٓ أَن نُّبَدِّلَ خَيْرًا مِّنْهُمْ وَمَا نَحْنُ بِمَسْبُوقِينَ');
INSERT INTO "quran_text" VALUES(5417,70,42,'فَذَرْهُمْ يَخُوضُوا۟ وَيَلْعَبُوا۟ حَتَّىٰ يُلَـٰقُوا۟ يَوْمَهُمُ ٱلَّذِى يُوعَدُونَ');
INSERT INTO "quran_text" VALUES(5418,70,43,'يَوْمَ يَخْرُجُونَ مِنَ ٱلْأَجْدَاثِ سِرَاعًا كَأَنَّهُمْ إِلَىٰ نُصُبٍ يُوفِضُونَ');
INSERT INTO "quran_text" VALUES(5419,70,44,'خَـٰشِعَةً أَبْصَـٰرُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ۚ ذَٰلِكَ ٱلْيَوْمُ ٱلَّذِى كَانُوا۟ يُوعَدُونَ');
INSERT INTO "quran_text" VALUES(5420,71,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ إِنَّآ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِۦٓ أَنْ أَنذِرْ قَوْمَكَ مِن قَبْلِ أَن يَأْتِيَهُمْ عَذَابٌ أَلِيمٌ');
INSERT INTO "quran_text" VALUES(5421,71,2,'قَالَ يَـٰقَوْمِ إِنِّى لَكُمْ نَذِيرٌ مُّبِينٌ');
INSERT INTO "quran_text" VALUES(5422,71,3,'أَنِ ٱعْبُدُوا۟ ٱللَّهَ وَٱتَّقُوهُ وَأَطِيعُونِ');
INSERT INTO "quran_text" VALUES(5423,71,4,'يَغْفِرْ لَكُم مِّن ذُنُوبِكُمْ وَيُؤَخِّرْكُمْ إِلَىٰٓ أَجَلٍ مُّسَمًّى ۚ إِنَّ أَجَلَ ٱللَّهِ إِذَا جَآءَ لَا يُؤَخَّرُ ۖ لَوْ كُنتُمْ تَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(5424,71,5,'قَالَ رَبِّ إِنِّى دَعَوْتُ قَوْمِى لَيْلًا وَنَهَارًا');
INSERT INTO "quran_text" VALUES(5425,71,6,'فَلَمْ يَزِدْهُمْ دُعَآءِىٓ إِلَّا فِرَارًا');
INSERT INTO "quran_text" VALUES(5426,71,7,'وَإِنِّى كُلَّمَا دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوٓا۟ أَصَـٰبِعَهُمْ فِىٓ ءَاذَانِهِمْ وَٱسْتَغْشَوْا۟ ثِيَابَهُمْ وَأَصَرُّوا۟ وَٱسْتَكْبَرُوا۟ ٱسْتِكْبَارًا');
INSERT INTO "quran_text" VALUES(5427,71,8,'ثُمَّ إِنِّى دَعَوْتُهُمْ جِهَارًا');
INSERT INTO "quran_text" VALUES(5428,71,9,'ثُمَّ إِنِّىٓ أَعْلَنتُ لَهُمْ وَأَسْرَرْتُ لَهُمْ إِسْرَارًا');
INSERT INTO "quran_text" VALUES(5429,71,10,'فَقُلْتُ ٱسْتَغْفِرُوا۟ رَبَّكُمْ إِنَّهُۥ كَانَ غَفَّارًا');
INSERT INTO "quran_text" VALUES(5430,71,11,'يُرْسِلِ ٱلسَّمَآءَ عَلَيْكُم مِّدْرَارًا');
INSERT INTO "quran_text" VALUES(5431,71,12,'وَيُمْدِدْكُم بِأَمْوَٰلٍ وَبَنِينَ وَيَجْعَل لَّكُمْ جَنَّـٰتٍ وَيَجْعَل لَّكُمْ أَنْهَـٰرًا');
INSERT INTO "quran_text" VALUES(5432,71,13,'مَّا لَكُمْ لَا تَرْجُونَ لِلَّهِ وَقَارًا');
INSERT INTO "quran_text" VALUES(5433,71,14,'وَقَدْ خَلَقَكُمْ أَطْوَارًا');
INSERT INTO "quran_text" VALUES(5434,71,15,'أَلَمْ تَرَوْا۟ كَيْفَ خَلَقَ ٱللَّهُ سَبْعَ سَمَـٰوَٰتٍ طِبَاقًا');
INSERT INTO "quran_text" VALUES(5435,71,16,'وَجَعَلَ ٱلْقَمَرَ فِيهِنَّ نُورًا وَجَعَلَ ٱلشَّمْسَ سِرَاجًا');
INSERT INTO "quran_text" VALUES(5436,71,17,'وَٱللَّهُ أَنۢبَتَكُم مِّنَ ٱلْأَرْضِ نَبَاتًا');
INSERT INTO "quran_text" VALUES(5437,71,18,'ثُمَّ يُعِيدُكُمْ فِيهَا وَيُخْرِجُكُمْ إِخْرَاجًا');
INSERT INTO "quran_text" VALUES(5438,71,19,'وَٱللَّهُ جَعَلَ لَكُمُ ٱلْأَرْضَ بِسَاطًا');
INSERT INTO "quran_text" VALUES(5439,71,20,'لِّتَسْلُكُوا۟ مِنْهَا سُبُلًا فِجَاجًا');
INSERT INTO "quran_text" VALUES(5440,71,21,'قَالَ نُوحٌ رَّبِّ إِنَّهُمْ عَصَوْنِى وَٱتَّبَعُوا۟ مَن لَّمْ يَزِدْهُ مَالُهُۥ وَوَلَدُهُۥٓ إِلَّا خَسَارًا');
INSERT INTO "quran_text" VALUES(5441,71,22,'وَمَكَرُوا۟ مَكْرًا كُبَّارًا');
INSERT INTO "quran_text" VALUES(5442,71,23,'وَقَالُوا۟ لَا تَذَرُنَّ ءَالِهَتَكُمْ وَلَا تَذَرُنَّ وَدًّا وَلَا سُوَاعًا وَلَا يَغُوثَ وَيَعُوقَ وَنَسْرًا');
INSERT INTO "quran_text" VALUES(5443,71,24,'وَقَدْ أَضَلُّوا۟ كَثِيرًا ۖ وَلَا تَزِدِ ٱلظَّـٰلِمِينَ إِلَّا ضَلَـٰلًا');
INSERT INTO "quran_text" VALUES(5444,71,25,'مِّمَّا خَطِيٓـَٔـٰتِهِمْ أُغْرِقُوا۟ فَأُدْخِلُوا۟ نَارًا فَلَمْ يَجِدُوا۟ لَهُم مِّن دُونِ ٱللَّهِ أَنصَارًا');
INSERT INTO "quran_text" VALUES(5445,71,26,'وَقَالَ نُوحٌ رَّبِّ لَا تَذَرْ عَلَى ٱلْأَرْضِ مِنَ ٱلْكَـٰفِرِينَ دَيَّارًا');
INSERT INTO "quran_text" VALUES(5446,71,27,'إِنَّكَ إِن تَذَرْهُمْ يُضِلُّوا۟ عِبَادَكَ وَلَا يَلِدُوٓا۟ إِلَّا فَاجِرًا كَفَّارًا');
INSERT INTO "quran_text" VALUES(5447,71,28,'رَّبِّ ٱغْفِرْ لِى وَلِوَٰلِدَىَّ وَلِمَن دَخَلَ بَيْتِىَ مُؤْمِنًا وَلِلْمُؤْمِنِينَ وَٱلْمُؤْمِنَـٰتِ وَلَا تَزِدِ ٱلظَّـٰلِمِينَ إِلَّا تَبَارًۢا');
INSERT INTO "quran_text" VALUES(5448,72,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ قُلْ أُوحِىَ إِلَىَّ أَنَّهُ ٱسْتَمَعَ نَفَرٌ مِّنَ ٱلْجِنِّ فَقَالُوٓا۟ إِنَّا سَمِعْنَا قُرْءَانًا عَجَبًا');
INSERT INTO "quran_text" VALUES(5449,72,2,'يَهْدِىٓ إِلَى ٱلرُّشْدِ فَـَٔامَنَّا بِهِۦ ۖ وَلَن نُّشْرِكَ بِرَبِّنَآ أَحَدًا');
INSERT INTO "quran_text" VALUES(5450,72,3,'وَأَنَّهُۥ تَعَـٰلَىٰ جَدُّ رَبِّنَا مَا ٱتَّخَذَ صَـٰحِبَةً وَلَا وَلَدًا');
INSERT INTO "quran_text" VALUES(5451,72,4,'وَأَنَّهُۥ كَانَ يَقُولُ سَفِيهُنَا عَلَى ٱللَّهِ شَطَطًا');
INSERT INTO "quran_text" VALUES(5452,72,5,'وَأَنَّا ظَنَنَّآ أَن لَّن تَقُولَ ٱلْإِنسُ وَٱلْجِنُّ عَلَى ٱللَّهِ كَذِبًا');
INSERT INTO "quran_text" VALUES(5453,72,6,'وَأَنَّهُۥ كَانَ رِجَالٌ مِّنَ ٱلْإِنسِ يَعُوذُونَ بِرِجَالٍ مِّنَ ٱلْجِنِّ فَزَادُوهُمْ رَهَقًا');
INSERT INTO "quran_text" VALUES(5454,72,7,'وَأَنَّهُمْ ظَنُّوا۟ كَمَا ظَنَنتُمْ أَن لَّن يَبْعَثَ ٱللَّهُ أَحَدًا');
INSERT INTO "quran_text" VALUES(5455,72,8,'وَأَنَّا لَمَسْنَا ٱلسَّمَآءَ فَوَجَدْنَـٰهَا مُلِئَتْ حَرَسًا شَدِيدًا وَشُهُبًا');
INSERT INTO "quran_text" VALUES(5456,72,9,'وَأَنَّا كُنَّا نَقْعُدُ مِنْهَا مَقَـٰعِدَ لِلسَّمْعِ ۖ فَمَن يَسْتَمِعِ ٱلْـَٔانَ يَجِدْ لَهُۥ شِهَابًا رَّصَدًا');
INSERT INTO "quran_text" VALUES(5457,72,10,'وَأَنَّا لَا نَدْرِىٓ أَشَرٌّ أُرِيدَ بِمَن فِى ٱلْأَرْضِ أَمْ أَرَادَ بِهِمْ رَبُّهُمْ رَشَدًا');
INSERT INTO "quran_text" VALUES(5458,72,11,'وَأَنَّا مِنَّا ٱلصَّـٰلِحُونَ وَمِنَّا دُونَ ذَٰلِكَ ۖ كُنَّا طَرَآئِقَ قِدَدًا');
INSERT INTO "quran_text" VALUES(5459,72,12,'وَأَنَّا ظَنَنَّآ أَن لَّن نُّعْجِزَ ٱللَّهَ فِى ٱلْأَرْضِ وَلَن نُّعْجِزَهُۥ هَرَبًا');
INSERT INTO "quran_text" VALUES(5460,72,13,'وَأَنَّا لَمَّا سَمِعْنَا ٱلْهُدَىٰٓ ءَامَنَّا بِهِۦ ۖ فَمَن يُؤْمِنۢ بِرَبِّهِۦ فَلَا يَخَافُ بَخْسًا وَلَا رَهَقًا');
INSERT INTO "quran_text" VALUES(5461,72,14,'وَأَنَّا مِنَّا ٱلْمُسْلِمُونَ وَمِنَّا ٱلْقَـٰسِطُونَ ۖ فَمَنْ أَسْلَمَ فَأُو۟لَـٰٓئِكَ تَحَرَّوْا۟ رَشَدًا');
INSERT INTO "quran_text" VALUES(5462,72,15,'وَأَمَّا ٱلْقَـٰسِطُونَ فَكَانُوا۟ لِجَهَنَّمَ حَطَبًا');
INSERT INTO "quran_text" VALUES(5463,72,16,'وَأَلَّوِ ٱسْتَقَـٰمُوا۟ عَلَى ٱلطَّرِيقَةِ لَأَسْقَيْنَـٰهُم مَّآءً غَدَقًا');
INSERT INTO "quran_text" VALUES(5464,72,17,'لِّنَفْتِنَهُمْ فِيهِ ۚ وَمَن يُعْرِضْ عَن ذِكْرِ رَبِّهِۦ يَسْلُكْهُ عَذَابًا صَعَدًا');
INSERT INTO "quran_text" VALUES(5465,72,18,'وَأَنَّ ٱلْمَسَـٰجِدَ لِلَّهِ فَلَا تَدْعُوا۟ مَعَ ٱللَّهِ أَحَدًا');
INSERT INTO "quran_text" VALUES(5466,72,19,'وَأَنَّهُۥ لَمَّا قَامَ عَبْدُ ٱللَّهِ يَدْعُوهُ كَادُوا۟ يَكُونُونَ عَلَيْهِ لِبَدًا');
INSERT INTO "quran_text" VALUES(5467,72,20,'قُلْ إِنَّمَآ أَدْعُوا۟ رَبِّى وَلَآ أُشْرِكُ بِهِۦٓ أَحَدًا');
INSERT INTO "quran_text" VALUES(5468,72,21,'قُلْ إِنِّى لَآ أَمْلِكُ لَكُمْ ضَرًّا وَلَا رَشَدًا');
INSERT INTO "quran_text" VALUES(5469,72,22,'قُلْ إِنِّى لَن يُجِيرَنِى مِنَ ٱللَّهِ أَحَدٌ وَلَنْ أَجِدَ مِن دُونِهِۦ مُلْتَحَدًا');
INSERT INTO "quran_text" VALUES(5470,72,23,'إِلَّا بَلَـٰغًا مِّنَ ٱللَّهِ وَرِسَـٰلَـٰتِهِۦ ۚ وَمَن يَعْصِ ٱللَّهَ وَرَسُولَهُۥ فَإِنَّ لَهُۥ نَارَ جَهَنَّمَ خَـٰلِدِينَ فِيهَآ أَبَدًا');
INSERT INTO "quran_text" VALUES(5471,72,24,'حَتَّىٰٓ إِذَا رَأَوْا۟ مَا يُوعَدُونَ فَسَيَعْلَمُونَ مَنْ أَضْعَفُ نَاصِرًا وَأَقَلُّ عَدَدًا');
INSERT INTO "quran_text" VALUES(5472,72,25,'قُلْ إِنْ أَدْرِىٓ أَقَرِيبٌ مَّا تُوعَدُونَ أَمْ يَجْعَلُ لَهُۥ رَبِّىٓ أَمَدًا');
INSERT INTO "quran_text" VALUES(5473,72,26,'عَـٰلِمُ ٱلْغَيْبِ فَلَا يُظْهِرُ عَلَىٰ غَيْبِهِۦٓ أَحَدًا');
INSERT INTO "quran_text" VALUES(5474,72,27,'إِلَّا مَنِ ٱرْتَضَىٰ مِن رَّسُولٍ فَإِنَّهُۥ يَسْلُكُ مِنۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِۦ رَصَدًا');
INSERT INTO "quran_text" VALUES(5475,72,28,'لِّيَعْلَمَ أَن قَدْ أَبْلَغُوا۟ رِسَـٰلَـٰتِ رَبِّهِمْ وَأَحَاطَ بِمَا لَدَيْهِمْ وَأَحْصَىٰ كُلَّ شَىْءٍ عَدَدًۢا');
INSERT INTO "quran_text" VALUES(5476,73,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ يَـٰٓأَيُّهَا ٱلْمُزَّمِّلُ');
INSERT INTO "quran_text" VALUES(5477,73,2,'قُمِ ٱلَّيْلَ إِلَّا قَلِيلًا');
INSERT INTO "quran_text" VALUES(5478,73,3,'نِّصْفَهُۥٓ أَوِ ٱنقُصْ مِنْهُ قَلِيلًا');
INSERT INTO "quran_text" VALUES(5479,73,4,'أَوْ زِدْ عَلَيْهِ وَرَتِّلِ ٱلْقُرْءَانَ تَرْتِيلًا');
INSERT INTO "quran_text" VALUES(5480,73,5,'إِنَّا سَنُلْقِى عَلَيْكَ قَوْلًا ثَقِيلًا');
INSERT INTO "quran_text" VALUES(5481,73,6,'إِنَّ نَاشِئَةَ ٱلَّيْلِ هِىَ أَشَدُّ وَطْـًٔا وَأَقْوَمُ قِيلًا');
INSERT INTO "quran_text" VALUES(5482,73,7,'إِنَّ لَكَ فِى ٱلنَّهَارِ سَبْحًا طَوِيلًا');
INSERT INTO "quran_text" VALUES(5483,73,8,'وَٱذْكُرِ ٱسْمَ رَبِّكَ وَتَبَتَّلْ إِلَيْهِ تَبْتِيلًا');
INSERT INTO "quran_text" VALUES(5484,73,9,'رَّبُّ ٱلْمَشْرِقِ وَٱلْمَغْرِبِ لَآ إِلَـٰهَ إِلَّا هُوَ فَٱتَّخِذْهُ وَكِيلًا');
INSERT INTO "quran_text" VALUES(5485,73,10,'وَٱصْبِرْ عَلَىٰ مَا يَقُولُونَ وَٱهْجُرْهُمْ هَجْرًا جَمِيلًا');
INSERT INTO "quran_text" VALUES(5486,73,11,'وَذَرْنِى وَٱلْمُكَذِّبِينَ أُو۟لِى ٱلنَّعْمَةِ وَمَهِّلْهُمْ قَلِيلًا');
INSERT INTO "quran_text" VALUES(5487,73,12,'إِنَّ لَدَيْنَآ أَنكَالًا وَجَحِيمًا');
INSERT INTO "quran_text" VALUES(5488,73,13,'وَطَعَامًا ذَا غُصَّةٍ وَعَذَابًا أَلِيمًا');
INSERT INTO "quran_text" VALUES(5489,73,14,'يَوْمَ تَرْجُفُ ٱلْأَرْضُ وَٱلْجِبَالُ وَكَانَتِ ٱلْجِبَالُ كَثِيبًا مَّهِيلًا');
INSERT INTO "quran_text" VALUES(5490,73,15,'إِنَّآ أَرْسَلْنَآ إِلَيْكُمْ رَسُولًا شَـٰهِدًا عَلَيْكُمْ كَمَآ أَرْسَلْنَآ إِلَىٰ فِرْعَوْنَ رَسُولًا');
INSERT INTO "quran_text" VALUES(5491,73,16,'فَعَصَىٰ فِرْعَوْنُ ٱلرَّسُولَ فَأَخَذْنَـٰهُ أَخْذًا وَبِيلًا');
INSERT INTO "quran_text" VALUES(5492,73,17,'فَكَيْفَ تَتَّقُونَ إِن كَفَرْتُمْ يَوْمًا يَجْعَلُ ٱلْوِلْدَٰنَ شِيبًا');
INSERT INTO "quran_text" VALUES(5493,73,18,'ٱلسَّمَآءُ مُنفَطِرٌۢ بِهِۦ ۚ كَانَ وَعْدُهُۥ مَفْعُولًا');
INSERT INTO "quran_text" VALUES(5494,73,19,'إِنَّ هَـٰذِهِۦ تَذْكِرَةٌ ۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلًا');
INSERT INTO "quran_text" VALUES(5495,73,20,'۞ إِنَّ رَبَّكَ يَعْلَمُ أَنَّكَ تَقُومُ أَدْنَىٰ مِن ثُلُثَىِ ٱلَّيْلِ وَنِصْفَهُۥ وَثُلُثَهُۥ وَطَآئِفَةٌ مِّنَ ٱلَّذِينَ مَعَكَ ۚ وَٱللَّهُ يُقَدِّرُ ٱلَّيْلَ وَٱلنَّهَارَ ۚ عَلِمَ أَن لَّن تُحْصُوهُ فَتَابَ عَلَيْكُمْ ۖ فَٱقْرَءُوا۟ مَا تَيَسَّرَ مِنَ ٱلْقُرْءَانِ ۚ عَلِمَ أَن سَيَكُونُ مِنكُم مَّرْضَىٰ ۙ وَءَاخَرُونَ يَضْرِبُونَ فِى ٱلْأَرْضِ يَبْتَغُونَ مِن فَضْلِ ٱللَّهِ ۙ وَءَاخَرُونَ يُقَـٰتِلُونَ فِى سَبِيلِ ٱللَّهِ ۖ فَٱقْرَءُوا۟ مَا تَيَسَّرَ مِنْهُ ۚ وَأَقِيمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ وَأَقْرِضُوا۟ ٱللَّهَ قَرْضًا حَسَنًا ۚ وَمَا تُقَدِّمُوا۟ لِأَنفُسِكُم مِّنْ خَيْرٍ تَجِدُوهُ عِندَ ٱللَّهِ هُوَ خَيْرًا وَأَعْظَمَ أَجْرًا ۚ وَٱسْتَغْفِرُوا۟ ٱللَّهَ ۖ إِنَّ ٱللَّهَ غَفُورٌ رَّحِيمٌۢ');
INSERT INTO "quran_text" VALUES(5496,74,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ يَـٰٓأَيُّهَا ٱلْمُدَّثِّرُ');
INSERT INTO "quran_text" VALUES(5497,74,2,'قُمْ فَأَنذِرْ');
INSERT INTO "quran_text" VALUES(5498,74,3,'وَرَبَّكَ فَكَبِّرْ');
INSERT INTO "quran_text" VALUES(5499,74,4,'وَثِيَابَكَ فَطَهِّرْ');
INSERT INTO "quran_text" VALUES(5500,74,5,'وَٱلرُّجْزَ فَٱهْجُرْ');
INSERT INTO "quran_text" VALUES(5501,74,6,'وَلَا تَمْنُن تَسْتَكْثِرُ');
INSERT INTO "quran_text" VALUES(5502,74,7,'وَلِرَبِّكَ فَٱصْبِرْ');
INSERT INTO "quran_text" VALUES(5503,74,8,'فَإِذَا نُقِرَ فِى ٱلنَّاقُورِ');
INSERT INTO "quran_text" VALUES(5504,74,9,'فَذَٰلِكَ يَوْمَئِذٍ يَوْمٌ عَسِيرٌ');
INSERT INTO "quran_text" VALUES(5505,74,10,'عَلَى ٱلْكَـٰفِرِينَ غَيْرُ يَسِيرٍ');
INSERT INTO "quran_text" VALUES(5506,74,11,'ذَرْنِى وَمَنْ خَلَقْتُ وَحِيدًا');
INSERT INTO "quran_text" VALUES(5507,74,12,'وَجَعَلْتُ لَهُۥ مَالًا مَّمْدُودًا');
INSERT INTO "quran_text" VALUES(5508,74,13,'وَبَنِينَ شُهُودًا');
INSERT INTO "quran_text" VALUES(5509,74,14,'وَمَهَّدتُّ لَهُۥ تَمْهِيدًا');
INSERT INTO "quran_text" VALUES(5510,74,15,'ثُمَّ يَطْمَعُ أَنْ أَزِيدَ');
INSERT INTO "quran_text" VALUES(5511,74,16,'كَلَّآ ۖ إِنَّهُۥ كَانَ لِـَٔايَـٰتِنَا عَنِيدًا');
INSERT INTO "quran_text" VALUES(5512,74,17,'سَأُرْهِقُهُۥ صَعُودًا');
INSERT INTO "quran_text" VALUES(5513,74,18,'إِنَّهُۥ فَكَّرَ وَقَدَّرَ');
INSERT INTO "quran_text" VALUES(5514,74,19,'فَقُتِلَ كَيْفَ قَدَّرَ');
INSERT INTO "quran_text" VALUES(5515,74,20,'ثُمَّ قُتِلَ كَيْفَ قَدَّرَ');
INSERT INTO "quran_text" VALUES(5516,74,21,'ثُمَّ نَظَرَ');
INSERT INTO "quran_text" VALUES(5517,74,22,'ثُمَّ عَبَسَ وَبَسَرَ');
INSERT INTO "quran_text" VALUES(5518,74,23,'ثُمَّ أَدْبَرَ وَٱسْتَكْبَرَ');
INSERT INTO "quran_text" VALUES(5519,74,24,'فَقَالَ إِنْ هَـٰذَآ إِلَّا سِحْرٌ يُؤْثَرُ');
INSERT INTO "quran_text" VALUES(5520,74,25,'إِنْ هَـٰذَآ إِلَّا قَوْلُ ٱلْبَشَرِ');
INSERT INTO "quran_text" VALUES(5521,74,26,'سَأُصْلِيهِ سَقَرَ');
INSERT INTO "quran_text" VALUES(5522,74,27,'وَمَآ أَدْرَىٰكَ مَا سَقَرُ');
INSERT INTO "quran_text" VALUES(5523,74,28,'لَا تُبْقِى وَلَا تَذَرُ');
INSERT INTO "quran_text" VALUES(5524,74,29,'لَوَّاحَةٌ لِّلْبَشَرِ');
INSERT INTO "quran_text" VALUES(5525,74,30,'عَلَيْهَا تِسْعَةَ عَشَرَ');
INSERT INTO "quran_text" VALUES(5526,74,31,'وَمَا جَعَلْنَآ أَصْحَـٰبَ ٱلنَّارِ إِلَّا مَلَـٰٓئِكَةً ۙ وَمَا جَعَلْنَا عِدَّتَهُمْ إِلَّا فِتْنَةً لِّلَّذِينَ كَفَرُوا۟ لِيَسْتَيْقِنَ ٱلَّذِينَ أُوتُوا۟ ٱلْكِتَـٰبَ وَيَزْدَادَ ٱلَّذِينَ ءَامَنُوٓا۟ إِيمَـٰنًا ۙ وَلَا يَرْتَابَ ٱلَّذِينَ أُوتُوا۟ ٱلْكِتَـٰبَ وَٱلْمُؤْمِنُونَ ۙ وَلِيَقُولَ ٱلَّذِينَ فِى قُلُوبِهِم مَّرَضٌ وَٱلْكَـٰفِرُونَ مَاذَآ أَرَادَ ٱللَّهُ بِهَـٰذَا مَثَلًا ۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ مَن يَشَآءُ وَيَهْدِى مَن يَشَآءُ ۚ وَمَا يَعْلَمُ جُنُودَ رَبِّكَ إِلَّا هُوَ ۚ وَمَا هِىَ إِلَّا ذِكْرَىٰ لِلْبَشَرِ');
INSERT INTO "quran_text" VALUES(5527,74,32,'كَلَّا وَٱلْقَمَرِ');
INSERT INTO "quran_text" VALUES(5528,74,33,'وَٱلَّيْلِ إِذْ أَدْبَرَ');
INSERT INTO "quran_text" VALUES(5529,74,34,'وَٱلصُّبْحِ إِذَآ أَسْفَرَ');
INSERT INTO "quran_text" VALUES(5530,74,35,'إِنَّهَا لَإِحْدَى ٱلْكُبَرِ');
INSERT INTO "quran_text" VALUES(5531,74,36,'نَذِيرًا لِّلْبَشَرِ');
INSERT INTO "quran_text" VALUES(5532,74,37,'لِمَن شَآءَ مِنكُمْ أَن يَتَقَدَّمَ أَوْ يَتَأَخَّرَ');
INSERT INTO "quran_text" VALUES(5533,74,38,'كُلُّ نَفْسٍۭ بِمَا كَسَبَتْ رَهِينَةٌ');
INSERT INTO "quran_text" VALUES(5534,74,39,'إِلَّآ أَصْحَـٰبَ ٱلْيَمِينِ');
INSERT INTO "quran_text" VALUES(5535,74,40,'فِى جَنَّـٰتٍ يَتَسَآءَلُونَ');
INSERT INTO "quran_text" VALUES(5536,74,41,'عَنِ ٱلْمُجْرِمِينَ');
INSERT INTO "quran_text" VALUES(5537,74,42,'مَا سَلَكَكُمْ فِى سَقَرَ');
INSERT INTO "quran_text" VALUES(5538,74,43,'قَالُوا۟ لَمْ نَكُ مِنَ ٱلْمُصَلِّينَ');
INSERT INTO "quran_text" VALUES(5539,74,44,'وَلَمْ نَكُ نُطْعِمُ ٱلْمِسْكِينَ');
INSERT INTO "quran_text" VALUES(5540,74,45,'وَكُنَّا نَخُوضُ مَعَ ٱلْخَآئِضِينَ');
INSERT INTO "quran_text" VALUES(5541,74,46,'وَكُنَّا نُكَذِّبُ بِيَوْمِ ٱلدِّينِ');
INSERT INTO "quran_text" VALUES(5542,74,47,'حَتَّىٰٓ أَتَىٰنَا ٱلْيَقِينُ');
INSERT INTO "quran_text" VALUES(5543,74,48,'فَمَا تَنفَعُهُمْ شَفَـٰعَةُ ٱلشَّـٰفِعِينَ');
INSERT INTO "quran_text" VALUES(5544,74,49,'فَمَا لَهُمْ عَنِ ٱلتَّذْكِرَةِ مُعْرِضِينَ');
INSERT INTO "quran_text" VALUES(5545,74,50,'كَأَنَّهُمْ حُمُرٌ مُّسْتَنفِرَةٌ');
INSERT INTO "quran_text" VALUES(5546,74,51,'فَرَّتْ مِن قَسْوَرَةٍۭ');
INSERT INTO "quran_text" VALUES(5547,74,52,'بَلْ يُرِيدُ كُلُّ ٱمْرِئٍ مِّنْهُمْ أَن يُؤْتَىٰ صُحُفًا مُّنَشَّرَةً');
INSERT INTO "quran_text" VALUES(5548,74,53,'كَلَّا ۖ بَل لَّا يَخَافُونَ ٱلْـَٔاخِرَةَ');
INSERT INTO "quran_text" VALUES(5549,74,54,'كَلَّآ إِنَّهُۥ تَذْكِرَةٌ');
INSERT INTO "quran_text" VALUES(5550,74,55,'فَمَن شَآءَ ذَكَرَهُۥ');
INSERT INTO "quran_text" VALUES(5551,74,56,'وَمَا يَذْكُرُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُ ۚ هُوَ أَهْلُ ٱلتَّقْوَىٰ وَأَهْلُ ٱلْمَغْفِرَةِ');
INSERT INTO "quran_text" VALUES(5552,75,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ لَآ أُقْسِمُ بِيَوْمِ ٱلْقِيَـٰمَةِ');
INSERT INTO "quran_text" VALUES(5553,75,2,'وَلَآ أُقْسِمُ بِٱلنَّفْسِ ٱللَّوَّامَةِ');
INSERT INTO "quran_text" VALUES(5554,75,3,'أَيَحْسَبُ ٱلْإِنسَـٰنُ أَلَّن نَّجْمَعَ عِظَامَهُۥ');
INSERT INTO "quran_text" VALUES(5555,75,4,'بَلَىٰ قَـٰدِرِينَ عَلَىٰٓ أَن نُّسَوِّىَ بَنَانَهُۥ');
INSERT INTO "quran_text" VALUES(5556,75,5,'بَلْ يُرِيدُ ٱلْإِنسَـٰنُ لِيَفْجُرَ أَمَامَهُۥ');
INSERT INTO "quran_text" VALUES(5557,75,6,'يَسْـَٔلُ أَيَّانَ يَوْمُ ٱلْقِيَـٰمَةِ');
INSERT INTO "quran_text" VALUES(5558,75,7,'فَإِذَا بَرِقَ ٱلْبَصَرُ');
INSERT INTO "quran_text" VALUES(5559,75,8,'وَخَسَفَ ٱلْقَمَرُ');
INSERT INTO "quran_text" VALUES(5560,75,9,'وَجُمِعَ ٱلشَّمْسُ وَٱلْقَمَرُ');
INSERT INTO "quran_text" VALUES(5561,75,10,'يَقُولُ ٱلْإِنسَـٰنُ يَوْمَئِذٍ أَيْنَ ٱلْمَفَرُّ');
INSERT INTO "quran_text" VALUES(5562,75,11,'كَلَّا لَا وَزَرَ');
INSERT INTO "quran_text" VALUES(5563,75,12,'إِلَىٰ رَبِّكَ يَوْمَئِذٍ ٱلْمُسْتَقَرُّ');
INSERT INTO "quran_text" VALUES(5564,75,13,'يُنَبَّؤُا۟ ٱلْإِنسَـٰنُ يَوْمَئِذٍۭ بِمَا قَدَّمَ وَأَخَّرَ');
INSERT INTO "quran_text" VALUES(5565,75,14,'بَلِ ٱلْإِنسَـٰنُ عَلَىٰ نَفْسِهِۦ بَصِيرَةٌ');
INSERT INTO "quran_text" VALUES(5566,75,15,'وَلَوْ أَلْقَىٰ مَعَاذِيرَهُۥ');
INSERT INTO "quran_text" VALUES(5567,75,16,'لَا تُحَرِّكْ بِهِۦ لِسَانَكَ لِتَعْجَلَ بِهِۦٓ');
INSERT INTO "quran_text" VALUES(5568,75,17,'إِنَّ عَلَيْنَا جَمْعَهُۥ وَقُرْءَانَهُۥ');
INSERT INTO "quran_text" VALUES(5569,75,18,'فَإِذَا قَرَأْنَـٰهُ فَٱتَّبِعْ قُرْءَانَهُۥ');
INSERT INTO "quran_text" VALUES(5570,75,19,'ثُمَّ إِنَّ عَلَيْنَا بَيَانَهُۥ');
INSERT INTO "quran_text" VALUES(5571,75,20,'كَلَّا بَلْ تُحِبُّونَ ٱلْعَاجِلَةَ');
INSERT INTO "quran_text" VALUES(5572,75,21,'وَتَذَرُونَ ٱلْـَٔاخِرَةَ');
INSERT INTO "quran_text" VALUES(5573,75,22,'وُجُوهٌ يَوْمَئِذٍ نَّاضِرَةٌ');
INSERT INTO "quran_text" VALUES(5574,75,23,'إِلَىٰ رَبِّهَا نَاظِرَةٌ');
INSERT INTO "quran_text" VALUES(5575,75,24,'وَوُجُوهٌ يَوْمَئِذٍۭ بَاسِرَةٌ');
INSERT INTO "quran_text" VALUES(5576,75,25,'تَظُنُّ أَن يُفْعَلَ بِهَا فَاقِرَةٌ');
INSERT INTO "quran_text" VALUES(5577,75,26,'كَلَّآ إِذَا بَلَغَتِ ٱلتَّرَاقِىَ');
INSERT INTO "quran_text" VALUES(5578,75,27,'وَقِيلَ مَنْ ۜ رَاقٍ');
INSERT INTO "quran_text" VALUES(5579,75,28,'وَظَنَّ أَنَّهُ ٱلْفِرَاقُ');
INSERT INTO "quran_text" VALUES(5580,75,29,'وَٱلْتَفَّتِ ٱلسَّاقُ بِٱلسَّاقِ');
INSERT INTO "quran_text" VALUES(5581,75,30,'إِلَىٰ رَبِّكَ يَوْمَئِذٍ ٱلْمَسَاقُ');
INSERT INTO "quran_text" VALUES(5582,75,31,'فَلَا صَدَّقَ وَلَا صَلَّىٰ');
INSERT INTO "quran_text" VALUES(5583,75,32,'وَلَـٰكِن كَذَّبَ وَتَوَلَّىٰ');
INSERT INTO "quran_text" VALUES(5584,75,33,'ثُمَّ ذَهَبَ إِلَىٰٓ أَهْلِهِۦ يَتَمَطَّىٰٓ');
INSERT INTO "quran_text" VALUES(5585,75,34,'أَوْلَىٰ لَكَ فَأَوْلَىٰ');
INSERT INTO "quran_text" VALUES(5586,75,35,'ثُمَّ أَوْلَىٰ لَكَ فَأَوْلَىٰٓ');
INSERT INTO "quran_text" VALUES(5587,75,36,'أَيَحْسَبُ ٱلْإِنسَـٰنُ أَن يُتْرَكَ سُدًى');
INSERT INTO "quran_text" VALUES(5588,75,37,'أَلَمْ يَكُ نُطْفَةً مِّن مَّنِىٍّ يُمْنَىٰ');
INSERT INTO "quran_text" VALUES(5589,75,38,'ثُمَّ كَانَ عَلَقَةً فَخَلَقَ فَسَوَّىٰ');
INSERT INTO "quran_text" VALUES(5590,75,39,'فَجَعَلَ مِنْهُ ٱلزَّوْجَيْنِ ٱلذَّكَرَ وَٱلْأُنثَىٰٓ');
INSERT INTO "quran_text" VALUES(5591,75,40,'أَلَيْسَ ذَٰلِكَ بِقَـٰدِرٍ عَلَىٰٓ أَن يُحْـِۧىَ ٱلْمَوْتَىٰ');
INSERT INTO "quran_text" VALUES(5592,76,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ هَلْ أَتَىٰ عَلَى ٱلْإِنسَـٰنِ حِينٌ مِّنَ ٱلدَّهْرِ لَمْ يَكُن شَيْـًٔا مَّذْكُورًا');
INSERT INTO "quran_text" VALUES(5593,76,2,'إِنَّا خَلَقْنَا ٱلْإِنسَـٰنَ مِن نُّطْفَةٍ أَمْشَاجٍ نَّبْتَلِيهِ فَجَعَلْنَـٰهُ سَمِيعًۢا بَصِيرًا');
INSERT INTO "quran_text" VALUES(5594,76,3,'إِنَّا هَدَيْنَـٰهُ ٱلسَّبِيلَ إِمَّا شَاكِرًا وَإِمَّا كَفُورًا');
INSERT INTO "quran_text" VALUES(5595,76,4,'إِنَّآ أَعْتَدْنَا لِلْكَـٰفِرِينَ سَلَـٰسِلَا۟ وَأَغْلَـٰلًا وَسَعِيرًا');
INSERT INTO "quran_text" VALUES(5596,76,5,'إِنَّ ٱلْأَبْرَارَ يَشْرَبُونَ مِن كَأْسٍ كَانَ مِزَاجُهَا كَافُورًا');
INSERT INTO "quran_text" VALUES(5597,76,6,'عَيْنًا يَشْرَبُ بِهَا عِبَادُ ٱللَّهِ يُفَجِّرُونَهَا تَفْجِيرًا');
INSERT INTO "quran_text" VALUES(5598,76,7,'يُوفُونَ بِٱلنَّذْرِ وَيَخَافُونَ يَوْمًا كَانَ شَرُّهُۥ مُسْتَطِيرًا');
INSERT INTO "quran_text" VALUES(5599,76,8,'وَيُطْعِمُونَ ٱلطَّعَامَ عَلَىٰ حُبِّهِۦ مِسْكِينًا وَيَتِيمًا وَأَسِيرًا');
INSERT INTO "quran_text" VALUES(5600,76,9,'إِنَّمَا نُطْعِمُكُمْ لِوَجْهِ ٱللَّهِ لَا نُرِيدُ مِنكُمْ جَزَآءً وَلَا شُكُورًا');
INSERT INTO "quran_text" VALUES(5601,76,10,'إِنَّا نَخَافُ مِن رَّبِّنَا يَوْمًا عَبُوسًا قَمْطَرِيرًا');
INSERT INTO "quran_text" VALUES(5602,76,11,'فَوَقَىٰهُمُ ٱللَّهُ شَرَّ ذَٰلِكَ ٱلْيَوْمِ وَلَقَّىٰهُمْ نَضْرَةً وَسُرُورًا');
INSERT INTO "quran_text" VALUES(5603,76,12,'وَجَزَىٰهُم بِمَا صَبَرُوا۟ جَنَّةً وَحَرِيرًا');
INSERT INTO "quran_text" VALUES(5604,76,13,'مُّتَّكِـِٔينَ فِيهَا عَلَى ٱلْأَرَآئِكِ ۖ لَا يَرَوْنَ فِيهَا شَمْسًا وَلَا زَمْهَرِيرًا');
INSERT INTO "quran_text" VALUES(5605,76,14,'وَدَانِيَةً عَلَيْهِمْ ظِلَـٰلُهَا وَذُلِّلَتْ قُطُوفُهَا تَذْلِيلًا');
INSERT INTO "quran_text" VALUES(5606,76,15,'وَيُطَافُ عَلَيْهِم بِـَٔانِيَةٍ مِّن فِضَّةٍ وَأَكْوَابٍ كَانَتْ قَوَارِيرَا۠');
INSERT INTO "quran_text" VALUES(5607,76,16,'قَوَارِيرَا۟ مِن فِضَّةٍ قَدَّرُوهَا تَقْدِيرًا');
INSERT INTO "quran_text" VALUES(5608,76,17,'وَيُسْقَوْنَ فِيهَا كَأْسًا كَانَ مِزَاجُهَا زَنجَبِيلًا');
INSERT INTO "quran_text" VALUES(5609,76,18,'عَيْنًا فِيهَا تُسَمَّىٰ سَلْسَبِيلًا');
INSERT INTO "quran_text" VALUES(5610,76,19,'۞ وَيَطُوفُ عَلَيْهِمْ وِلْدَٰنٌ مُّخَلَّدُونَ إِذَا رَأَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤًا مَّنثُورًا');
INSERT INTO "quran_text" VALUES(5611,76,20,'وَإِذَا رَأَيْتَ ثَمَّ رَأَيْتَ نَعِيمًا وَمُلْكًا كَبِيرًا');
INSERT INTO "quran_text" VALUES(5612,76,21,'عَـٰلِيَهُمْ ثِيَابُ سُندُسٍ خُضْرٌ وَإِسْتَبْرَقٌ ۖ وَحُلُّوٓا۟ أَسَاوِرَ مِن فِضَّةٍ وَسَقَىٰهُمْ رَبُّهُمْ شَرَابًا طَهُورًا');
INSERT INTO "quran_text" VALUES(5613,76,22,'إِنَّ هَـٰذَا كَانَ لَكُمْ جَزَآءً وَكَانَ سَعْيُكُم مَّشْكُورًا');
INSERT INTO "quran_text" VALUES(5614,76,23,'إِنَّا نَحْنُ نَزَّلْنَا عَلَيْكَ ٱلْقُرْءَانَ تَنزِيلًا');
INSERT INTO "quran_text" VALUES(5615,76,24,'فَٱصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تُطِعْ مِنْهُمْ ءَاثِمًا أَوْ كَفُورًا');
INSERT INTO "quran_text" VALUES(5616,76,25,'وَٱذْكُرِ ٱسْمَ رَبِّكَ بُكْرَةً وَأَصِيلًا');
INSERT INTO "quran_text" VALUES(5617,76,26,'وَمِنَ ٱلَّيْلِ فَٱسْجُدْ لَهُۥ وَسَبِّحْهُ لَيْلًا طَوِيلًا');
INSERT INTO "quran_text" VALUES(5618,76,27,'إِنَّ هَـٰٓؤُلَآءِ يُحِبُّونَ ٱلْعَاجِلَةَ وَيَذَرُونَ وَرَآءَهُمْ يَوْمًا ثَقِيلًا');
INSERT INTO "quran_text" VALUES(5619,76,28,'نَّحْنُ خَلَقْنَـٰهُمْ وَشَدَدْنَآ أَسْرَهُمْ ۖ وَإِذَا شِئْنَا بَدَّلْنَآ أَمْثَـٰلَهُمْ تَبْدِيلًا');
INSERT INTO "quran_text" VALUES(5620,76,29,'إِنَّ هَـٰذِهِۦ تَذْكِرَةٌ ۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلًا');
INSERT INTO "quran_text" VALUES(5621,76,30,'وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُ ۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمًا');
INSERT INTO "quran_text" VALUES(5622,76,31,'يُدْخِلُ مَن يَشَآءُ فِى رَحْمَتِهِۦ ۚ وَٱلظَّـٰلِمِينَ أَعَدَّ لَهُمْ عَذَابًا أَلِيمًۢا');
INSERT INTO "quran_text" VALUES(5623,77,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَٱلْمُرْسَلَـٰتِ عُرْفًا');
INSERT INTO "quran_text" VALUES(5624,77,2,'فَٱلْعَـٰصِفَـٰتِ عَصْفًا');
INSERT INTO "quran_text" VALUES(5625,77,3,'وَٱلنَّـٰشِرَٰتِ نَشْرًا');
INSERT INTO "quran_text" VALUES(5626,77,4,'فَٱلْفَـٰرِقَـٰتِ فَرْقًا');
INSERT INTO "quran_text" VALUES(5627,77,5,'فَٱلْمُلْقِيَـٰتِ ذِكْرًا');
INSERT INTO "quran_text" VALUES(5628,77,6,'عُذْرًا أَوْ نُذْرًا');
INSERT INTO "quran_text" VALUES(5629,77,7,'إِنَّمَا تُوعَدُونَ لَوَٰقِعٌ');
INSERT INTO "quran_text" VALUES(5630,77,8,'فَإِذَا ٱلنُّجُومُ طُمِسَتْ');
INSERT INTO "quran_text" VALUES(5631,77,9,'وَإِذَا ٱلسَّمَآءُ فُرِجَتْ');
INSERT INTO "quran_text" VALUES(5632,77,10,'وَإِذَا ٱلْجِبَالُ نُسِفَتْ');
INSERT INTO "quran_text" VALUES(5633,77,11,'وَإِذَا ٱلرُّسُلُ أُقِّتَتْ');
INSERT INTO "quran_text" VALUES(5634,77,12,'لِأَىِّ يَوْمٍ أُجِّلَتْ');
INSERT INTO "quran_text" VALUES(5635,77,13,'لِيَوْمِ ٱلْفَصْلِ');
INSERT INTO "quran_text" VALUES(5636,77,14,'وَمَآ أَدْرَىٰكَ مَا يَوْمُ ٱلْفَصْلِ');
INSERT INTO "quran_text" VALUES(5637,77,15,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5638,77,16,'أَلَمْ نُهْلِكِ ٱلْأَوَّلِينَ');
INSERT INTO "quran_text" VALUES(5639,77,17,'ثُمَّ نُتْبِعُهُمُ ٱلْـَٔاخِرِينَ');
INSERT INTO "quran_text" VALUES(5640,77,18,'كَذَٰلِكَ نَفْعَلُ بِٱلْمُجْرِمِينَ');
INSERT INTO "quran_text" VALUES(5641,77,19,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5642,77,20,'أَلَمْ نَخْلُقكُّم مِّن مَّآءٍ مَّهِينٍ');
INSERT INTO "quran_text" VALUES(5643,77,21,'فَجَعَلْنَـٰهُ فِى قَرَارٍ مَّكِينٍ');
INSERT INTO "quran_text" VALUES(5644,77,22,'إِلَىٰ قَدَرٍ مَّعْلُومٍ');
INSERT INTO "quran_text" VALUES(5645,77,23,'فَقَدَرْنَا فَنِعْمَ ٱلْقَـٰدِرُونَ');
INSERT INTO "quran_text" VALUES(5646,77,24,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5647,77,25,'أَلَمْ نَجْعَلِ ٱلْأَرْضَ كِفَاتًا');
INSERT INTO "quran_text" VALUES(5648,77,26,'أَحْيَآءً وَأَمْوَٰتًا');
INSERT INTO "quran_text" VALUES(5649,77,27,'وَجَعَلْنَا فِيهَا رَوَٰسِىَ شَـٰمِخَـٰتٍ وَأَسْقَيْنَـٰكُم مَّآءً فُرَاتًا');
INSERT INTO "quran_text" VALUES(5650,77,28,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5651,77,29,'ٱنطَلِقُوٓا۟ إِلَىٰ مَا كُنتُم بِهِۦ تُكَذِّبُونَ');
INSERT INTO "quran_text" VALUES(5652,77,30,'ٱنطَلِقُوٓا۟ إِلَىٰ ظِلٍّ ذِى ثَلَـٰثِ شُعَبٍ');
INSERT INTO "quran_text" VALUES(5653,77,31,'لَّا ظَلِيلٍ وَلَا يُغْنِى مِنَ ٱللَّهَبِ');
INSERT INTO "quran_text" VALUES(5654,77,32,'إِنَّهَا تَرْمِى بِشَرَرٍ كَٱلْقَصْرِ');
INSERT INTO "quran_text" VALUES(5655,77,33,'كَأَنَّهُۥ جِمَـٰلَتٌ صُفْرٌ');
INSERT INTO "quran_text" VALUES(5656,77,34,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5657,77,35,'هَـٰذَا يَوْمُ لَا يَنطِقُونَ');
INSERT INTO "quran_text" VALUES(5658,77,36,'وَلَا يُؤْذَنُ لَهُمْ فَيَعْتَذِرُونَ');
INSERT INTO "quran_text" VALUES(5659,77,37,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5660,77,38,'هَـٰذَا يَوْمُ ٱلْفَصْلِ ۖ جَمَعْنَـٰكُمْ وَٱلْأَوَّلِينَ');
INSERT INTO "quran_text" VALUES(5661,77,39,'فَإِن كَانَ لَكُمْ كَيْدٌ فَكِيدُونِ');
INSERT INTO "quran_text" VALUES(5662,77,40,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5663,77,41,'إِنَّ ٱلْمُتَّقِينَ فِى ظِلَـٰلٍ وَعُيُونٍ');
INSERT INTO "quran_text" VALUES(5664,77,42,'وَفَوَٰكِهَ مِمَّا يَشْتَهُونَ');
INSERT INTO "quran_text" VALUES(5665,77,43,'كُلُوا۟ وَٱشْرَبُوا۟ هَنِيٓـًٔۢا بِمَا كُنتُمْ تَعْمَلُونَ');
INSERT INTO "quran_text" VALUES(5666,77,44,'إِنَّا كَذَٰلِكَ نَجْزِى ٱلْمُحْسِنِينَ');
INSERT INTO "quran_text" VALUES(5667,77,45,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5668,77,46,'كُلُوا۟ وَتَمَتَّعُوا۟ قَلِيلًا إِنَّكُم مُّجْرِمُونَ');
INSERT INTO "quran_text" VALUES(5669,77,47,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5670,77,48,'وَإِذَا قِيلَ لَهُمُ ٱرْكَعُوا۟ لَا يَرْكَعُونَ');
INSERT INTO "quran_text" VALUES(5671,77,49,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5672,77,50,'فَبِأَىِّ حَدِيثٍۭ بَعْدَهُۥ يُؤْمِنُونَ');
INSERT INTO "quran_text" VALUES(5673,78,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ عَمَّ يَتَسَآءَلُونَ');
INSERT INTO "quran_text" VALUES(5674,78,2,'عَنِ ٱلنَّبَإِ ٱلْعَظِيمِ');
INSERT INTO "quran_text" VALUES(5675,78,3,'ٱلَّذِى هُمْ فِيهِ مُخْتَلِفُونَ');
INSERT INTO "quran_text" VALUES(5676,78,4,'كَلَّا سَيَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(5677,78,5,'ثُمَّ كَلَّا سَيَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(5678,78,6,'أَلَمْ نَجْعَلِ ٱلْأَرْضَ مِهَـٰدًا');
INSERT INTO "quran_text" VALUES(5679,78,7,'وَٱلْجِبَالَ أَوْتَادًا');
INSERT INTO "quran_text" VALUES(5680,78,8,'وَخَلَقْنَـٰكُمْ أَزْوَٰجًا');
INSERT INTO "quran_text" VALUES(5681,78,9,'وَجَعَلْنَا نَوْمَكُمْ سُبَاتًا');
INSERT INTO "quran_text" VALUES(5682,78,10,'وَجَعَلْنَا ٱلَّيْلَ لِبَاسًا');
INSERT INTO "quran_text" VALUES(5683,78,11,'وَجَعَلْنَا ٱلنَّهَارَ مَعَاشًا');
INSERT INTO "quran_text" VALUES(5684,78,12,'وَبَنَيْنَا فَوْقَكُمْ سَبْعًا شِدَادًا');
INSERT INTO "quran_text" VALUES(5685,78,13,'وَجَعَلْنَا سِرَاجًا وَهَّاجًا');
INSERT INTO "quran_text" VALUES(5686,78,14,'وَأَنزَلْنَا مِنَ ٱلْمُعْصِرَٰتِ مَآءً ثَجَّاجًا');
INSERT INTO "quran_text" VALUES(5687,78,15,'لِّنُخْرِجَ بِهِۦ حَبًّا وَنَبَاتًا');
INSERT INTO "quran_text" VALUES(5688,78,16,'وَجَنَّـٰتٍ أَلْفَافًا');
INSERT INTO "quran_text" VALUES(5689,78,17,'إِنَّ يَوْمَ ٱلْفَصْلِ كَانَ مِيقَـٰتًا');
INSERT INTO "quran_text" VALUES(5690,78,18,'يَوْمَ يُنفَخُ فِى ٱلصُّورِ فَتَأْتُونَ أَفْوَاجًا');
INSERT INTO "quran_text" VALUES(5691,78,19,'وَفُتِحَتِ ٱلسَّمَآءُ فَكَانَتْ أَبْوَٰبًا');
INSERT INTO "quran_text" VALUES(5692,78,20,'وَسُيِّرَتِ ٱلْجِبَالُ فَكَانَتْ سَرَابًا');
INSERT INTO "quran_text" VALUES(5693,78,21,'إِنَّ جَهَنَّمَ كَانَتْ مِرْصَادًا');
INSERT INTO "quran_text" VALUES(5694,78,22,'لِّلطَّـٰغِينَ مَـَٔابًا');
INSERT INTO "quran_text" VALUES(5695,78,23,'لَّـٰبِثِينَ فِيهَآ أَحْقَابًا');
INSERT INTO "quran_text" VALUES(5696,78,24,'لَّا يَذُوقُونَ فِيهَا بَرْدًا وَلَا شَرَابًا');
INSERT INTO "quran_text" VALUES(5697,78,25,'إِلَّا حَمِيمًا وَغَسَّاقًا');
INSERT INTO "quran_text" VALUES(5698,78,26,'جَزَآءً وِفَاقًا');
INSERT INTO "quran_text" VALUES(5699,78,27,'إِنَّهُمْ كَانُوا۟ لَا يَرْجُونَ حِسَابًا');
INSERT INTO "quran_text" VALUES(5700,78,28,'وَكَذَّبُوا۟ بِـَٔايَـٰتِنَا كِذَّابًا');
INSERT INTO "quran_text" VALUES(5701,78,29,'وَكُلَّ شَىْءٍ أَحْصَيْنَـٰهُ كِتَـٰبًا');
INSERT INTO "quran_text" VALUES(5702,78,30,'فَذُوقُوا۟ فَلَن نَّزِيدَكُمْ إِلَّا عَذَابًا');
INSERT INTO "quran_text" VALUES(5703,78,31,'إِنَّ لِلْمُتَّقِينَ مَفَازًا');
INSERT INTO "quran_text" VALUES(5704,78,32,'حَدَآئِقَ وَأَعْنَـٰبًا');
INSERT INTO "quran_text" VALUES(5705,78,33,'وَكَوَاعِبَ أَتْرَابًا');
INSERT INTO "quran_text" VALUES(5706,78,34,'وَكَأْسًا دِهَاقًا');
INSERT INTO "quran_text" VALUES(5707,78,35,'لَّا يَسْمَعُونَ فِيهَا لَغْوًا وَلَا كِذَّٰبًا');
INSERT INTO "quran_text" VALUES(5708,78,36,'جَزَآءً مِّن رَّبِّكَ عَطَآءً حِسَابًا');
INSERT INTO "quran_text" VALUES(5709,78,37,'رَّبِّ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَا ٱلرَّحْمَـٰنِ ۖ لَا يَمْلِكُونَ مِنْهُ خِطَابًا');
INSERT INTO "quran_text" VALUES(5710,78,38,'يَوْمَ يَقُومُ ٱلرُّوحُ وَٱلْمَلَـٰٓئِكَةُ صَفًّا ۖ لَّا يَتَكَلَّمُونَ إِلَّا مَنْ أَذِنَ لَهُ ٱلرَّحْمَـٰنُ وَقَالَ صَوَابًا');
INSERT INTO "quran_text" VALUES(5711,78,39,'ذَٰلِكَ ٱلْيَوْمُ ٱلْحَقُّ ۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ مَـَٔابًا');
INSERT INTO "quran_text" VALUES(5712,78,40,'إِنَّآ أَنذَرْنَـٰكُمْ عَذَابًا قَرِيبًا يَوْمَ يَنظُرُ ٱلْمَرْءُ مَا قَدَّمَتْ يَدَاهُ وَيَقُولُ ٱلْكَافِرُ يَـٰلَيْتَنِى كُنتُ تُرَٰبًۢا');
INSERT INTO "quran_text" VALUES(5713,79,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَٱلنَّـٰزِعَـٰتِ غَرْقًا');
INSERT INTO "quran_text" VALUES(5714,79,2,'وَٱلنَّـٰشِطَـٰتِ نَشْطًا');
INSERT INTO "quran_text" VALUES(5715,79,3,'وَٱلسَّـٰبِحَـٰتِ سَبْحًا');
INSERT INTO "quran_text" VALUES(5716,79,4,'فَٱلسَّـٰبِقَـٰتِ سَبْقًا');
INSERT INTO "quran_text" VALUES(5717,79,5,'فَٱلْمُدَبِّرَٰتِ أَمْرًا');
INSERT INTO "quran_text" VALUES(5718,79,6,'يَوْمَ تَرْجُفُ ٱلرَّاجِفَةُ');
INSERT INTO "quran_text" VALUES(5719,79,7,'تَتْبَعُهَا ٱلرَّادِفَةُ');
INSERT INTO "quran_text" VALUES(5720,79,8,'قُلُوبٌ يَوْمَئِذٍ وَاجِفَةٌ');
INSERT INTO "quran_text" VALUES(5721,79,9,'أَبْصَـٰرُهَا خَـٰشِعَةٌ');
INSERT INTO "quran_text" VALUES(5722,79,10,'يَقُولُونَ أَءِنَّا لَمَرْدُودُونَ فِى ٱلْحَافِرَةِ');
INSERT INTO "quran_text" VALUES(5723,79,11,'أَءِذَا كُنَّا عِظَـٰمًا نَّخِرَةً');
INSERT INTO "quran_text" VALUES(5724,79,12,'قَالُوا۟ تِلْكَ إِذًا كَرَّةٌ خَاسِرَةٌ');
INSERT INTO "quran_text" VALUES(5725,79,13,'فَإِنَّمَا هِىَ زَجْرَةٌ وَٰحِدَةٌ');
INSERT INTO "quran_text" VALUES(5726,79,14,'فَإِذَا هُم بِٱلسَّاهِرَةِ');
INSERT INTO "quran_text" VALUES(5727,79,15,'هَلْ أَتَىٰكَ حَدِيثُ مُوسَىٰٓ');
INSERT INTO "quran_text" VALUES(5728,79,16,'إِذْ نَادَىٰهُ رَبُّهُۥ بِٱلْوَادِ ٱلْمُقَدَّسِ طُوًى');
INSERT INTO "quran_text" VALUES(5729,79,17,'ٱذْهَبْ إِلَىٰ فِرْعَوْنَ إِنَّهُۥ طَغَىٰ');
INSERT INTO "quran_text" VALUES(5730,79,18,'فَقُلْ هَل لَّكَ إِلَىٰٓ أَن تَزَكَّىٰ');
INSERT INTO "quran_text" VALUES(5731,79,19,'وَأَهْدِيَكَ إِلَىٰ رَبِّكَ فَتَخْشَىٰ');
INSERT INTO "quran_text" VALUES(5732,79,20,'فَأَرَىٰهُ ٱلْـَٔايَةَ ٱلْكُبْرَىٰ');
INSERT INTO "quran_text" VALUES(5733,79,21,'فَكَذَّبَ وَعَصَىٰ');
INSERT INTO "quran_text" VALUES(5734,79,22,'ثُمَّ أَدْبَرَ يَسْعَىٰ');
INSERT INTO "quran_text" VALUES(5735,79,23,'فَحَشَرَ فَنَادَىٰ');
INSERT INTO "quran_text" VALUES(5736,79,24,'فَقَالَ أَنَا۠ رَبُّكُمُ ٱلْأَعْلَىٰ');
INSERT INTO "quran_text" VALUES(5737,79,25,'فَأَخَذَهُ ٱللَّهُ نَكَالَ ٱلْـَٔاخِرَةِ وَٱلْأُولَىٰٓ');
INSERT INTO "quran_text" VALUES(5738,79,26,'إِنَّ فِى ذَٰلِكَ لَعِبْرَةً لِّمَن يَخْشَىٰٓ');
INSERT INTO "quran_text" VALUES(5739,79,27,'ءَأَنتُمْ أَشَدُّ خَلْقًا أَمِ ٱلسَّمَآءُ ۚ بَنَىٰهَا');
INSERT INTO "quran_text" VALUES(5740,79,28,'رَفَعَ سَمْكَهَا فَسَوَّىٰهَا');
INSERT INTO "quran_text" VALUES(5741,79,29,'وَأَغْطَشَ لَيْلَهَا وَأَخْرَجَ ضُحَىٰهَا');
INSERT INTO "quran_text" VALUES(5742,79,30,'وَٱلْأَرْضَ بَعْدَ ذَٰلِكَ دَحَىٰهَآ');
INSERT INTO "quran_text" VALUES(5743,79,31,'أَخْرَجَ مِنْهَا مَآءَهَا وَمَرْعَىٰهَا');
INSERT INTO "quran_text" VALUES(5744,79,32,'وَٱلْجِبَالَ أَرْسَىٰهَا');
INSERT INTO "quran_text" VALUES(5745,79,33,'مَتَـٰعًا لَّكُمْ وَلِأَنْعَـٰمِكُمْ');
INSERT INTO "quran_text" VALUES(5746,79,34,'فَإِذَا جَآءَتِ ٱلطَّآمَّةُ ٱلْكُبْرَىٰ');
INSERT INTO "quran_text" VALUES(5747,79,35,'يَوْمَ يَتَذَكَّرُ ٱلْإِنسَـٰنُ مَا سَعَىٰ');
INSERT INTO "quran_text" VALUES(5748,79,36,'وَبُرِّزَتِ ٱلْجَحِيمُ لِمَن يَرَىٰ');
INSERT INTO "quran_text" VALUES(5749,79,37,'فَأَمَّا مَن طَغَىٰ');
INSERT INTO "quran_text" VALUES(5750,79,38,'وَءَاثَرَ ٱلْحَيَوٰةَ ٱلدُّنْيَا');
INSERT INTO "quran_text" VALUES(5751,79,39,'فَإِنَّ ٱلْجَحِيمَ هِىَ ٱلْمَأْوَىٰ');
INSERT INTO "quran_text" VALUES(5752,79,40,'وَأَمَّا مَنْ خَافَ مَقَامَ رَبِّهِۦ وَنَهَى ٱلنَّفْسَ عَنِ ٱلْهَوَىٰ');
INSERT INTO "quran_text" VALUES(5753,79,41,'فَإِنَّ ٱلْجَنَّةَ هِىَ ٱلْمَأْوَىٰ');
INSERT INTO "quran_text" VALUES(5754,79,42,'يَسْـَٔلُونَكَ عَنِ ٱلسَّاعَةِ أَيَّانَ مُرْسَىٰهَا');
INSERT INTO "quran_text" VALUES(5755,79,43,'فِيمَ أَنتَ مِن ذِكْرَىٰهَآ');
INSERT INTO "quran_text" VALUES(5756,79,44,'إِلَىٰ رَبِّكَ مُنتَهَىٰهَآ');
INSERT INTO "quran_text" VALUES(5757,79,45,'إِنَّمَآ أَنتَ مُنذِرُ مَن يَخْشَىٰهَا');
INSERT INTO "quran_text" VALUES(5758,79,46,'كَأَنَّهُمْ يَوْمَ يَرَوْنَهَا لَمْ يَلْبَثُوٓا۟ إِلَّا عَشِيَّةً أَوْ ضُحَىٰهَا');
INSERT INTO "quran_text" VALUES(5759,80,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ عَبَسَ وَتَوَلَّىٰٓ');
INSERT INTO "quran_text" VALUES(5760,80,2,'أَن جَآءَهُ ٱلْأَعْمَىٰ');
INSERT INTO "quran_text" VALUES(5761,80,3,'وَمَا يُدْرِيكَ لَعَلَّهُۥ يَزَّكَّىٰٓ');
INSERT INTO "quran_text" VALUES(5762,80,4,'أَوْ يَذَّكَّرُ فَتَنفَعَهُ ٱلذِّكْرَىٰٓ');
INSERT INTO "quran_text" VALUES(5763,80,5,'أَمَّا مَنِ ٱسْتَغْنَىٰ');
INSERT INTO "quran_text" VALUES(5764,80,6,'فَأَنتَ لَهُۥ تَصَدَّىٰ');
INSERT INTO "quran_text" VALUES(5765,80,7,'وَمَا عَلَيْكَ أَلَّا يَزَّكَّىٰ');
INSERT INTO "quran_text" VALUES(5766,80,8,'وَأَمَّا مَن جَآءَكَ يَسْعَىٰ');
INSERT INTO "quran_text" VALUES(5767,80,9,'وَهُوَ يَخْشَىٰ');
INSERT INTO "quran_text" VALUES(5768,80,10,'فَأَنتَ عَنْهُ تَلَهَّىٰ');
INSERT INTO "quran_text" VALUES(5769,80,11,'كَلَّآ إِنَّهَا تَذْكِرَةٌ');
INSERT INTO "quran_text" VALUES(5770,80,12,'فَمَن شَآءَ ذَكَرَهُۥ');
INSERT INTO "quran_text" VALUES(5771,80,13,'فِى صُحُفٍ مُّكَرَّمَةٍ');
INSERT INTO "quran_text" VALUES(5772,80,14,'مَّرْفُوعَةٍ مُّطَهَّرَةٍۭ');
INSERT INTO "quran_text" VALUES(5773,80,15,'بِأَيْدِى سَفَرَةٍ');
INSERT INTO "quran_text" VALUES(5774,80,16,'كِرَامٍۭ بَرَرَةٍ');
INSERT INTO "quran_text" VALUES(5775,80,17,'قُتِلَ ٱلْإِنسَـٰنُ مَآ أَكْفَرَهُۥ');
INSERT INTO "quran_text" VALUES(5776,80,18,'مِنْ أَىِّ شَىْءٍ خَلَقَهُۥ');
INSERT INTO "quran_text" VALUES(5777,80,19,'مِن نُّطْفَةٍ خَلَقَهُۥ فَقَدَّرَهُۥ');
INSERT INTO "quran_text" VALUES(5778,80,20,'ثُمَّ ٱلسَّبِيلَ يَسَّرَهُۥ');
INSERT INTO "quran_text" VALUES(5779,80,21,'ثُمَّ أَمَاتَهُۥ فَأَقْبَرَهُۥ');
INSERT INTO "quran_text" VALUES(5780,80,22,'ثُمَّ إِذَا شَآءَ أَنشَرَهُۥ');
INSERT INTO "quran_text" VALUES(5781,80,23,'كَلَّا لَمَّا يَقْضِ مَآ أَمَرَهُۥ');
INSERT INTO "quran_text" VALUES(5782,80,24,'فَلْيَنظُرِ ٱلْإِنسَـٰنُ إِلَىٰ طَعَامِهِۦٓ');
INSERT INTO "quran_text" VALUES(5783,80,25,'أَنَّا صَبَبْنَا ٱلْمَآءَ صَبًّا');
INSERT INTO "quran_text" VALUES(5784,80,26,'ثُمَّ شَقَقْنَا ٱلْأَرْضَ شَقًّا');
INSERT INTO "quran_text" VALUES(5785,80,27,'فَأَنۢبَتْنَا فِيهَا حَبًّا');
INSERT INTO "quran_text" VALUES(5786,80,28,'وَعِنَبًا وَقَضْبًا');
INSERT INTO "quran_text" VALUES(5787,80,29,'وَزَيْتُونًا وَنَخْلًا');
INSERT INTO "quran_text" VALUES(5788,80,30,'وَحَدَآئِقَ غُلْبًا');
INSERT INTO "quran_text" VALUES(5789,80,31,'وَفَـٰكِهَةً وَأَبًّا');
INSERT INTO "quran_text" VALUES(5790,80,32,'مَّتَـٰعًا لَّكُمْ وَلِأَنْعَـٰمِكُمْ');
INSERT INTO "quran_text" VALUES(5791,80,33,'فَإِذَا جَآءَتِ ٱلصَّآخَّةُ');
INSERT INTO "quran_text" VALUES(5792,80,34,'يَوْمَ يَفِرُّ ٱلْمَرْءُ مِنْ أَخِيهِ');
INSERT INTO "quran_text" VALUES(5793,80,35,'وَأُمِّهِۦ وَأَبِيهِ');
INSERT INTO "quran_text" VALUES(5794,80,36,'وَصَـٰحِبَتِهِۦ وَبَنِيهِ');
INSERT INTO "quran_text" VALUES(5795,80,37,'لِكُلِّ ٱمْرِئٍ مِّنْهُمْ يَوْمَئِذٍ شَأْنٌ يُغْنِيهِ');
INSERT INTO "quran_text" VALUES(5796,80,38,'وُجُوهٌ يَوْمَئِذٍ مُّسْفِرَةٌ');
INSERT INTO "quran_text" VALUES(5797,80,39,'ضَاحِكَةٌ مُّسْتَبْشِرَةٌ');
INSERT INTO "quran_text" VALUES(5798,80,40,'وَوُجُوهٌ يَوْمَئِذٍ عَلَيْهَا غَبَرَةٌ');
INSERT INTO "quran_text" VALUES(5799,80,41,'تَرْهَقُهَا قَتَرَةٌ');
INSERT INTO "quran_text" VALUES(5800,80,42,'أُو۟لَـٰٓئِكَ هُمُ ٱلْكَفَرَةُ ٱلْفَجَرَةُ');
INSERT INTO "quran_text" VALUES(5801,81,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ إِذَا ٱلشَّمْسُ كُوِّرَتْ');
INSERT INTO "quran_text" VALUES(5802,81,2,'وَإِذَا ٱلنُّجُومُ ٱنكَدَرَتْ');
INSERT INTO "quran_text" VALUES(5803,81,3,'وَإِذَا ٱلْجِبَالُ سُيِّرَتْ');
INSERT INTO "quran_text" VALUES(5804,81,4,'وَإِذَا ٱلْعِشَارُ عُطِّلَتْ');
INSERT INTO "quran_text" VALUES(5805,81,5,'وَإِذَا ٱلْوُحُوشُ حُشِرَتْ');
INSERT INTO "quran_text" VALUES(5806,81,6,'وَإِذَا ٱلْبِحَارُ سُجِّرَتْ');
INSERT INTO "quran_text" VALUES(5807,81,7,'وَإِذَا ٱلنُّفُوسُ زُوِّجَتْ');
INSERT INTO "quran_text" VALUES(5808,81,8,'وَإِذَا ٱلْمَوْءُۥدَةُ سُئِلَتْ');
INSERT INTO "quran_text" VALUES(5809,81,9,'بِأَىِّ ذَنۢبٍ قُتِلَتْ');
INSERT INTO "quran_text" VALUES(5810,81,10,'وَإِذَا ٱلصُّحُفُ نُشِرَتْ');
INSERT INTO "quran_text" VALUES(5811,81,11,'وَإِذَا ٱلسَّمَآءُ كُشِطَتْ');
INSERT INTO "quran_text" VALUES(5812,81,12,'وَإِذَا ٱلْجَحِيمُ سُعِّرَتْ');
INSERT INTO "quran_text" VALUES(5813,81,13,'وَإِذَا ٱلْجَنَّةُ أُزْلِفَتْ');
INSERT INTO "quran_text" VALUES(5814,81,14,'عَلِمَتْ نَفْسٌ مَّآ أَحْضَرَتْ');
INSERT INTO "quran_text" VALUES(5815,81,15,'فَلَآ أُقْسِمُ بِٱلْخُنَّسِ');
INSERT INTO "quran_text" VALUES(5816,81,16,'ٱلْجَوَارِ ٱلْكُنَّسِ');
INSERT INTO "quran_text" VALUES(5817,81,17,'وَٱلَّيْلِ إِذَا عَسْعَسَ');
INSERT INTO "quran_text" VALUES(5818,81,18,'وَٱلصُّبْحِ إِذَا تَنَفَّسَ');
INSERT INTO "quran_text" VALUES(5819,81,19,'إِنَّهُۥ لَقَوْلُ رَسُولٍ كَرِيمٍ');
INSERT INTO "quran_text" VALUES(5820,81,20,'ذِى قُوَّةٍ عِندَ ذِى ٱلْعَرْشِ مَكِينٍ');
INSERT INTO "quran_text" VALUES(5821,81,21,'مُّطَاعٍ ثَمَّ أَمِينٍ');
INSERT INTO "quran_text" VALUES(5822,81,22,'وَمَا صَاحِبُكُم بِمَجْنُونٍ');
INSERT INTO "quran_text" VALUES(5823,81,23,'وَلَقَدْ رَءَاهُ بِٱلْأُفُقِ ٱلْمُبِينِ');
INSERT INTO "quran_text" VALUES(5824,81,24,'وَمَا هُوَ عَلَى ٱلْغَيْبِ بِضَنِينٍ');
INSERT INTO "quran_text" VALUES(5825,81,25,'وَمَا هُوَ بِقَوْلِ شَيْطَـٰنٍ رَّجِيمٍ');
INSERT INTO "quran_text" VALUES(5826,81,26,'فَأَيْنَ تَذْهَبُونَ');
INSERT INTO "quran_text" VALUES(5827,81,27,'إِنْ هُوَ إِلَّا ذِكْرٌ لِّلْعَـٰلَمِينَ');
INSERT INTO "quran_text" VALUES(5828,81,28,'لِمَن شَآءَ مِنكُمْ أَن يَسْتَقِيمَ');
INSERT INTO "quran_text" VALUES(5829,81,29,'وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُ رَبُّ ٱلْعَـٰلَمِينَ');
INSERT INTO "quran_text" VALUES(5830,82,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ إِذَا ٱلسَّمَآءُ ٱنفَطَرَتْ');
INSERT INTO "quran_text" VALUES(5831,82,2,'وَإِذَا ٱلْكَوَاكِبُ ٱنتَثَرَتْ');
INSERT INTO "quran_text" VALUES(5832,82,3,'وَإِذَا ٱلْبِحَارُ فُجِّرَتْ');
INSERT INTO "quran_text" VALUES(5833,82,4,'وَإِذَا ٱلْقُبُورُ بُعْثِرَتْ');
INSERT INTO "quran_text" VALUES(5834,82,5,'عَلِمَتْ نَفْسٌ مَّا قَدَّمَتْ وَأَخَّرَتْ');
INSERT INTO "quran_text" VALUES(5835,82,6,'يَـٰٓأَيُّهَا ٱلْإِنسَـٰنُ مَا غَرَّكَ بِرَبِّكَ ٱلْكَرِيمِ');
INSERT INTO "quran_text" VALUES(5836,82,7,'ٱلَّذِى خَلَقَكَ فَسَوَّىٰكَ فَعَدَلَكَ');
INSERT INTO "quran_text" VALUES(5837,82,8,'فِىٓ أَىِّ صُورَةٍ مَّا شَآءَ رَكَّبَكَ');
INSERT INTO "quran_text" VALUES(5838,82,9,'كَلَّا بَلْ تُكَذِّبُونَ بِٱلدِّينِ');
INSERT INTO "quran_text" VALUES(5839,82,10,'وَإِنَّ عَلَيْكُمْ لَحَـٰفِظِينَ');
INSERT INTO "quran_text" VALUES(5840,82,11,'كِرَامًا كَـٰتِبِينَ');
INSERT INTO "quran_text" VALUES(5841,82,12,'يَعْلَمُونَ مَا تَفْعَلُونَ');
INSERT INTO "quran_text" VALUES(5842,82,13,'إِنَّ ٱلْأَبْرَارَ لَفِى نَعِيمٍ');
INSERT INTO "quran_text" VALUES(5843,82,14,'وَإِنَّ ٱلْفُجَّارَ لَفِى جَحِيمٍ');
INSERT INTO "quran_text" VALUES(5844,82,15,'يَصْلَوْنَهَا يَوْمَ ٱلدِّينِ');
INSERT INTO "quran_text" VALUES(5845,82,16,'وَمَا هُمْ عَنْهَا بِغَآئِبِينَ');
INSERT INTO "quran_text" VALUES(5846,82,17,'وَمَآ أَدْرَىٰكَ مَا يَوْمُ ٱلدِّينِ');
INSERT INTO "quran_text" VALUES(5847,82,18,'ثُمَّ مَآ أَدْرَىٰكَ مَا يَوْمُ ٱلدِّينِ');
INSERT INTO "quran_text" VALUES(5848,82,19,'يَوْمَ لَا تَمْلِكُ نَفْسٌ لِّنَفْسٍ شَيْـًٔا ۖ وَٱلْأَمْرُ يَوْمَئِذٍ لِّلَّهِ');
INSERT INTO "quran_text" VALUES(5849,83,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَيْلٌ لِّلْمُطَفِّفِينَ');
INSERT INTO "quran_text" VALUES(5850,83,2,'ٱلَّذِينَ إِذَا ٱكْتَالُوا۟ عَلَى ٱلنَّاسِ يَسْتَوْفُونَ');
INSERT INTO "quran_text" VALUES(5851,83,3,'وَإِذَا كَالُوهُمْ أَو وَّزَنُوهُمْ يُخْسِرُونَ');
INSERT INTO "quran_text" VALUES(5852,83,4,'أَلَا يَظُنُّ أُو۟لَـٰٓئِكَ أَنَّهُم مَّبْعُوثُونَ');
INSERT INTO "quran_text" VALUES(5853,83,5,'لِيَوْمٍ عَظِيمٍ');
INSERT INTO "quran_text" VALUES(5854,83,6,'يَوْمَ يَقُومُ ٱلنَّاسُ لِرَبِّ ٱلْعَـٰلَمِينَ');
INSERT INTO "quran_text" VALUES(5855,83,7,'كَلَّآ إِنَّ كِتَـٰبَ ٱلْفُجَّارِ لَفِى سِجِّينٍ');
INSERT INTO "quran_text" VALUES(5856,83,8,'وَمَآ أَدْرَىٰكَ مَا سِجِّينٌ');
INSERT INTO "quran_text" VALUES(5857,83,9,'كِتَـٰبٌ مَّرْقُومٌ');
INSERT INTO "quran_text" VALUES(5858,83,10,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ');
INSERT INTO "quran_text" VALUES(5859,83,11,'ٱلَّذِينَ يُكَذِّبُونَ بِيَوْمِ ٱلدِّينِ');
INSERT INTO "quran_text" VALUES(5860,83,12,'وَمَا يُكَذِّبُ بِهِۦٓ إِلَّا كُلُّ مُعْتَدٍ أَثِيمٍ');
INSERT INTO "quran_text" VALUES(5861,83,13,'إِذَا تُتْلَىٰ عَلَيْهِ ءَايَـٰتُنَا قَالَ أَسَـٰطِيرُ ٱلْأَوَّلِينَ');
INSERT INTO "quran_text" VALUES(5862,83,14,'كَلَّا ۖ بَلْ ۜ رَانَ عَلَىٰ قُلُوبِهِم مَّا كَانُوا۟ يَكْسِبُونَ');
INSERT INTO "quran_text" VALUES(5863,83,15,'كَلَّآ إِنَّهُمْ عَن رَّبِّهِمْ يَوْمَئِذٍ لَّمَحْجُوبُونَ');
INSERT INTO "quran_text" VALUES(5864,83,16,'ثُمَّ إِنَّهُمْ لَصَالُوا۟ ٱلْجَحِيمِ');
INSERT INTO "quran_text" VALUES(5865,83,17,'ثُمَّ يُقَالُ هَـٰذَا ٱلَّذِى كُنتُم بِهِۦ تُكَذِّبُونَ');
INSERT INTO "quran_text" VALUES(5866,83,18,'كَلَّآ إِنَّ كِتَـٰبَ ٱلْأَبْرَارِ لَفِى عِلِّيِّينَ');
INSERT INTO "quran_text" VALUES(5867,83,19,'وَمَآ أَدْرَىٰكَ مَا عِلِّيُّونَ');
INSERT INTO "quran_text" VALUES(5868,83,20,'كِتَـٰبٌ مَّرْقُومٌ');
INSERT INTO "quran_text" VALUES(5869,83,21,'يَشْهَدُهُ ٱلْمُقَرَّبُونَ');
INSERT INTO "quran_text" VALUES(5870,83,22,'إِنَّ ٱلْأَبْرَارَ لَفِى نَعِيمٍ');
INSERT INTO "quran_text" VALUES(5871,83,23,'عَلَى ٱلْأَرَآئِكِ يَنظُرُونَ');
INSERT INTO "quran_text" VALUES(5872,83,24,'تَعْرِفُ فِى وُجُوهِهِمْ نَضْرَةَ ٱلنَّعِيمِ');
INSERT INTO "quran_text" VALUES(5873,83,25,'يُسْقَوْنَ مِن رَّحِيقٍ مَّخْتُومٍ');
INSERT INTO "quran_text" VALUES(5874,83,26,'خِتَـٰمُهُۥ مِسْكٌ ۚ وَفِى ذَٰلِكَ فَلْيَتَنَافَسِ ٱلْمُتَنَـٰفِسُونَ');
INSERT INTO "quran_text" VALUES(5875,83,27,'وَمِزَاجُهُۥ مِن تَسْنِيمٍ');
INSERT INTO "quran_text" VALUES(5876,83,28,'عَيْنًا يَشْرَبُ بِهَا ٱلْمُقَرَّبُونَ');
INSERT INTO "quran_text" VALUES(5877,83,29,'إِنَّ ٱلَّذِينَ أَجْرَمُوا۟ كَانُوا۟ مِنَ ٱلَّذِينَ ءَامَنُوا۟ يَضْحَكُونَ');
INSERT INTO "quran_text" VALUES(5878,83,30,'وَإِذَا مَرُّوا۟ بِهِمْ يَتَغَامَزُونَ');
INSERT INTO "quran_text" VALUES(5879,83,31,'وَإِذَا ٱنقَلَبُوٓا۟ إِلَىٰٓ أَهْلِهِمُ ٱنقَلَبُوا۟ فَكِهِينَ');
INSERT INTO "quran_text" VALUES(5880,83,32,'وَإِذَا رَأَوْهُمْ قَالُوٓا۟ إِنَّ هَـٰٓؤُلَآءِ لَضَآلُّونَ');
INSERT INTO "quran_text" VALUES(5881,83,33,'وَمَآ أُرْسِلُوا۟ عَلَيْهِمْ حَـٰفِظِينَ');
INSERT INTO "quran_text" VALUES(5882,83,34,'فَٱلْيَوْمَ ٱلَّذِينَ ءَامَنُوا۟ مِنَ ٱلْكُفَّارِ يَضْحَكُونَ');
INSERT INTO "quran_text" VALUES(5883,83,35,'عَلَى ٱلْأَرَآئِكِ يَنظُرُونَ');
INSERT INTO "quran_text" VALUES(5884,83,36,'هَلْ ثُوِّبَ ٱلْكُفَّارُ مَا كَانُوا۟ يَفْعَلُونَ');
INSERT INTO "quran_text" VALUES(5885,84,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ إِذَا ٱلسَّمَآءُ ٱنشَقَّتْ');
INSERT INTO "quran_text" VALUES(5886,84,2,'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ');
INSERT INTO "quran_text" VALUES(5887,84,3,'وَإِذَا ٱلْأَرْضُ مُدَّتْ');
INSERT INTO "quran_text" VALUES(5888,84,4,'وَأَلْقَتْ مَا فِيهَا وَتَخَلَّتْ');
INSERT INTO "quran_text" VALUES(5889,84,5,'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ');
INSERT INTO "quran_text" VALUES(5890,84,6,'يَـٰٓأَيُّهَا ٱلْإِنسَـٰنُ إِنَّكَ كَادِحٌ إِلَىٰ رَبِّكَ كَدْحًا فَمُلَـٰقِيهِ');
INSERT INTO "quran_text" VALUES(5891,84,7,'فَأَمَّا مَنْ أُوتِىَ كِتَـٰبَهُۥ بِيَمِينِهِۦ');
INSERT INTO "quran_text" VALUES(5892,84,8,'فَسَوْفَ يُحَاسَبُ حِسَابًا يَسِيرًا');
INSERT INTO "quran_text" VALUES(5893,84,9,'وَيَنقَلِبُ إِلَىٰٓ أَهْلِهِۦ مَسْرُورًا');
INSERT INTO "quran_text" VALUES(5894,84,10,'وَأَمَّا مَنْ أُوتِىَ كِتَـٰبَهُۥ وَرَآءَ ظَهْرِهِۦ');
INSERT INTO "quran_text" VALUES(5895,84,11,'فَسَوْفَ يَدْعُوا۟ ثُبُورًا');
INSERT INTO "quran_text" VALUES(5896,84,12,'وَيَصْلَىٰ سَعِيرًا');
INSERT INTO "quran_text" VALUES(5897,84,13,'إِنَّهُۥ كَانَ فِىٓ أَهْلِهِۦ مَسْرُورًا');
INSERT INTO "quran_text" VALUES(5898,84,14,'إِنَّهُۥ ظَنَّ أَن لَّن يَحُورَ');
INSERT INTO "quran_text" VALUES(5899,84,15,'بَلَىٰٓ إِنَّ رَبَّهُۥ كَانَ بِهِۦ بَصِيرًا');
INSERT INTO "quran_text" VALUES(5900,84,16,'فَلَآ أُقْسِمُ بِٱلشَّفَقِ');
INSERT INTO "quran_text" VALUES(5901,84,17,'وَٱلَّيْلِ وَمَا وَسَقَ');
INSERT INTO "quran_text" VALUES(5902,84,18,'وَٱلْقَمَرِ إِذَا ٱتَّسَقَ');
INSERT INTO "quran_text" VALUES(5903,84,19,'لَتَرْكَبُنَّ طَبَقًا عَن طَبَقٍ');
INSERT INTO "quran_text" VALUES(5904,84,20,'فَمَا لَهُمْ لَا يُؤْمِنُونَ');
INSERT INTO "quran_text" VALUES(5905,84,21,'وَإِذَا قُرِئَ عَلَيْهِمُ ٱلْقُرْءَانُ لَا يَسْجُدُونَ ۩');
INSERT INTO "quran_text" VALUES(5906,84,22,'بَلِ ٱلَّذِينَ كَفَرُوا۟ يُكَذِّبُونَ');
INSERT INTO "quran_text" VALUES(5907,84,23,'وَٱللَّهُ أَعْلَمُ بِمَا يُوعُونَ');
INSERT INTO "quran_text" VALUES(5908,84,24,'فَبَشِّرْهُم بِعَذَابٍ أَلِيمٍ');
INSERT INTO "quran_text" VALUES(5909,84,25,'إِلَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍۭ');
INSERT INTO "quran_text" VALUES(5910,85,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَٱلسَّمَآءِ ذَاتِ ٱلْبُرُوجِ');
INSERT INTO "quran_text" VALUES(5911,85,2,'وَٱلْيَوْمِ ٱلْمَوْعُودِ');
INSERT INTO "quran_text" VALUES(5912,85,3,'وَشَاهِدٍ وَمَشْهُودٍ');
INSERT INTO "quran_text" VALUES(5913,85,4,'قُتِلَ أَصْحَـٰبُ ٱلْأُخْدُودِ');
INSERT INTO "quran_text" VALUES(5914,85,5,'ٱلنَّارِ ذَاتِ ٱلْوَقُودِ');
INSERT INTO "quran_text" VALUES(5915,85,6,'إِذْ هُمْ عَلَيْهَا قُعُودٌ');
INSERT INTO "quran_text" VALUES(5916,85,7,'وَهُمْ عَلَىٰ مَا يَفْعَلُونَ بِٱلْمُؤْمِنِينَ شُهُودٌ');
INSERT INTO "quran_text" VALUES(5917,85,8,'وَمَا نَقَمُوا۟ مِنْهُمْ إِلَّآ أَن يُؤْمِنُوا۟ بِٱللَّهِ ٱلْعَزِيزِ ٱلْحَمِيدِ');
INSERT INTO "quran_text" VALUES(5918,85,9,'ٱلَّذِى لَهُۥ مُلْكُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ وَٱللَّهُ عَلَىٰ كُلِّ شَىْءٍ شَهِيدٌ');
INSERT INTO "quran_text" VALUES(5919,85,10,'إِنَّ ٱلَّذِينَ فَتَنُوا۟ ٱلْمُؤْمِنِينَ وَٱلْمُؤْمِنَـٰتِ ثُمَّ لَمْ يَتُوبُوا۟ فَلَهُمْ عَذَابُ جَهَنَّمَ وَلَهُمْ عَذَابُ ٱلْحَرِيقِ');
INSERT INTO "quran_text" VALUES(5920,85,11,'إِنَّ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَهُمْ جَنَّـٰتٌ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ ۚ ذَٰلِكَ ٱلْفَوْزُ ٱلْكَبِيرُ');
INSERT INTO "quran_text" VALUES(5921,85,12,'إِنَّ بَطْشَ رَبِّكَ لَشَدِيدٌ');
INSERT INTO "quran_text" VALUES(5922,85,13,'إِنَّهُۥ هُوَ يُبْدِئُ وَيُعِيدُ');
INSERT INTO "quran_text" VALUES(5923,85,14,'وَهُوَ ٱلْغَفُورُ ٱلْوَدُودُ');
INSERT INTO "quran_text" VALUES(5924,85,15,'ذُو ٱلْعَرْشِ ٱلْمَجِيدُ');
INSERT INTO "quran_text" VALUES(5925,85,16,'فَعَّالٌ لِّمَا يُرِيدُ');
INSERT INTO "quran_text" VALUES(5926,85,17,'هَلْ أَتَىٰكَ حَدِيثُ ٱلْجُنُودِ');
INSERT INTO "quran_text" VALUES(5927,85,18,'فِرْعَوْنَ وَثَمُودَ');
INSERT INTO "quran_text" VALUES(5928,85,19,'بَلِ ٱلَّذِينَ كَفَرُوا۟ فِى تَكْذِيبٍ');
INSERT INTO "quran_text" VALUES(5929,85,20,'وَٱللَّهُ مِن وَرَآئِهِم مُّحِيطٌۢ');
INSERT INTO "quran_text" VALUES(5930,85,21,'بَلْ هُوَ قُرْءَانٌ مَّجِيدٌ');
INSERT INTO "quran_text" VALUES(5931,85,22,'فِى لَوْحٍ مَّحْفُوظٍۭ');
INSERT INTO "quran_text" VALUES(5932,86,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَٱلسَّمَآءِ وَٱلطَّارِقِ');
INSERT INTO "quran_text" VALUES(5933,86,2,'وَمَآ أَدْرَىٰكَ مَا ٱلطَّارِقُ');
INSERT INTO "quran_text" VALUES(5934,86,3,'ٱلنَّجْمُ ٱلثَّاقِبُ');
INSERT INTO "quran_text" VALUES(5935,86,4,'إِن كُلُّ نَفْسٍ لَّمَّا عَلَيْهَا حَافِظٌ');
INSERT INTO "quran_text" VALUES(5936,86,5,'فَلْيَنظُرِ ٱلْإِنسَـٰنُ مِمَّ خُلِقَ');
INSERT INTO "quran_text" VALUES(5937,86,6,'خُلِقَ مِن مَّآءٍ دَافِقٍ');
INSERT INTO "quran_text" VALUES(5938,86,7,'يَخْرُجُ مِنۢ بَيْنِ ٱلصُّلْبِ وَٱلتَّرَآئِبِ');
INSERT INTO "quran_text" VALUES(5939,86,8,'إِنَّهُۥ عَلَىٰ رَجْعِهِۦ لَقَادِرٌ');
INSERT INTO "quran_text" VALUES(5940,86,9,'يَوْمَ تُبْلَى ٱلسَّرَآئِرُ');
INSERT INTO "quran_text" VALUES(5941,86,10,'فَمَا لَهُۥ مِن قُوَّةٍ وَلَا نَاصِرٍ');
INSERT INTO "quran_text" VALUES(5942,86,11,'وَٱلسَّمَآءِ ذَاتِ ٱلرَّجْعِ');
INSERT INTO "quran_text" VALUES(5943,86,12,'وَٱلْأَرْضِ ذَاتِ ٱلصَّدْعِ');
INSERT INTO "quran_text" VALUES(5944,86,13,'إِنَّهُۥ لَقَوْلٌ فَصْلٌ');
INSERT INTO "quran_text" VALUES(5945,86,14,'وَمَا هُوَ بِٱلْهَزْلِ');
INSERT INTO "quran_text" VALUES(5946,86,15,'إِنَّهُمْ يَكِيدُونَ كَيْدًا');
INSERT INTO "quran_text" VALUES(5947,86,16,'وَأَكِيدُ كَيْدًا');
INSERT INTO "quran_text" VALUES(5948,86,17,'فَمَهِّلِ ٱلْكَـٰفِرِينَ أَمْهِلْهُمْ رُوَيْدًۢا');
INSERT INTO "quran_text" VALUES(5949,87,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ سَبِّحِ ٱسْمَ رَبِّكَ ٱلْأَعْلَى');
INSERT INTO "quran_text" VALUES(5950,87,2,'ٱلَّذِى خَلَقَ فَسَوَّىٰ');
INSERT INTO "quran_text" VALUES(5951,87,3,'وَٱلَّذِى قَدَّرَ فَهَدَىٰ');
INSERT INTO "quran_text" VALUES(5952,87,4,'وَٱلَّذِىٓ أَخْرَجَ ٱلْمَرْعَىٰ');
INSERT INTO "quran_text" VALUES(5953,87,5,'فَجَعَلَهُۥ غُثَآءً أَحْوَىٰ');
INSERT INTO "quran_text" VALUES(5954,87,6,'سَنُقْرِئُكَ فَلَا تَنسَىٰٓ');
INSERT INTO "quran_text" VALUES(5955,87,7,'إِلَّا مَا شَآءَ ٱللَّهُ ۚ إِنَّهُۥ يَعْلَمُ ٱلْجَهْرَ وَمَا يَخْفَىٰ');
INSERT INTO "quran_text" VALUES(5956,87,8,'وَنُيَسِّرُكَ لِلْيُسْرَىٰ');
INSERT INTO "quran_text" VALUES(5957,87,9,'فَذَكِّرْ إِن نَّفَعَتِ ٱلذِّكْرَىٰ');
INSERT INTO "quran_text" VALUES(5958,87,10,'سَيَذَّكَّرُ مَن يَخْشَىٰ');
INSERT INTO "quran_text" VALUES(5959,87,11,'وَيَتَجَنَّبُهَا ٱلْأَشْقَى');
INSERT INTO "quran_text" VALUES(5960,87,12,'ٱلَّذِى يَصْلَى ٱلنَّارَ ٱلْكُبْرَىٰ');
INSERT INTO "quran_text" VALUES(5961,87,13,'ثُمَّ لَا يَمُوتُ فِيهَا وَلَا يَحْيَىٰ');
INSERT INTO "quran_text" VALUES(5962,87,14,'قَدْ أَفْلَحَ مَن تَزَكَّىٰ');
INSERT INTO "quran_text" VALUES(5963,87,15,'وَذَكَرَ ٱسْمَ رَبِّهِۦ فَصَلَّىٰ');
INSERT INTO "quran_text" VALUES(5964,87,16,'بَلْ تُؤْثِرُونَ ٱلْحَيَوٰةَ ٱلدُّنْيَا');
INSERT INTO "quran_text" VALUES(5965,87,17,'وَٱلْـَٔاخِرَةُ خَيْرٌ وَأَبْقَىٰٓ');
INSERT INTO "quran_text" VALUES(5966,87,18,'إِنَّ هَـٰذَا لَفِى ٱلصُّحُفِ ٱلْأُولَىٰ');
INSERT INTO "quran_text" VALUES(5967,87,19,'صُحُفِ إِبْرَٰهِيمَ وَمُوسَىٰ');
INSERT INTO "quran_text" VALUES(5968,88,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ هَلْ أَتَىٰكَ حَدِيثُ ٱلْغَـٰشِيَةِ');
INSERT INTO "quran_text" VALUES(5969,88,2,'وُجُوهٌ يَوْمَئِذٍ خَـٰشِعَةٌ');
INSERT INTO "quran_text" VALUES(5970,88,3,'عَامِلَةٌ نَّاصِبَةٌ');
INSERT INTO "quran_text" VALUES(5971,88,4,'تَصْلَىٰ نَارًا حَامِيَةً');
INSERT INTO "quran_text" VALUES(5972,88,5,'تُسْقَىٰ مِنْ عَيْنٍ ءَانِيَةٍ');
INSERT INTO "quran_text" VALUES(5973,88,6,'لَّيْسَ لَهُمْ طَعَامٌ إِلَّا مِن ضَرِيعٍ');
INSERT INTO "quran_text" VALUES(5974,88,7,'لَّا يُسْمِنُ وَلَا يُغْنِى مِن جُوعٍ');
INSERT INTO "quran_text" VALUES(5975,88,8,'وُجُوهٌ يَوْمَئِذٍ نَّاعِمَةٌ');
INSERT INTO "quran_text" VALUES(5976,88,9,'لِّسَعْيِهَا رَاضِيَةٌ');
INSERT INTO "quran_text" VALUES(5977,88,10,'فِى جَنَّةٍ عَالِيَةٍ');
INSERT INTO "quran_text" VALUES(5978,88,11,'لَّا تَسْمَعُ فِيهَا لَـٰغِيَةً');
INSERT INTO "quran_text" VALUES(5979,88,12,'فِيهَا عَيْنٌ جَارِيَةٌ');
INSERT INTO "quran_text" VALUES(5980,88,13,'فِيهَا سُرُرٌ مَّرْفُوعَةٌ');
INSERT INTO "quran_text" VALUES(5981,88,14,'وَأَكْوَابٌ مَّوْضُوعَةٌ');
INSERT INTO "quran_text" VALUES(5982,88,15,'وَنَمَارِقُ مَصْفُوفَةٌ');
INSERT INTO "quran_text" VALUES(5983,88,16,'وَزَرَابِىُّ مَبْثُوثَةٌ');
INSERT INTO "quran_text" VALUES(5984,88,17,'أَفَلَا يَنظُرُونَ إِلَى ٱلْإِبِلِ كَيْفَ خُلِقَتْ');
INSERT INTO "quran_text" VALUES(5985,88,18,'وَإِلَى ٱلسَّمَآءِ كَيْفَ رُفِعَتْ');
INSERT INTO "quran_text" VALUES(5986,88,19,'وَإِلَى ٱلْجِبَالِ كَيْفَ نُصِبَتْ');
INSERT INTO "quran_text" VALUES(5987,88,20,'وَإِلَى ٱلْأَرْضِ كَيْفَ سُطِحَتْ');
INSERT INTO "quran_text" VALUES(5988,88,21,'فَذَكِّرْ إِنَّمَآ أَنتَ مُذَكِّرٌ');
INSERT INTO "quran_text" VALUES(5989,88,22,'لَّسْتَ عَلَيْهِم بِمُصَيْطِرٍ');
INSERT INTO "quran_text" VALUES(5990,88,23,'إِلَّا مَن تَوَلَّىٰ وَكَفَرَ');
INSERT INTO "quran_text" VALUES(5991,88,24,'فَيُعَذِّبُهُ ٱللَّهُ ٱلْعَذَابَ ٱلْأَكْبَرَ');
INSERT INTO "quran_text" VALUES(5992,88,25,'إِنَّ إِلَيْنَآ إِيَابَهُمْ');
INSERT INTO "quran_text" VALUES(5993,88,26,'ثُمَّ إِنَّ عَلَيْنَا حِسَابَهُم');
INSERT INTO "quran_text" VALUES(5994,89,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَٱلْفَجْرِ');
INSERT INTO "quran_text" VALUES(5995,89,2,'وَلَيَالٍ عَشْرٍ');
INSERT INTO "quran_text" VALUES(5996,89,3,'وَٱلشَّفْعِ وَٱلْوَتْرِ');
INSERT INTO "quran_text" VALUES(5997,89,4,'وَٱلَّيْلِ إِذَا يَسْرِ');
INSERT INTO "quran_text" VALUES(5998,89,5,'هَلْ فِى ذَٰلِكَ قَسَمٌ لِّذِى حِجْرٍ');
INSERT INTO "quran_text" VALUES(5999,89,6,'أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعَادٍ');
INSERT INTO "quran_text" VALUES(6000,89,7,'إِرَمَ ذَاتِ ٱلْعِمَادِ');
INSERT INTO "quran_text" VALUES(6001,89,8,'ٱلَّتِى لَمْ يُخْلَقْ مِثْلُهَا فِى ٱلْبِلَـٰدِ');
INSERT INTO "quran_text" VALUES(6002,89,9,'وَثَمُودَ ٱلَّذِينَ جَابُوا۟ ٱلصَّخْرَ بِٱلْوَادِ');
INSERT INTO "quran_text" VALUES(6003,89,10,'وَفِرْعَوْنَ ذِى ٱلْأَوْتَادِ');
INSERT INTO "quran_text" VALUES(6004,89,11,'ٱلَّذِينَ طَغَوْا۟ فِى ٱلْبِلَـٰدِ');
INSERT INTO "quran_text" VALUES(6005,89,12,'فَأَكْثَرُوا۟ فِيهَا ٱلْفَسَادَ');
INSERT INTO "quran_text" VALUES(6006,89,13,'فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذَابٍ');
INSERT INTO "quran_text" VALUES(6007,89,14,'إِنَّ رَبَّكَ لَبِٱلْمِرْصَادِ');
INSERT INTO "quran_text" VALUES(6008,89,15,'فَأَمَّا ٱلْإِنسَـٰنُ إِذَا مَا ٱبْتَلَىٰهُ رَبُّهُۥ فَأَكْرَمَهُۥ وَنَعَّمَهُۥ فَيَقُولُ رَبِّىٓ أَكْرَمَنِ');
INSERT INTO "quran_text" VALUES(6009,89,16,'وَأَمَّآ إِذَا مَا ٱبْتَلَىٰهُ فَقَدَرَ عَلَيْهِ رِزْقَهُۥ فَيَقُولُ رَبِّىٓ أَهَـٰنَنِ');
INSERT INTO "quran_text" VALUES(6010,89,17,'كَلَّا ۖ بَل لَّا تُكْرِمُونَ ٱلْيَتِيمَ');
INSERT INTO "quran_text" VALUES(6011,89,18,'وَلَا تَحَـٰٓضُّونَ عَلَىٰ طَعَامِ ٱلْمِسْكِينِ');
INSERT INTO "quran_text" VALUES(6012,89,19,'وَتَأْكُلُونَ ٱلتُّرَاثَ أَكْلًا لَّمًّا');
INSERT INTO "quran_text" VALUES(6013,89,20,'وَتُحِبُّونَ ٱلْمَالَ حُبًّا جَمًّا');
INSERT INTO "quran_text" VALUES(6014,89,21,'كَلَّآ إِذَا دُكَّتِ ٱلْأَرْضُ دَكًّا دَكًّا');
INSERT INTO "quran_text" VALUES(6015,89,22,'وَجَآءَ رَبُّكَ وَٱلْمَلَكُ صَفًّا صَفًّا');
INSERT INTO "quran_text" VALUES(6016,89,23,'وَجِا۟ىٓءَ يَوْمَئِذٍۭ بِجَهَنَّمَ ۚ يَوْمَئِذٍ يَتَذَكَّرُ ٱلْإِنسَـٰنُ وَأَنَّىٰ لَهُ ٱلذِّكْرَىٰ');
INSERT INTO "quran_text" VALUES(6017,89,24,'يَقُولُ يَـٰلَيْتَنِى قَدَّمْتُ لِحَيَاتِى');
INSERT INTO "quran_text" VALUES(6018,89,25,'فَيَوْمَئِذٍ لَّا يُعَذِّبُ عَذَابَهُۥٓ أَحَدٌ');
INSERT INTO "quran_text" VALUES(6019,89,26,'وَلَا يُوثِقُ وَثَاقَهُۥٓ أَحَدٌ');
INSERT INTO "quran_text" VALUES(6020,89,27,'يَـٰٓأَيَّتُهَا ٱلنَّفْسُ ٱلْمُطْمَئِنَّةُ');
INSERT INTO "quran_text" VALUES(6021,89,28,'ٱرْجِعِىٓ إِلَىٰ رَبِّكِ رَاضِيَةً مَّرْضِيَّةً');
INSERT INTO "quran_text" VALUES(6022,89,29,'فَٱدْخُلِى فِى عِبَـٰدِى');
INSERT INTO "quran_text" VALUES(6023,89,30,'وَٱدْخُلِى جَنَّتِى');
INSERT INTO "quran_text" VALUES(6024,90,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ لَآ أُقْسِمُ بِهَـٰذَا ٱلْبَلَدِ');
INSERT INTO "quran_text" VALUES(6025,90,2,'وَأَنتَ حِلٌّۢ بِهَـٰذَا ٱلْبَلَدِ');
INSERT INTO "quran_text" VALUES(6026,90,3,'وَوَالِدٍ وَمَا وَلَدَ');
INSERT INTO "quran_text" VALUES(6027,90,4,'لَقَدْ خَلَقْنَا ٱلْإِنسَـٰنَ فِى كَبَدٍ');
INSERT INTO "quran_text" VALUES(6028,90,5,'أَيَحْسَبُ أَن لَّن يَقْدِرَ عَلَيْهِ أَحَدٌ');
INSERT INTO "quran_text" VALUES(6029,90,6,'يَقُولُ أَهْلَكْتُ مَالًا لُّبَدًا');
INSERT INTO "quran_text" VALUES(6030,90,7,'أَيَحْسَبُ أَن لَّمْ يَرَهُۥٓ أَحَدٌ');
INSERT INTO "quran_text" VALUES(6031,90,8,'أَلَمْ نَجْعَل لَّهُۥ عَيْنَيْنِ');
INSERT INTO "quran_text" VALUES(6032,90,9,'وَلِسَانًا وَشَفَتَيْنِ');
INSERT INTO "quran_text" VALUES(6033,90,10,'وَهَدَيْنَـٰهُ ٱلنَّجْدَيْنِ');
INSERT INTO "quran_text" VALUES(6034,90,11,'فَلَا ٱقْتَحَمَ ٱلْعَقَبَةَ');
INSERT INTO "quran_text" VALUES(6035,90,12,'وَمَآ أَدْرَىٰكَ مَا ٱلْعَقَبَةُ');
INSERT INTO "quran_text" VALUES(6036,90,13,'فَكُّ رَقَبَةٍ');
INSERT INTO "quran_text" VALUES(6037,90,14,'أَوْ إِطْعَـٰمٌ فِى يَوْمٍ ذِى مَسْغَبَةٍ');
INSERT INTO "quran_text" VALUES(6038,90,15,'يَتِيمًا ذَا مَقْرَبَةٍ');
INSERT INTO "quran_text" VALUES(6039,90,16,'أَوْ مِسْكِينًا ذَا مَتْرَبَةٍ');
INSERT INTO "quran_text" VALUES(6040,90,17,'ثُمَّ كَانَ مِنَ ٱلَّذِينَ ءَامَنُوا۟ وَتَوَاصَوْا۟ بِٱلصَّبْرِ وَتَوَاصَوْا۟ بِٱلْمَرْحَمَةِ');
INSERT INTO "quran_text" VALUES(6041,90,18,'أُو۟لَـٰٓئِكَ أَصْحَـٰبُ ٱلْمَيْمَنَةِ');
INSERT INTO "quran_text" VALUES(6042,90,19,'وَٱلَّذِينَ كَفَرُوا۟ بِـَٔايَـٰتِنَا هُمْ أَصْحَـٰبُ ٱلْمَشْـَٔمَةِ');
INSERT INTO "quran_text" VALUES(6043,90,20,'عَلَيْهِمْ نَارٌ مُّؤْصَدَةٌۢ');
INSERT INTO "quran_text" VALUES(6044,91,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَٱلشَّمْسِ وَضُحَىٰهَا');
INSERT INTO "quran_text" VALUES(6045,91,2,'وَٱلْقَمَرِ إِذَا تَلَىٰهَا');
INSERT INTO "quran_text" VALUES(6046,91,3,'وَٱلنَّهَارِ إِذَا جَلَّىٰهَا');
INSERT INTO "quran_text" VALUES(6047,91,4,'وَٱلَّيْلِ إِذَا يَغْشَىٰهَا');
INSERT INTO "quran_text" VALUES(6048,91,5,'وَٱلسَّمَآءِ وَمَا بَنَىٰهَا');
INSERT INTO "quran_text" VALUES(6049,91,6,'وَٱلْأَرْضِ وَمَا طَحَىٰهَا');
INSERT INTO "quran_text" VALUES(6050,91,7,'وَنَفْسٍ وَمَا سَوَّىٰهَا');
INSERT INTO "quran_text" VALUES(6051,91,8,'فَأَلْهَمَهَا فُجُورَهَا وَتَقْوَىٰهَا');
INSERT INTO "quran_text" VALUES(6052,91,9,'قَدْ أَفْلَحَ مَن زَكَّىٰهَا');
INSERT INTO "quran_text" VALUES(6053,91,10,'وَقَدْ خَابَ مَن دَسَّىٰهَا');
INSERT INTO "quran_text" VALUES(6054,91,11,'كَذَّبَتْ ثَمُودُ بِطَغْوَىٰهَآ');
INSERT INTO "quran_text" VALUES(6055,91,12,'إِذِ ٱنۢبَعَثَ أَشْقَىٰهَا');
INSERT INTO "quran_text" VALUES(6056,91,13,'فَقَالَ لَهُمْ رَسُولُ ٱللَّهِ نَاقَةَ ٱللَّهِ وَسُقْيَـٰهَا');
INSERT INTO "quran_text" VALUES(6057,91,14,'فَكَذَّبُوهُ فَعَقَرُوهَا فَدَمْدَمَ عَلَيْهِمْ رَبُّهُم بِذَنۢبِهِمْ فَسَوَّىٰهَا');
INSERT INTO "quran_text" VALUES(6058,91,15,'وَلَا يَخَافُ عُقْبَـٰهَا');
INSERT INTO "quran_text" VALUES(6059,92,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَٱلَّيْلِ إِذَا يَغْشَىٰ');
INSERT INTO "quran_text" VALUES(6060,92,2,'وَٱلنَّهَارِ إِذَا تَجَلَّىٰ');
INSERT INTO "quran_text" VALUES(6061,92,3,'وَمَا خَلَقَ ٱلذَّكَرَ وَٱلْأُنثَىٰٓ');
INSERT INTO "quran_text" VALUES(6062,92,4,'إِنَّ سَعْيَكُمْ لَشَتَّىٰ');
INSERT INTO "quran_text" VALUES(6063,92,5,'فَأَمَّا مَنْ أَعْطَىٰ وَٱتَّقَىٰ');
INSERT INTO "quran_text" VALUES(6064,92,6,'وَصَدَّقَ بِٱلْحُسْنَىٰ');
INSERT INTO "quran_text" VALUES(6065,92,7,'فَسَنُيَسِّرُهُۥ لِلْيُسْرَىٰ');
INSERT INTO "quran_text" VALUES(6066,92,8,'وَأَمَّا مَنۢ بَخِلَ وَٱسْتَغْنَىٰ');
INSERT INTO "quran_text" VALUES(6067,92,9,'وَكَذَّبَ بِٱلْحُسْنَىٰ');
INSERT INTO "quran_text" VALUES(6068,92,10,'فَسَنُيَسِّرُهُۥ لِلْعُسْرَىٰ');
INSERT INTO "quran_text" VALUES(6069,92,11,'وَمَا يُغْنِى عَنْهُ مَالُهُۥٓ إِذَا تَرَدَّىٰٓ');
INSERT INTO "quran_text" VALUES(6070,92,12,'إِنَّ عَلَيْنَا لَلْهُدَىٰ');
INSERT INTO "quran_text" VALUES(6071,92,13,'وَإِنَّ لَنَا لَلْـَٔاخِرَةَ وَٱلْأُولَىٰ');
INSERT INTO "quran_text" VALUES(6072,92,14,'فَأَنذَرْتُكُمْ نَارًا تَلَظَّىٰ');
INSERT INTO "quran_text" VALUES(6073,92,15,'لَا يَصْلَىٰهَآ إِلَّا ٱلْأَشْقَى');
INSERT INTO "quran_text" VALUES(6074,92,16,'ٱلَّذِى كَذَّبَ وَتَوَلَّىٰ');
INSERT INTO "quran_text" VALUES(6075,92,17,'وَسَيُجَنَّبُهَا ٱلْأَتْقَى');
INSERT INTO "quran_text" VALUES(6076,92,18,'ٱلَّذِى يُؤْتِى مَالَهُۥ يَتَزَكَّىٰ');
INSERT INTO "quran_text" VALUES(6077,92,19,'وَمَا لِأَحَدٍ عِندَهُۥ مِن نِّعْمَةٍ تُجْزَىٰٓ');
INSERT INTO "quran_text" VALUES(6078,92,20,'إِلَّا ٱبْتِغَآءَ وَجْهِ رَبِّهِ ٱلْأَعْلَىٰ');
INSERT INTO "quran_text" VALUES(6079,92,21,'وَلَسَوْفَ يَرْضَىٰ');
INSERT INTO "quran_text" VALUES(6080,93,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَٱلضُّحَىٰ');
INSERT INTO "quran_text" VALUES(6081,93,2,'وَٱلَّيْلِ إِذَا سَجَىٰ');
INSERT INTO "quran_text" VALUES(6082,93,3,'مَا وَدَّعَكَ رَبُّكَ وَمَا قَلَىٰ');
INSERT INTO "quran_text" VALUES(6083,93,4,'وَلَلْـَٔاخِرَةُ خَيْرٌ لَّكَ مِنَ ٱلْأُولَىٰ');
INSERT INTO "quran_text" VALUES(6084,93,5,'وَلَسَوْفَ يُعْطِيكَ رَبُّكَ فَتَرْضَىٰٓ');
INSERT INTO "quran_text" VALUES(6085,93,6,'أَلَمْ يَجِدْكَ يَتِيمًا فَـَٔاوَىٰ');
INSERT INTO "quran_text" VALUES(6086,93,7,'وَوَجَدَكَ ضَآلًّا فَهَدَىٰ');
INSERT INTO "quran_text" VALUES(6087,93,8,'وَوَجَدَكَ عَآئِلًا فَأَغْنَىٰ');
INSERT INTO "quran_text" VALUES(6088,93,9,'فَأَمَّا ٱلْيَتِيمَ فَلَا تَقْهَرْ');
INSERT INTO "quran_text" VALUES(6089,93,10,'وَأَمَّا ٱلسَّآئِلَ فَلَا تَنْهَرْ');
INSERT INTO "quran_text" VALUES(6090,93,11,'وَأَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ');
INSERT INTO "quran_text" VALUES(6091,94,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ أَلَمْ نَشْرَحْ لَكَ صَدْرَكَ');
INSERT INTO "quran_text" VALUES(6092,94,2,'وَوَضَعْنَا عَنكَ وِزْرَكَ');
INSERT INTO "quran_text" VALUES(6093,94,3,'ٱلَّذِىٓ أَنقَضَ ظَهْرَكَ');
INSERT INTO "quran_text" VALUES(6094,94,4,'وَرَفَعْنَا لَكَ ذِكْرَكَ');
INSERT INTO "quran_text" VALUES(6095,94,5,'فَإِنَّ مَعَ ٱلْعُسْرِ يُسْرًا');
INSERT INTO "quran_text" VALUES(6096,94,6,'إِنَّ مَعَ ٱلْعُسْرِ يُسْرًا');
INSERT INTO "quran_text" VALUES(6097,94,7,'فَإِذَا فَرَغْتَ فَٱنصَبْ');
INSERT INTO "quran_text" VALUES(6098,94,8,'وَإِلَىٰ رَبِّكَ فَٱرْغَب');
INSERT INTO "quran_text" VALUES(6099,95,1,'بِّسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَٱلتِّينِ وَٱلزَّيْتُونِ');
INSERT INTO "quran_text" VALUES(6100,95,2,'وَطُورِ سِينِينَ');
INSERT INTO "quran_text" VALUES(6101,95,3,'وَهَـٰذَا ٱلْبَلَدِ ٱلْأَمِينِ');
INSERT INTO "quran_text" VALUES(6102,95,4,'لَقَدْ خَلَقْنَا ٱلْإِنسَـٰنَ فِىٓ أَحْسَنِ تَقْوِيمٍ');
INSERT INTO "quran_text" VALUES(6103,95,5,'ثُمَّ رَدَدْنَـٰهُ أَسْفَلَ سَـٰفِلِينَ');
INSERT INTO "quran_text" VALUES(6104,95,6,'إِلَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ فَلَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ');
INSERT INTO "quran_text" VALUES(6105,95,7,'فَمَا يُكَذِّبُكَ بَعْدُ بِٱلدِّينِ');
INSERT INTO "quran_text" VALUES(6106,95,8,'أَلَيْسَ ٱللَّهُ بِأَحْكَمِ ٱلْحَـٰكِمِينَ');
INSERT INTO "quran_text" VALUES(6107,96,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ ٱقْرَأْ بِٱسْمِ رَبِّكَ ٱلَّذِى خَلَقَ');
INSERT INTO "quran_text" VALUES(6108,96,2,'خَلَقَ ٱلْإِنسَـٰنَ مِنْ عَلَقٍ');
INSERT INTO "quran_text" VALUES(6109,96,3,'ٱقْرَأْ وَرَبُّكَ ٱلْأَكْرَمُ');
INSERT INTO "quran_text" VALUES(6110,96,4,'ٱلَّذِى عَلَّمَ بِٱلْقَلَمِ');
INSERT INTO "quran_text" VALUES(6111,96,5,'عَلَّمَ ٱلْإِنسَـٰنَ مَا لَمْ يَعْلَمْ');
INSERT INTO "quran_text" VALUES(6112,96,6,'كَلَّآ إِنَّ ٱلْإِنسَـٰنَ لَيَطْغَىٰٓ');
INSERT INTO "quran_text" VALUES(6113,96,7,'أَن رَّءَاهُ ٱسْتَغْنَىٰٓ');
INSERT INTO "quran_text" VALUES(6114,96,8,'إِنَّ إِلَىٰ رَبِّكَ ٱلرُّجْعَىٰٓ');
INSERT INTO "quran_text" VALUES(6115,96,9,'أَرَءَيْتَ ٱلَّذِى يَنْهَىٰ');
INSERT INTO "quran_text" VALUES(6116,96,10,'عَبْدًا إِذَا صَلَّىٰٓ');
INSERT INTO "quran_text" VALUES(6117,96,11,'أَرَءَيْتَ إِن كَانَ عَلَى ٱلْهُدَىٰٓ');
INSERT INTO "quran_text" VALUES(6118,96,12,'أَوْ أَمَرَ بِٱلتَّقْوَىٰٓ');
INSERT INTO "quran_text" VALUES(6119,96,13,'أَرَءَيْتَ إِن كَذَّبَ وَتَوَلَّىٰٓ');
INSERT INTO "quran_text" VALUES(6120,96,14,'أَلَمْ يَعْلَم بِأَنَّ ٱللَّهَ يَرَىٰ');
INSERT INTO "quran_text" VALUES(6121,96,15,'كَلَّا لَئِن لَّمْ يَنتَهِ لَنَسْفَعًۢا بِٱلنَّاصِيَةِ');
INSERT INTO "quran_text" VALUES(6122,96,16,'نَاصِيَةٍ كَـٰذِبَةٍ خَاطِئَةٍ');
INSERT INTO "quran_text" VALUES(6123,96,17,'فَلْيَدْعُ نَادِيَهُۥ');
INSERT INTO "quran_text" VALUES(6124,96,18,'سَنَدْعُ ٱلزَّبَانِيَةَ');
INSERT INTO "quran_text" VALUES(6125,96,19,'كَلَّا لَا تُطِعْهُ وَٱسْجُدْ وَٱقْتَرِب ۩');
INSERT INTO "quran_text" VALUES(6126,97,1,'بِّسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ إِنَّآ أَنزَلْنَـٰهُ فِى لَيْلَةِ ٱلْقَدْرِ');
INSERT INTO "quran_text" VALUES(6127,97,2,'وَمَآ أَدْرَىٰكَ مَا لَيْلَةُ ٱلْقَدْرِ');
INSERT INTO "quran_text" VALUES(6128,97,3,'لَيْلَةُ ٱلْقَدْرِ خَيْرٌ مِّنْ أَلْفِ شَهْرٍ');
INSERT INTO "quran_text" VALUES(6129,97,4,'تَنَزَّلُ ٱلْمَلَـٰٓئِكَةُ وَٱلرُّوحُ فِيهَا بِإِذْنِ رَبِّهِم مِّن كُلِّ أَمْرٍ');
INSERT INTO "quran_text" VALUES(6130,97,5,'سَلَـٰمٌ هِىَ حَتَّىٰ مَطْلَعِ ٱلْفَجْرِ');
INSERT INTO "quran_text" VALUES(6131,98,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ لَمْ يَكُنِ ٱلَّذِينَ كَفَرُوا۟ مِنْ أَهْلِ ٱلْكِتَـٰبِ وَٱلْمُشْرِكِينَ مُنفَكِّينَ حَتَّىٰ تَأْتِيَهُمُ ٱلْبَيِّنَةُ');
INSERT INTO "quran_text" VALUES(6132,98,2,'رَسُولٌ مِّنَ ٱللَّهِ يَتْلُوا۟ صُحُفًا مُّطَهَّرَةً');
INSERT INTO "quran_text" VALUES(6133,98,3,'فِيهَا كُتُبٌ قَيِّمَةٌ');
INSERT INTO "quran_text" VALUES(6134,98,4,'وَمَا تَفَرَّقَ ٱلَّذِينَ أُوتُوا۟ ٱلْكِتَـٰبَ إِلَّا مِنۢ بَعْدِ مَا جَآءَتْهُمُ ٱلْبَيِّنَةُ');
INSERT INTO "quran_text" VALUES(6135,98,5,'وَمَآ أُمِرُوٓا۟ إِلَّا لِيَعْبُدُوا۟ ٱللَّهَ مُخْلِصِينَ لَهُ ٱلدِّينَ حُنَفَآءَ وَيُقِيمُوا۟ ٱلصَّلَوٰةَ وَيُؤْتُوا۟ ٱلزَّكَوٰةَ ۚ وَذَٰلِكَ دِينُ ٱلْقَيِّمَةِ');
INSERT INTO "quran_text" VALUES(6136,98,6,'إِنَّ ٱلَّذِينَ كَفَرُوا۟ مِنْ أَهْلِ ٱلْكِتَـٰبِ وَٱلْمُشْرِكِينَ فِى نَارِ جَهَنَّمَ خَـٰلِدِينَ فِيهَآ ۚ أُو۟لَـٰٓئِكَ هُمْ شَرُّ ٱلْبَرِيَّةِ');
INSERT INTO "quran_text" VALUES(6137,98,7,'إِنَّ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ أُو۟لَـٰٓئِكَ هُمْ خَيْرُ ٱلْبَرِيَّةِ');
INSERT INTO "quran_text" VALUES(6138,98,8,'جَزَآؤُهُمْ عِندَ رَبِّهِمْ جَنَّـٰتُ عَدْنٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ خَـٰلِدِينَ فِيهَآ أَبَدًا ۖ رَّضِىَ ٱللَّهُ عَنْهُمْ وَرَضُوا۟ عَنْهُ ۚ ذَٰلِكَ لِمَنْ خَشِىَ رَبَّهُۥ');
INSERT INTO "quran_text" VALUES(6139,99,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ إِذَا زُلْزِلَتِ ٱلْأَرْضُ زِلْزَالَهَا');
INSERT INTO "quran_text" VALUES(6140,99,2,'وَأَخْرَجَتِ ٱلْأَرْضُ أَثْقَالَهَا');
INSERT INTO "quran_text" VALUES(6141,99,3,'وَقَالَ ٱلْإِنسَـٰنُ مَا لَهَا');
INSERT INTO "quran_text" VALUES(6142,99,4,'يَوْمَئِذٍ تُحَدِّثُ أَخْبَارَهَا');
INSERT INTO "quran_text" VALUES(6143,99,5,'بِأَنَّ رَبَّكَ أَوْحَىٰ لَهَا');
INSERT INTO "quran_text" VALUES(6144,99,6,'يَوْمَئِذٍ يَصْدُرُ ٱلنَّاسُ أَشْتَاتًا لِّيُرَوْا۟ أَعْمَـٰلَهُمْ');
INSERT INTO "quran_text" VALUES(6145,99,7,'فَمَن يَعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْرًا يَرَهُۥ');
INSERT INTO "quran_text" VALUES(6146,99,8,'وَمَن يَعْمَلْ مِثْقَالَ ذَرَّةٍ شَرًّا يَرَهُۥ');
INSERT INTO "quran_text" VALUES(6147,100,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَٱلْعَـٰدِيَـٰتِ ضَبْحًا');
INSERT INTO "quran_text" VALUES(6148,100,2,'فَٱلْمُورِيَـٰتِ قَدْحًا');
INSERT INTO "quran_text" VALUES(6149,100,3,'فَٱلْمُغِيرَٰتِ صُبْحًا');
INSERT INTO "quran_text" VALUES(6150,100,4,'فَأَثَرْنَ بِهِۦ نَقْعًا');
INSERT INTO "quran_text" VALUES(6151,100,5,'فَوَسَطْنَ بِهِۦ جَمْعًا');
INSERT INTO "quran_text" VALUES(6152,100,6,'إِنَّ ٱلْإِنسَـٰنَ لِرَبِّهِۦ لَكَنُودٌ');
INSERT INTO "quran_text" VALUES(6153,100,7,'وَإِنَّهُۥ عَلَىٰ ذَٰلِكَ لَشَهِيدٌ');
INSERT INTO "quran_text" VALUES(6154,100,8,'وَإِنَّهُۥ لِحُبِّ ٱلْخَيْرِ لَشَدِيدٌ');
INSERT INTO "quran_text" VALUES(6155,100,9,'۞ أَفَلَا يَعْلَمُ إِذَا بُعْثِرَ مَا فِى ٱلْقُبُورِ');
INSERT INTO "quran_text" VALUES(6156,100,10,'وَحُصِّلَ مَا فِى ٱلصُّدُورِ');
INSERT INTO "quran_text" VALUES(6157,100,11,'إِنَّ رَبَّهُم بِهِمْ يَوْمَئِذٍ لَّخَبِيرٌۢ');
INSERT INTO "quran_text" VALUES(6158,101,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ ٱلْقَارِعَةُ');
INSERT INTO "quran_text" VALUES(6159,101,2,'مَا ٱلْقَارِعَةُ');
INSERT INTO "quran_text" VALUES(6160,101,3,'وَمَآ أَدْرَىٰكَ مَا ٱلْقَارِعَةُ');
INSERT INTO "quran_text" VALUES(6161,101,4,'يَوْمَ يَكُونُ ٱلنَّاسُ كَٱلْفَرَاشِ ٱلْمَبْثُوثِ');
INSERT INTO "quran_text" VALUES(6162,101,5,'وَتَكُونُ ٱلْجِبَالُ كَٱلْعِهْنِ ٱلْمَنفُوشِ');
INSERT INTO "quran_text" VALUES(6163,101,6,'فَأَمَّا مَن ثَقُلَتْ مَوَٰزِينُهُۥ');
INSERT INTO "quran_text" VALUES(6164,101,7,'فَهُوَ فِى عِيشَةٍ رَّاضِيَةٍ');
INSERT INTO "quran_text" VALUES(6165,101,8,'وَأَمَّا مَنْ خَفَّتْ مَوَٰزِينُهُۥ');
INSERT INTO "quran_text" VALUES(6166,101,9,'فَأُمُّهُۥ هَاوِيَةٌ');
INSERT INTO "quran_text" VALUES(6167,101,10,'وَمَآ أَدْرَىٰكَ مَا هِيَهْ');
INSERT INTO "quran_text" VALUES(6168,101,11,'نَارٌ حَامِيَةٌۢ');
INSERT INTO "quran_text" VALUES(6169,102,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ أَلْهَىٰكُمُ ٱلتَّكَاثُرُ');
INSERT INTO "quran_text" VALUES(6170,102,2,'حَتَّىٰ زُرْتُمُ ٱلْمَقَابِرَ');
INSERT INTO "quran_text" VALUES(6171,102,3,'كَلَّا سَوْفَ تَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(6172,102,4,'ثُمَّ كَلَّا سَوْفَ تَعْلَمُونَ');
INSERT INTO "quran_text" VALUES(6173,102,5,'كَلَّا لَوْ تَعْلَمُونَ عِلْمَ ٱلْيَقِينِ');
INSERT INTO "quran_text" VALUES(6174,102,6,'لَتَرَوُنَّ ٱلْجَحِيمَ');
INSERT INTO "quran_text" VALUES(6175,102,7,'ثُمَّ لَتَرَوُنَّهَا عَيْنَ ٱلْيَقِينِ');
INSERT INTO "quran_text" VALUES(6176,102,8,'ثُمَّ لَتُسْـَٔلُنَّ يَوْمَئِذٍ عَنِ ٱلنَّعِيمِ');
INSERT INTO "quran_text" VALUES(6177,103,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَٱلْعَصْرِ');
INSERT INTO "quran_text" VALUES(6178,103,2,'إِنَّ ٱلْإِنسَـٰنَ لَفِى خُسْرٍ');
INSERT INTO "quran_text" VALUES(6179,103,3,'إِلَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَتَوَاصَوْا۟ بِٱلْحَقِّ وَتَوَاصَوْا۟ بِٱلصَّبْرِ');
INSERT INTO "quran_text" VALUES(6180,104,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ وَيْلٌ لِّكُلِّ هُمَزَةٍ لُّمَزَةٍ');
INSERT INTO "quran_text" VALUES(6181,104,2,'ٱلَّذِى جَمَعَ مَالًا وَعَدَّدَهُۥ');
INSERT INTO "quran_text" VALUES(6182,104,3,'يَحْسَبُ أَنَّ مَالَهُۥٓ أَخْلَدَهُۥ');
INSERT INTO "quran_text" VALUES(6183,104,4,'كَلَّا ۖ لَيُنۢبَذَنَّ فِى ٱلْحُطَمَةِ');
INSERT INTO "quran_text" VALUES(6184,104,5,'وَمَآ أَدْرَىٰكَ مَا ٱلْحُطَمَةُ');
INSERT INTO "quran_text" VALUES(6185,104,6,'نَارُ ٱللَّهِ ٱلْمُوقَدَةُ');
INSERT INTO "quran_text" VALUES(6186,104,7,'ٱلَّتِى تَطَّلِعُ عَلَى ٱلْأَفْـِٔدَةِ');
INSERT INTO "quran_text" VALUES(6187,104,8,'إِنَّهَا عَلَيْهِم مُّؤْصَدَةٌ');
INSERT INTO "quran_text" VALUES(6188,104,9,'فِى عَمَدٍ مُّمَدَّدَةٍۭ');
INSERT INTO "quran_text" VALUES(6189,105,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِأَصْحَـٰبِ ٱلْفِيلِ');
INSERT INTO "quran_text" VALUES(6190,105,2,'أَلَمْ يَجْعَلْ كَيْدَهُمْ فِى تَضْلِيلٍ');
INSERT INTO "quran_text" VALUES(6191,105,3,'وَأَرْسَلَ عَلَيْهِمْ طَيْرًا أَبَابِيلَ');
INSERT INTO "quran_text" VALUES(6192,105,4,'تَرْمِيهِم بِحِجَارَةٍ مِّن سِجِّيلٍ');
INSERT INTO "quran_text" VALUES(6193,105,5,'فَجَعَلَهُمْ كَعَصْفٍ مَّأْكُولٍۭ');
INSERT INTO "quran_text" VALUES(6194,106,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ لِإِيلَـٰفِ قُرَيْشٍ');
INSERT INTO "quran_text" VALUES(6195,106,2,'إِۦلَـٰفِهِمْ رِحْلَةَ ٱلشِّتَآءِ وَٱلصَّيْفِ');
INSERT INTO "quran_text" VALUES(6196,106,3,'فَلْيَعْبُدُوا۟ رَبَّ هَـٰذَا ٱلْبَيْتِ');
INSERT INTO "quran_text" VALUES(6197,106,4,'ٱلَّذِىٓ أَطْعَمَهُم مِّن جُوعٍ وَءَامَنَهُم مِّنْ خَوْفٍۭ');
INSERT INTO "quran_text" VALUES(6198,107,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ أَرَءَيْتَ ٱلَّذِى يُكَذِّبُ بِٱلدِّينِ');
INSERT INTO "quran_text" VALUES(6199,107,2,'فَذَٰلِكَ ٱلَّذِى يَدُعُّ ٱلْيَتِيمَ');
INSERT INTO "quran_text" VALUES(6200,107,3,'وَلَا يَحُضُّ عَلَىٰ طَعَامِ ٱلْمِسْكِينِ');
INSERT INTO "quran_text" VALUES(6201,107,4,'فَوَيْلٌ لِّلْمُصَلِّينَ');
INSERT INTO "quran_text" VALUES(6202,107,5,'ٱلَّذِينَ هُمْ عَن صَلَاتِهِمْ سَاهُونَ');
INSERT INTO "quran_text" VALUES(6203,107,6,'ٱلَّذِينَ هُمْ يُرَآءُونَ');
INSERT INTO "quran_text" VALUES(6204,107,7,'وَيَمْنَعُونَ ٱلْمَاعُونَ');
INSERT INTO "quran_text" VALUES(6205,108,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ إِنَّآ أَعْطَيْنَـٰكَ ٱلْكَوْثَرَ');
INSERT INTO "quran_text" VALUES(6206,108,2,'فَصَلِّ لِرَبِّكَ وَٱنْحَرْ');
INSERT INTO "quran_text" VALUES(6207,108,3,'إِنَّ شَانِئَكَ هُوَ ٱلْأَبْتَرُ');
INSERT INTO "quran_text" VALUES(6208,109,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ قُلْ يَـٰٓأَيُّهَا ٱلْكَـٰفِرُونَ');
INSERT INTO "quran_text" VALUES(6209,109,2,'لَآ أَعْبُدُ مَا تَعْبُدُونَ');
INSERT INTO "quran_text" VALUES(6210,109,3,'وَلَآ أَنتُمْ عَـٰبِدُونَ مَآ أَعْبُدُ');
INSERT INTO "quran_text" VALUES(6211,109,4,'وَلَآ أَنَا۠ عَابِدٌ مَّا عَبَدتُّمْ');
INSERT INTO "quran_text" VALUES(6212,109,5,'وَلَآ أَنتُمْ عَـٰبِدُونَ مَآ أَعْبُدُ');
INSERT INTO "quran_text" VALUES(6213,109,6,'لَكُمْ دِينُكُمْ وَلِىَ دِينِ');
INSERT INTO "quran_text" VALUES(6214,110,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ إِذَا جَآءَ نَصْرُ ٱللَّهِ وَٱلْفَتْحُ');
INSERT INTO "quran_text" VALUES(6215,110,2,'وَرَأَيْتَ ٱلنَّاسَ يَدْخُلُونَ فِى دِينِ ٱللَّهِ أَفْوَاجًا');
INSERT INTO "quran_text" VALUES(6216,110,3,'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَٱسْتَغْفِرْهُ ۚ إِنَّهُۥ كَانَ تَوَّابًۢا');
INSERT INTO "quran_text" VALUES(6217,111,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ تَبَّتْ يَدَآ أَبِى لَهَبٍ وَتَبَّ');
INSERT INTO "quran_text" VALUES(6218,111,2,'مَآ أَغْنَىٰ عَنْهُ مَالُهُۥ وَمَا كَسَبَ');
INSERT INTO "quran_text" VALUES(6219,111,3,'سَيَصْلَىٰ نَارًا ذَاتَ لَهَبٍ');
INSERT INTO "quran_text" VALUES(6220,111,4,'وَٱمْرَأَتُهُۥ حَمَّالَةَ ٱلْحَطَبِ');
INSERT INTO "quran_text" VALUES(6221,111,5,'فِى جِيدِهَا حَبْلٌ مِّن مَّسَدٍۭ');
INSERT INTO "quran_text" VALUES(6222,112,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ قُلْ هُوَ ٱللَّهُ أَحَدٌ');
INSERT INTO "quran_text" VALUES(6223,112,2,'ٱللَّهُ ٱلصَّمَدُ');
INSERT INTO "quran_text" VALUES(6224,112,3,'لَمْ يَلِدْ وَلَمْ يُولَدْ');
INSERT INTO "quran_text" VALUES(6225,112,4,'وَلَمْ يَكُن لَّهُۥ كُفُوًا أَحَدٌۢ');
INSERT INTO "quran_text" VALUES(6226,113,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ قُلْ أَعُوذُ بِرَبِّ ٱلْفَلَقِ');
INSERT INTO "quran_text" VALUES(6227,113,2,'مِن شَرِّ مَا خَلَقَ');
INSERT INTO "quran_text" VALUES(6228,113,3,'وَمِن شَرِّ غَاسِقٍ إِذَا وَقَبَ');
INSERT INTO "quran_text" VALUES(6229,113,4,'وَمِن شَرِّ ٱلنَّفَّـٰثَـٰتِ فِى ٱلْعُقَدِ');
INSERT INTO "quran_text" VALUES(6230,113,5,'وَمِن شَرِّ حَاسِدٍ إِذَا حَسَدَ');
INSERT INTO "quran_text" VALUES(6231,114,1,'بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ قُلْ أَعُوذُ بِرَبِّ ٱلنَّاسِ');
INSERT INTO "quran_text" VALUES(6232,114,2,'مَلِكِ ٱلنَّاسِ');
INSERT INTO "quran_text" VALUES(6233,114,3,'إِلَـٰهِ ٱلنَّاسِ');
INSERT INTO "quran_text" VALUES(6234,114,4,'مِن شَرِّ ٱلْوَسْوَاسِ ٱلْخَنَّاسِ');
INSERT INTO "quran_text" VALUES(6235,114,5,'ٱلَّذِى يُوَسْوِسُ فِى صُدُورِ ٱلنَّاسِ');
INSERT INTO "quran_text" VALUES(6236,114,6,'مِنَ ٱلْجِنَّةِ وَٱلنَّاسِ');
